INSERT INTO `tintuc` (`matin`, `anh`, `motatin`, `tieude`, `noidung`, `tacgia`, `ngayviet`, `maloai`) VALUES
(1, '1626916958052-lynxmpeb3c0tv-l.jpg', 'Đồng đô la Mỹ giảm nhẹ khi cả thị trường chờ đợi cuộc họp của Fed và trong bối cảnh thị trường tiền số sôi động và tăng mạnh trở lại.', 'Tỷ giá USD hôm nay 28/7/2021: Đô la Mỹ giảm nhẹ tiếp', '<p>&nbsp;</p>\r\n\r\n<p><strong>Tỷ gi&aacute; USD thị trường thế giới</strong></p>\r\n\r\n<p>S&aacute;ng 28/7, chỉ số Dollar Index (DXY), theo d&otilde;i đồng bạc xanh so với rổ c&aacute;c tiền tệ kh&aacute;c đang ở mức 92,495.</p>\r\n\r\n<p>Gi&aacute; USD so với c&aacute;c ngoại tệ lớn kh&aacute;c s&aacute;ng nay hiện đứng ở mức:</p>\r\n\r\n<p>1 euro đổi 1,1818 USD.</p>\r\n\r\n<p>&quot; &gt;</p>\r\n', 'Nguyễn Như Quyền', '2021-07-28', 1),
(2, '1627002209160-ty-gia-23.jpg', 'Ngày 27/7, đồng USD giảm khi lạm phát đe dọa sự phục hồi kinh tế ở Mỹ và tình hình chống dịch COVID-19 đang căng thẳng tại nhiều nơi.', 'Tỷ giá USD hôm nay 27/7/2021: Đô la Mỹ giảm giá', '<p>&nbsp;</p>\r\n\r\n<p><strong>Tỷ gi&aacute; USD thị trường thế giới</strong></p>\r\n\r\n<p>S&aacute;ng 27/7, chỉ số Dollar Index (DXY), theo d&otilde;i đồng bạc xanh so với rổ c&aacute;c tiền tệ kh&aacute;c đang ở mức 92,640. Chỉ số n&agrave;y thấp hơn một ch&uacute;t so với mức cao nhất trong ba th&aacute;ng rưỡi của tuần trước l&agrave; 93,194 nhưng đ&atilde; tăng khoảng 3,8% so với mức thấp gần đ&acirc;y v&agrave;o ng&agrave;y 25/5.</p>\r\n\r\n<p>Gi&aacute; USD so với c&aacute;c ngoại tệ lớn kh&aacute;c s&aacute;ng nay hiện đứng ở mức:</p>\r\n\r\n<p>1 euro đổi 1,1801 USD.</p>\r\n\r\n<p>1 bảng Anh đổi 1,3823 USD.</p>\r\n\r\n<p>1 USD đổi 110,38 y&ecirc;n.</p>\r\n\r\n<p><strong>Chuy&ecirc;n gia nhận định về gi&aacute; USD</strong></p>\r\n\r\n<p>Ng&acirc;n h&agrave;ng Commonwealth Bank of Australia (CBA) cho biết, đồng đ&ocirc; la Mỹ sẽ tiếp tục mạnh l&ecirc;n trong suốt tuần do Fed được cho l&agrave; sẽ tiến gần hơn đến thời điểm bắt đầu cắt giảm kế hoạch mua lại t&agrave;i sản.</p>\r\n\r\n<p>Chiến lược gia Joseph Capurso của CBA cho biết: &quot;Ch&uacute;ng t&ocirc;i hy vọng FOMC sẽ bỏ từ &#39;đ&aacute;ng kể&#39; trong cụm từ &#39;tiến triển đ&aacute;ng kể&#39; ở thị trường lao động, trước khi loại bỏ c&aacute;c biện ph&aacute;p k&iacute;ch th&iacute;ch tiền tệ. Việc loại bỏ từ &#39;đ&aacute;ng kể&#39; b&aacute;o hiệu Fed sẽ sớm giảm bớt việc mua t&agrave;i sản&quot;.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, rủi ro đối với triển vọng n&agrave;y l&agrave; sự gia tăng c&aacute;c trường hợp nhiễm mới COVID-19 ở Mỹ.</p>\r\n\r\n<p>&quot; &gt;</p>\r\n', 'Nguyễn duy Thành', '2021-07-28', 1),
(3, '1626748330894-image-(46)(3).jpeg', 'Dịch COVID-19 bùng phát trở lại lần thứ 4 đã ảnh hưởng nghiêm trọng đến nhiều hoạt động kinh tế - xã hội của Việt Nam.', '3 kịch bản kinh tế Việt Nam sau đợt dịch COVID-19 thứ 4 bùng phát', '<p>&nbsp;</p>\r\n\r\n<p>Với kịch bản t&iacute;ch cực, c&aacute;c nước tr&ecirc;n thế giới cơ bản triển khai th&agrave;nh c&ocirc;ng việc ti&ecirc;m vaccine v&agrave;o qu&yacute; IV/2021 gi&uacute;p giảm dần t&igrave;nh trạng l&acirc;y nhiễm, kh&ocirc;i phục đa số hoạt động kinh tế - x&atilde; hội tại c&aacute;c nước; căng thẳng thương mại v&agrave; bất ổn địa ch&iacute;nh trị lắng dịu hơn tạo tiền đề kh&ocirc;i phục thương mại v&agrave; đầu tư to&agrave;n cầu.</p>\r\n\r\n<p>Tại Việt Nam, dịch bệnh cơ bản được kiểm so&aacute;t ngay trong th&aacute;ng 7/2021, ti&ecirc;m vaccine được đẩy nhanh v&agrave; đạt miễn dịch cộng đồng v&agrave;o qu&yacute; I/2022; kinh tế vĩ m&ocirc; duy tr&igrave; ổn định; c&aacute;c ch&iacute;nh s&aacute;ch hỗ trợ người d&acirc;n, doanh nghiệp được đẩy nhanh; qu&aacute; tr&igrave;nh cơ cấu lại, chuyển đổi số được th&uacute;c đẩy; thu h&uacute;t đầu tư trong v&agrave; ngo&agrave;i nước, ti&ecirc;u d&ugrave;ng nội địa hồi phục nhanh...v.v. Khi đ&oacute;, tăng trưởng GDP năm 2021 của Việt Nam c&oacute; thể đạt 6,3-6,5%.</p>\r\n\r\n<p>Với kịch bản ti&ecirc;u cực, tới cuối năm 2021, dịch bệnh mới cơ bản được kiểm so&aacute;t ở khối c&aacute;c nước ph&aacute;t triển, vaccine chậm đưa v&agrave;o ti&ecirc;m chủng tại c&aacute;c quốc gia đang ph&aacute;t triển do hạn hẹp về nguồn cung, qu&aacute; tr&igrave;nh phục hồi kh&oacute; khăn, hoạt động thương mại v&agrave; đầu tư to&agrave;n cầu chỉ phục hồi nhẹ.</p>\r\n\r\n<p>Tại Việt Nam, đợt dịch n&agrave;y cơ bản được kiểm so&aacute;t đến hết qu&yacute; III/2021, qu&aacute; tr&igrave;nh ti&ecirc;m vaccine chậm triển khai v&agrave; chỉ đạt mức miễn dịch cộng đồng v&agrave;o qu&yacute; III/2022, dẫn tới việc chậm kh&ocirc;i phục c&aacute;c hoạt động kinh tế x&atilde; hội; c&aacute;c ch&iacute;nh s&aacute;ch hỗ trợ người d&acirc;n, doanh nghiệp, qu&aacute; tr&igrave;nh t&aacute;i cơ cấu, chuyển đổi số được th&uacute;c đẩy nhưng dịch vụ, thương mại v&agrave; thu h&uacute;t vốn FDI phục hồi chậm. Khi đ&oacute;, kinh tế Việt Nam năm 2021 chỉ c&oacute; thể tăng trưởng từ 5,1-5,3%.</p>\r\n\r\n<p>&quot;Nhiệm vụ ưu ti&ecirc;n số 1, quan trọng h&agrave;ng đầu hiện nay vẫn phải l&agrave; tiếp tục ph&ograve;ng chống v&agrave; kiểm so&aacute;t dịch COVID-19 hiệu quả c&ugrave;ng với việc đẩy nhanh tiến tr&igrave;nh vaccine.</p>\r\n\r\n<p>Tiếp theo, Ch&iacute;nh phủ, c&aacute;c bộ ng&agrave;nh, địa phương cần khẩn trương triển khai c&aacute;c g&oacute;i hỗ trợ người d&acirc;n v&agrave; doanh nghiệp đ&atilde; ban h&agrave;nh.</p>\r\n\r\n<p>Th&ecirc;m v&agrave;o đ&oacute;, cần cập nhật kịch bản phục hồi, ph&aacute;t triển kinh tế x&atilde; hội trong bối cảnh mới đến hết năm 2021 v&agrave; giai đoạn 2022-2025; trong đ&oacute;, cần ki&ecirc;n định mục ti&ecirc;u k&eacute;p, t&igrave;m kiếm c&aacute;c động lực tăng trưởng thay thế, bổ sung v&agrave; ch&iacute;nh s&aacute;ch phục hồi xanh.</p>\r\n\r\n<p>Cuối c&ugrave;ng, ch&iacute;nh phủ cần ki&ecirc;n định giữ vững ổn định kinh tế vĩ m&ocirc;&quot; - Tiến sĩ Cấn Văn Lực n&oacute;i.</p>\r\n\r\n<p>&quot; &gt;</p>\r\n', 'Bùi Xuân Tuân', '2021-07-28', 1),
(4, 'cafe.jpg', 'Giá cà phê hôm nay 22/7 trong khoảng 35.700 - 36.600 đồng/kg. Giá cà phê Arabica tiếp tục tăng mạnh, lên mức cao nhất trong vòng 5 năm qua.', 'Giá cà phê hôm nay 22/7: Arabica tăng cao nhất 5 năm qua, Robusta hướng tới mốc 1.800 USD/tấn', '<p>&nbsp;</p>\r\n\r\n<p>Tại huyện Di Linh, L&acirc;m H&agrave;, Bảo Lộc (L&acirc;m Đồng) gi&aacute; c&agrave; ph&ecirc; h&ocirc;m nay được thu mua với mức 36.800 đồng/kg.</p>\r\n\r\n<p>Tại huyện Cư M&#39;gar (Đắk Lắk) gi&aacute; c&agrave; ph&ecirc; h&ocirc;m nay ở mức 37.700 đồng/kg. Tại huyện Ea H&#39;leo (Đắk Lắk), Bu&ocirc;n Hồ (Đắk Lắk) gi&aacute; c&agrave; ph&ecirc; h&ocirc;m nay được thu mua c&ugrave;ng mức 37.600 đồng/kg.</p>\r\n\r\n<p>Tương tự tại tỉnh Đắk N&ocirc;ng, gi&aacute; c&agrave; ph&ecirc; h&ocirc;m nay thu mua ở mức 37.600 đồng/kg tại Gia Nghĩa v&agrave; 37.500 đồng/kg ở Đắk R&#39;lấp.</p>\r\n\r\n<p>Tại tỉnh Gia Lai, gi&aacute; c&agrave; ph&ecirc; h&ocirc;m nay ở mức 37.600 đồng/kg (Chư Pr&ocirc;ng), ở Pleiku v&agrave; La Grai c&ugrave;ng gi&aacute; 37.500 đồng/kg. C&ograve;n gi&aacute; c&agrave; ph&ecirc; h&ocirc;m nay tại tỉnh Kon Tum được thu mua với mức 37.600 đồng/kg. S&aacute;ng nay, gi&aacute; c&agrave; ph&ecirc; tại c&aacute;c v&ugrave;ng trồng trọng điểm đi ngang so với c&ugrave;ng thời điểm h&ocirc;m qua. Tổng kết tuần trước, gi&aacute; c&agrave; ph&ecirc; nội địa tăng th&ecirc;m 1.400 - 1.500 đồng/kg.</p>\r\n\r\n<p>Kết th&uacute;c phi&ecirc;n giao dịch gần nhất, gi&aacute; c&agrave; ph&ecirc; Robusta tại London giao th&aacute;ng 9/2021 tăng 10 USD/tấn ở mức 1.899 USD/tấn, giao th&aacute;ng 11/2021 tăng 12 USD/tấn ở mức 1.907 USD/tấn. Trong khi đ&oacute; tr&ecirc;n s&agrave;n New York, gi&aacute; c&agrave; ph&ecirc; Arabica giao th&aacute;ng 9/2021 giảm 4,65 cent/lb ở mức 189 cent/lb, giao th&aacute;ng 12/2021 giảm 4,65 cent/lb ở mức 197,95 cent/lb.</p>\r\n\r\n<p>Tổng hợp tuần trước, gi&aacute; c&agrave; ph&ecirc; Robusta tại London giao th&aacute;ng 9/2021 tăng th&ecirc;m 132 USD/tấn, giao th&aacute;ng 11 tăng 151 USD/tấn; gi&aacute; c&agrave; ph&ecirc; Arabica giao th&aacute;ng 9/2021 tăng 27,65 cent/lb, giao th&aacute;ng 12/2021 tăng 33,85 cent/lb.</p>\r\n\r\n<p>Như vậy, gi&aacute; Arabica đ&atilde; l&ecirc;n lại mức cao nhất t&iacute;nh từ 6,5 năm qua, c&ograve;n Robusta cao nhất 3,5 năm.</p>\r\n\r\n<p>Theo chuy&ecirc;n gia Nguyễn Quang B&igrave;nh đ&aacute;nh gi&aacute;, nh&igrave;n v&agrave;o đồ thị gi&aacute; c&agrave; ph&ecirc; thời gian qua cho thấy k&ecirc;nh gi&aacute; theo hướng l&ecirc;n, đỉnh v&agrave; đ&aacute;y sau cao hơn đỉnh v&agrave; đ&aacute;y trước với một đợt đột biến tăng mạnh. Trong ho&agrave;n cảnh hiện nay, nếu dịch Covid-19 tiếp tục diễn biến phức tạp, thị trường c&agrave; ph&ecirc; sẽ c&oacute; những đợt tăng/giảm cực đoan được lặp đi lặp lại.</p>\r\n\r\n<p>Tuần trước, khi qua khỏi mức t&acirc;m l&yacute; quan trọng 1.800 USD/tấn, s&agrave;n London đ&atilde; ph&oacute;ng tự do l&ecirc;n đến 1.993 trong khi th&aacute;ng 11/21 đ&atilde; vượt mức 2.000 để c&oacute; l&uacute;c chạm 2.002 USD/tấn trong phi&ecirc;n 23/07/2021.</p>\r\n\r\n<p>Một yếu tố quan trọng ảnh hưởng đến gi&aacute; c&agrave; ph&ecirc; hiện nay l&agrave; thời tiết tại Brazil. C&agrave; ph&ecirc; nước n&agrave;y năm nay mất m&ugrave;a theo chu kỳ v&agrave; khả năng th&ecirc;m một năm mất m&ugrave;a do r&eacute;t đậm r&eacute;t hại. T&acirc;m l&yacute; của c&aacute;c nh&agrave; kinh doanh đ&atilde; bất an, điều hướng gi&aacute; ph&aacute;i sinh đi qu&aacute; đ&agrave; tạo n&ecirc;n bất ổn tr&ecirc;n thị trường. Ch&iacute;nh v&igrave; vậy, d&ugrave; thị trường London đ&atilde; v&agrave;o v&ugrave;ng mua qu&aacute; mức gi&aacute; vẫn tăng mạnh kh&oacute; cưỡng. Gi&aacute; chỉ dịu lại v&agrave; chỉnh xuống một khi tin sương gi&aacute; tại Brazil kh&ocirc;ng &aacute;m ảnh lắm thị trường.</p>\r\n\r\n<p>Vị chuy&ecirc;n gia n&agrave;y cảnh b&aacute;o, khi th&ocirc;ng tin r&eacute;t đậm r&eacute;t hại kh&ocirc;ng c&ograve;n rủi ro lớn, sẽ c&oacute; những đợt điều chỉnh gi&aacute; đi xuống, rớt mạnh một c&aacute;ch kh&ocirc;ng thương tiếc, do c&aacute;c quỹ đầu tư thấy m&igrave;nh đ&aacute;nh gi&aacute; sai v&agrave; mua qu&aacute; đ&agrave;, bấy giờ họ phải t&igrave;m c&aacute;ch b&aacute;n th&aacute;o.</p>\r\n\r\n<p>Tuy vậy, &ocirc;ng Nguyễn Quang B&igrave;nh nhận định, hướng xuống của c&agrave; ph&ecirc; đang l&agrave; rất kh&oacute; trong tuần n&agrave;y, v&igrave; c&ograve;n một dự b&aacute;o kh&aacute;c về r&eacute;t đậm trong những ng&agrave;y cuối th&aacute;ng. N&ecirc;n, khả năng vượt đỉnh 1.993 USD/tấn của c&agrave; ph&ecirc; Robusta kh&ocirc;ng phải l&agrave; kh&ocirc;ng thế.</p>\r\n\r\n<p>&quot; &gt;</p>\r\n', 'Lê Văn Tú', '2021-07-28', 1),
(5, 'chu-tich-ha-noi-nguyen-duc-chung-chi-dao-khan-chong-dich-covid-19.jpg', 'Ai đứng sau Bùi Quang Huy, thúc gửi ‘mật thư’ cho ông Nguyễn Đức Chung?', 'Ai đứng sau Bùi Quang Huy', '<p>&Ocirc;ng chủ Nhật Cường từng gửi email cho &ocirc;ng Nguyễn Đức Chung đề xuất &ocirc;ng Chung chỉ đạo c&oacute; lợi cho C&ocirc;ng ty Nhật Cường Software. Sau lưng B&ugrave;i Quang Huy, c&oacute; người kh&aacute;c th&uacute;c việc n&agrave;y.</p>\r\n\r\n<p>Theo kết luận điều tra, B&ugrave;i Quang Huy, TGĐ C&ocirc;ng ty Nhật Cường từng gửi email cho &ocirc;ng Nguyễn Đức Chung (cựu Chủ tịch UBND TP H&agrave; Nội) đề xuất &ocirc;ng Chung chỉ đạo c&oacute; lợi cho C&ocirc;ng ty Nhật Cường Software.</p>\r\n\r\n<p>CQĐT l&agrave;m r&otilde;, li&ecirc;n quan đến việc dừng thầu, ng&agrave;y 15/5/2016, V&otilde; Minh Hiếu (Gi&aacute;m đốc Nhật Cường Software) gửi email cho B&ugrave;i Quang Huy, từ đ&oacute; &ocirc;ng chủ Nhật Cường gửi email cho &ocirc;ng Nguyễn Đức Chung.</p>\r\n\r\n<p>Email n&agrave;y c&oacute; nội dung: &ldquo;Theo th&ocirc;ng tin Hiếu được biết th&igrave; Sở KH&amp;ĐT H&agrave; Nội đang triển khai g&oacute;i thầu số h&oacute;a năm 2016 v&agrave; dự kiến đ&oacute;ng thầu v&agrave;o thứ 2, ng&agrave;y 16/5/2016.</p>\r\n\r\n<p>Hiếu muốn Huy n&oacute;i với &ocirc;ng Chung cho một c&ocirc;ng ty do Hiếu giới thiệu c&oacute; sử dụng phần mềm nhận dạng của Nga tham gia g&oacute;i thầu số h&oacute;a hồ sơ đăng k&yacute; doanh nghiệp, ngo&agrave;i ra c&oacute; thể số h&oacute;a cả văn bản giấy tờ v&agrave; cơ sở dữ liệu hộ tịch tại phường.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/07/26/11/chu-tich-ha-noi-nguyen-duc-chung-chi-dao-khan-chong-dich-covid-19.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p>Từ dữ liệu số h&oacute;a đ&oacute; c&oacute; thể tạo lập cơ sở dữ liệu doanh nghiệp v&agrave; t&iacute;ch hợp v&agrave;o hệ thống ch&iacute;nh quyền điện tử của&nbsp;TP H&agrave; Nội v&agrave; cơ sở dữ liệu d&acirc;n cư.</p>\r\n\r\n<p>Do đ&atilde; đến thời điểm đ&oacute;ng thầu, kh&ocirc;ng kịp chuẩn bị hồ sơ dự thầu n&ecirc;n Hiếu đề nghị Huy đề xuất &ocirc;ng Chung chỉ đạo l&ugrave;i ng&agrave;y đ&oacute;ng thầu th&ecirc;m 2 tuần để c&oacute; thời gian chuẩn bị hồ sơ dự thầu v&agrave; tự chứng minh năng lực một c&aacute;ch tự nhi&ecirc;n, kh&ocirc;ng can thiệp&rdquo;.</p>\r\n\r\n<p>Đối với nội dung email m&agrave; B&ugrave;i Quang Huy gửi &ocirc;ng Nguyễn Đức Chung, cựu Chủ tịch UBND TP H&agrave; Nội c&oacute; lời khai cho rằng, &ocirc;ng kh&ocirc;ng kiểm tra nội dung email gửi đến do kh&ocirc;ng c&oacute; thời gian.</p>\r\n\r\n<p>&Ocirc;ng Nguyễn Đức Chung thừa nhận th&acirc;n quen B&ugrave;i Quốc Huy khoảng từ năm 2005, 2006, khi c&ograve;n l&agrave;m ở ph&ograve;ng CSHS, C&ocirc;ng an H&agrave; Nội v&agrave; c&oacute; nhờ Huy đưa qu&agrave; biếu cho một số c&aacute; nh&acirc;n v&agrave;o dịp tết.</p>\r\n\r\n<p>Tuy nhi&ecirc;n &ocirc;ng Chung kh&ocirc;ng thừa nhận việc chỉ đạo đ&igrave;nh chỉ thầu l&agrave; theo đề nghị của &ocirc;ng chủ Nhật Cường.</p>\r\n\r\n<p>Theo lời khai của &ocirc;ng Nguyễn Đức Chung, &ocirc;ng chỉ đạo cho C&ocirc;ng ty Nhật Cường v&agrave;o l&agrave;m th&iacute; điểm số h&oacute;a, nhưng việc th&iacute; điểm kh&ocirc;ng li&ecirc;n quan đến g&oacute;i thầu số h&oacute;a, kh&ocirc;ng t&aacute;c động Sở KH&amp;ĐT để sửa đổi hồ sơ mời thầu.</p>\r\n\r\n<p>Việc cập nhật c&ocirc;ng nghệ mới về số h&oacute;a v&agrave;o g&oacute;i thầu l&agrave; do &ocirc;ng Nguyễn Văn Tứ, Gi&aacute;m đốc Sở KH&amp;ĐT chỉ đạo.</p>\r\n\r\n<p>&Ocirc;ng Chung khai, sau khi chỉ đạo g&oacute;i thầu 2016, &ocirc;ng kh&ocirc;ng kiểm tra lại, nhưng vẫn k&yacute; quyết định bổ sung dự to&aacute;n cho g&oacute;i thầu 2017 v&igrave; tin tưởng cấp dưới.</p>\r\n\r\n<p><strong>Danh s&aacute;ch biếu tặng c&aacute; nh&acirc;n c&oacute; ảnh hưởng</strong></p>\r\n\r\n<p>Kết luận điều tra chỉ ra rằng, qu&aacute; tr&igrave;nh đấu thầu, thực hiện hợp đồng, L&ecirc; Duy Tuấn (Gi&aacute;m đốc kinh doanh C&ocirc;ng ty Đ&ocirc;ng Kinh) &nbsp;được giao lập bảng tổng hợp danh s&aacute;ch c&aacute;c c&aacute; nh&acirc;n c&oacute; ảnh hưởng tới dự &aacute;n, dự kiến chi ph&iacute; biếu tặng.</p>\r\n\r\n<p>Cụ thể, g&oacute;i thầu số h&oacute;a năm 2016, c&aacute;c email thể hiện L&ecirc; Duy Tuấn lập danh s&aacute;ch biếu tặng &ocirc;ng Nguyễn Văn Tứ (Gi&aacute;m đốc Sở KH&amp;ĐT), Nguyễn Tiến Học (cựu PGĐ Sở KH&amp;ĐT), Phạm Thị Kim Tuyến (cựu Trưởng ph&ograve;ng Đăng k&yacute; kinh doanh), Phạm Thị Thu Hường (cựu Ch&aacute;nh Văn ph&ograve;ng Sở KH&amp;ĐT)...</p>\r\n\r\n<p>G&oacute;i thầu số h&oacute;a năm 2017, c&aacute;c email cũng thể hiện danh s&aacute;ch biếu tặng cho c&aacute;c c&aacute; nh&acirc;n n&ecirc;u tr&ecirc;n.</p>\r\n\r\n<p>Về việc biếu tiền, tặng qu&agrave; cho c&aacute;n bộ Sở KH&amp;ĐT, L&ecirc; Duy Tuấn x&aacute;c nhận, B&ugrave;i Quang Huy trực tiếp biếu tiền cho Gi&aacute;m đốc Sở, c&ograve;n Tuấn chịu tr&aacute;ch nhiệm biếu tiền PGĐ v&agrave; những người li&ecirc;n quan kh&aacute;c.</p>\r\n\r\n<p>Theo lời khai của Tuấn, B&ugrave;i Quang Huy n&oacute;i lại với bị can đ&atilde; biếu cho &ocirc;ng Tứ 500 triệu đồng.</p>\r\n\r\n<p>Dịp tết 2017, Tuấn đến ph&ograve;ng l&agrave;m việc của &ocirc;ng Học ch&uacute;c tết, biếu 100 triệu đồng. Dịp tết 2018, Tuấn đến ph&ograve;ng đăng k&yacute; kinh doanh ch&uacute;c tết v&agrave; c&oacute; biếu 30 triệu đồng đưa cho b&agrave; Tuyến.</p>\r\n\r\n<p>Tuấn c&ograve;n mua tặng một m&aacute;y robot lau nh&agrave; nh&acirc;n dịp bị can Tuyến chuyển nh&agrave; mới (trị gi&aacute; khoảng 10 triệu đồng). Tuấn ch&uacute;c tết b&agrave; Hường 1 chai rượu v&agrave; tặng Sở KH&amp;ĐT 5 chậu hoa lan trị gi&aacute; khoảng 25 triệu đồng.</p>\r\n', 'Nguyễn Thanh Bằng', '2021-07-28', 2),
(7, 'bs20le20quang20trung-1627213334846-16272133356211803209445.jpg', 'Xe chở công nhân, chuyên gia qua chốt kiểm soát cần thủ tục gì?', 'Xe chở công nhân, chuyên gia qua chốt kiểm soát', '<p><em>Bạn đọc Nguyễn Xuân Hiếu đặt câu hỏi: \"Tôi lái ô tô của công ty liên danh với nước ngoài chuyên đưa đón chuyên gia. Theo quy định của Sở GTVT Hà Nội, xe được đi qua chốt, nhưng phải đáp ứng giấy tờ như danh sách người ngồi trên xe có giấy xét nghiệm PCR âm tính… Tuy nhiên có một số chốt lại đòi mã QR code luồng xanh. Vậy quy định chính xác là như thế nào?\".</em></p><p><strong>Trả lời: </strong></p><p>Đại diện Sở GTVT Hà Nội cho biết, đối với xe chở công nhân, chuyên gia của các cơ sở sản xuất, kinh doanh không bị dừng hoạt động của UBND cấp tỉnh, TP thì các doanh nghiệp đăng ký với khu công nghiệp, khu chế xuất để báo cáo danh sách người đi xe đến UBND quận, huyện. </p><p>UBND quận, huyện làm đầu mối tiếp nhận gửi danh sách sang Sở GTVT. Sau đó, GTVT sẽ cấp phù hiệu để xe được thông qua các chốt. </p><p>Để qua các chốt chặn, ngoài yêu cầu giấy xét nghiệm PCR âm tính trong 72h, còn phải có danh sách của từng người ngồi trên xe kèm theo căn cước công dân/chứng minh nhân dân.</p><p>Lực lượng kiểm tra tại các chốt sẽ rà soát nghiêm để tránh tình trạng chở người sai quy định.</p>', 'Lê Văn Tú', '2021-07-28', 2),
(9, 'dc165-1627462480817552335785.jpg', 'Bệnh viện Dã chiến số 16 TPHCM đi vào hoạt động', 'Bệnh viện Dã chiến số 16 TPHCM ', '<p><strong>Ng&agrave;y 28/7, Bệnh viện D&atilde; chiến số 16 của TPHCM (phường Ph&uacute; Thuận, Quận 7) ch&iacute;nh thức đi v&agrave;o hoạt động với quy m&ocirc; gần 3.000 giường sau 20 ng&agrave;y thần tốc x&acirc;y dựng.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong ng&agrave;y đầu ti&ecirc;n, Bệnh viện D&atilde; chiến số 16 c&oacute; gần 700 giường. Hai ng&agrave;y tới sẽ nhận b&agrave;n giao tiếp 500 giường, số giường c&ograve;n lại ho&agrave;n th&agrave;nh v&agrave;o đầu th&aacute;ng 8/2021.</p>\r\n\r\n<p>Bệnh viện d&atilde; chiến n&agrave;y do Bệnh viện H&ugrave;ng Vương tiếp nhận, vận h&agrave;nh. TS.BS Ho&agrave;ng Thị Diễm Tuyết - Gi&aacute;m đốc Bệnh viện H&ugrave;ng Vương cho biết, tất cả c&aacute;n bộ, nh&acirc;n vi&ecirc;n, y b&aacute;c sĩ trước khi đưa đến Bệnh viện D&atilde; chiến số 16 đ&atilde; được tập huấn kỹ phương ph&aacute;p tiếp nhận, điều trị, chăm s&oacute;c bệnh nh&acirc;n COVID-19. Mỗi người đều hứa dốc sức, &quot;thần tốc&quot; trong xử tr&iacute; ca bệnh, nhất l&agrave; ca bệnh chuyển biến nặng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://giadinh.mediacdn.vn/thumb_w/640/296230595582509056/2021/7/28/dc165-1627462480817552335785.jpg\" /></p>\r\n\r\n<p>H&agrave;nh lang rộng kết nối c&aacute;c ph&ograve;ng bệnh.</p>\r\n\r\n<p>Ngay trước khi bệnh viện mở cửa, s&aacute;ng 28/7, &ocirc;ng Nguyễn Trọng Khoa &ndash; Ph&oacute; Cục trưởng Cục Quản l&yacute; Kh&aacute;m chữa bệnh Bộ Y tế đ&aacute;nh gi&aacute; cao tinh thần chống dịch của từng c&aacute;n bộ, nh&acirc;n vi&ecirc;n ở bệnh viện v&agrave; b&agrave;y tỏ niềm tin với kinh nghiệm v&agrave; năng lực sẵn c&oacute;, đội ngũ nh&acirc;n sự của Bệnh viện H&ugrave;ng Vương sẽ vận h&agrave;nh tốt Bệnh viện D&atilde; chiến số 16.</p>\r\n\r\n<p>Ph&ograve;ng bệnh rộng r&atilde;i, tho&aacute;ng m&aacute;t.</p>\r\n\r\n<p>Tất cả ph&ograve;ng bệnh của Bệnh viện D&atilde; chiến số 16 được thiết kế tho&aacute;ng m&aacute;t, chia ra nhiều ph&acirc;n khu kh&aacute;c nhau: Ph&acirc;n khu điều h&agrave;nh; khu bệnh nh&acirc;n mới tiếp nhận; khu bệnh nh&acirc;n c&oacute; triệu chứng; khu cấp cứu&hellip;&nbsp;</p>\r\n\r\n<p>Bệnh viện c&oacute; đến 350 đầu &ocirc; xy đ&aacute;p ứng c&ugrave;ng l&uacute;c cho h&agrave;ng loạt bệnh nh&acirc;n trong t&igrave;nh huống phải cấp cứu, thở &ocirc; xy. Đồng thời đ&atilde; lắp đặt bồn &ocirc; xy c&oacute; dung t&iacute;ch 7,5m3 dẫn &ocirc; xy tới tận 350 giường cho bệnh nh&acirc;n. C&ugrave;ng với đ&oacute; chuẩn bị sẵn 350 b&igrave;nh &ocirc; xy di động c&oacute; gắn đầy đủ c&aacute;c bộ thở v&agrave; dụng cụ đi k&egrave;m.</p>\r\n', 'Nguyễn Như Quyền', '2021-07-28', 3),
(10, 'dich-covid-19-1627465483274301285818.jpeg', 'Ngày thứ 13 ở tâm dịch TP.HCM, kỹ thuật viên xét nghiệm Nguyễn Đình Hoàng dành ', '\"Hết dịch anh sẽ tặng em chiếc váy cô dâu\"', '<p>&nbsp;</p>\r\n\r\n<p>N&oacute;i chuyện qua điện thoại c&ugrave;ng vợ đang sinh sống ở H&agrave; Nội, anh Nguyễn Đ&igrave;nh Ho&agrave;ng bảo &ldquo;d&ugrave; xa m&agrave; gần&rdquo;. Ảnh: Ho&agrave;i Thương</p>\r\n\r\n<p>Anh Ho&agrave;ng cho hay, anh l&agrave; kỹ thuật vi&ecirc;n Khoa X&eacute;t nghiệm của Bệnh viện H&ugrave;ng Vương, tỉnh Ph&uacute; Thọ. Trong 55 nh&acirc;n vi&ecirc;n y tế của tỉnh Ph&uacute; Thọ chi viện v&agrave;o TP.HCM th&igrave; Bệnh viện H&ugrave;ng Vương c&oacute; 3 người, gồm 1 b&aacute;c sĩ hồi sức, 1 điều dưỡng v&agrave; anh, c&ograve;n lại 52 nh&acirc;n sự của Sở Y tế.</p>\r\n\r\n<p><img alt=\"\" src=\"https://giadinh.mediacdn.vn/thumb_w/640/296230595582509056/2021/7/28/dich-covid-19-1627465483274301285818.jpeg\" style=\"height:428px; width:640px\" /></p>\r\n\r\n<p>Những ng&agrave;y đầu đợt dịch bệnh thứ 4 b&ugrave;ng ph&aacute;t ở một số tỉnh th&agrave;nh, anh v&agrave; nhiều đồng nghiệp đ&atilde; ghi danh t&igrave;nh nguyện v&agrave;o c&aacute;c t&acirc;m dịch. Chuyến chi viện đến Bắc Giang anh Ho&agrave;ng bị lỗi hẹn bởi thời điểm đ&oacute; ng&agrave;nh y tế Bắc Giang cần nhiều điều dưỡng. Anh nhường suất lại cho c&aacute;c đồng nghiệp điều dưỡng l&ecirc;n đường. Dịch bệnh b&ugrave;ng ph&aacute;t nghi&ecirc;m trọng tại TP.HCM, lần n&agrave;y anh quyết t&acirc;m kh&ocirc;ng để m&igrave;nh đứng ngo&agrave;i cuộc, tiếp tục đăng k&yacute; xung phong.</p>\r\n\r\n<p>Ng&agrave;y được th&ocirc;ng b&aacute;o lịch l&ecirc;n đường v&agrave;o TP.HCM, cả 2 vợ chồng anh vừa k&yacute; v&agrave;o tờ giấy đăng k&yacute; kết h&ocirc;n, b&uacute;t mực c&ograve;n chưa r&aacute;o. Anh nhẩm t&iacute;nh, n&oacute;i &quot;h&ocirc;m l&ecirc;n đường, 2 vợ chồng son mới sống chung với nhau được 10 ng&agrave;y&quot;. Phải tạm g&aacute;c lại hạnh ph&uacute;c ri&ecirc;ng, thế nhưng may mắn l&agrave; thời điểm chia tay kh&ocirc;ng c&oacute; nước mắt, cũng kh&ocirc;ng n&iacute;u k&eacute;o để anh phải xao l&ograve;ng. Ngược lại, chị L&ecirc; Thị Thanh T&acirc;m v&agrave; gia đ&igrave;nh hai b&ecirc;n nội ngoại đều ủng hộ anh l&ecirc;n đường l&agrave;m nhiệm vụ. Ở nh&agrave;, họ sẽ l&agrave; hậu phương vững chắc của anh.</p>\r\n\r\n<p><strong>Mọi sự chung sức l&uacute;c n&agrave;y đều rất đ&aacute;ng qu&yacute;</strong></p>\r\n\r\n<p>Ng&agrave;y 14/7, đo&agrave;n Ph&uacute; Thọ v&agrave;o đến TP.HCM, chia ra từng mũi nhọn hỗ trợ c&aacute;c bệnh viện d&atilde; chiến, bệnh viện điều trị COVID-19 tr&ecirc;n địa b&agrave;n. Anh Ho&agrave;ng v&agrave; một số đồng nghiệp được ph&acirc;n c&ocirc;ng tới Bệnh viện Hồi sức COVID-19, nơi tiếp nhận v&agrave; điều trị những bệnh nh&acirc;n nặng v&agrave; nguy kịch.</p>\r\n\r\n<p>L&agrave; kỹ thuật vi&ecirc;n x&eacute;t nghiệm, h&agrave;ng ng&agrave;y anh trực chiến trong ph&ograve;ng lab trực tiếp t&aacute;ch chiết chạy x&eacute;t nghiệm c&aacute;c mẫu bệnh phẩm của bệnh nh&acirc;n. D&ugrave; kh&ocirc;ng v&agrave;o c&aacute;c ph&ograve;ng điều trị nhưng cập nhật tin tức mỗi ng&agrave;y v&agrave; trực tiếp chứng kiến c&aacute;c đồng nghiệp khối điều trị quay cuồng trong khối c&ocirc;ng việc, anh c&oacute; thể hiểu r&otilde; cuộc chiến n&agrave;y thật sự cam go.</p>\r\n\r\n<p>Đối với nhiệm vụ chạy mẫu, để đảm bảo được khối lượng c&ocirc;ng việc, anh v&agrave; c&aacute;c đồng nghiệp chia th&agrave;nh 2 ca, 3 k&iacute;p. Mỗi ca k&eacute;o d&agrave;i 12 giờ đồng hồ (ca ng&agrave;y từ 6 giờ s&aacute;ng đến 6 giờ tối, ca đ&ecirc;m 6 giờ tối đến 6 giờ s&aacute;ng). C&aacute;c ca trực sẽ được lu&acirc;n phi&ecirc;n mỗi ng&agrave;y để đảm bảo giữ sức bền trong cuộc chiến n&agrave;y.</p>\r\n\r\n<p>Đời người hẳn đ&atilde; từng đưa ra nhiều quyết định ở những cột mốc đ&aacute;ng nhớ, c&oacute; thể đ&uacute;ng hoặc sai. Khi được hỏi &quot;anh c&oacute; hối hận khi quyết định rời xa tổ ấm để xung phong v&agrave;o t&acirc;m dịch&quot;. Anh cười, n&oacute;i: &quot;Hết ca trực th&igrave; nhớ vợ, nhớ gia đ&igrave;nh nhưng c&oacute; thể gọi điện thoại về tr&ograve; chuyện, d&ugrave; xa m&agrave; gần. C&ograve;n ng&agrave;y đ&oacute; (ng&agrave;y 14/7 đo&agrave;n l&ecirc;n đường chi viện v&agrave;o TP.HCM- PV), nếu kh&ocirc;ng l&ecirc;n đường, c&oacute; lẽ cả cuộc đời t&ocirc;i phải sống trong &acirc;n hận v&igrave; phủi đi tr&aacute;ch nhiệm. Hiện giờ mọi sự chung sức l&uacute;c n&agrave;y đều rất đ&aacute;ng qu&yacute;. T&ocirc;i tin tưởng v&agrave;o quyết định của m&igrave;nh&quot;.</p>\r\n\r\n<p>Vợ chồng anh Ho&agrave;ng chị T&acirc;m vừa chỉ đăng k&yacute; kết h&ocirc;n. Đ&aacute;m cưới chưa được tổ chức. Những ng&agrave;y ở TP.HCM, anh v&agrave; đồng nghiệp gồng m&igrave;nh, nỗ lực. Anh lu&ocirc;n nhắc vợ: &quot;Hết dịch anh sẽ tặng em chiếc v&aacute;y c&ocirc; d&acirc;u&quot;. Một đ&aacute;m cưới muộn nhưng trọn vẹn cả hạnh ph&uacute;c ri&ecirc;ng trong hạnh ph&uacute;c chung của x&atilde; hội&hellip;</p>\r\n\r\n<p>&quot; &gt;</p>\r\n', 'Hoài Thu', '2021-07-28', 3),
(11, 'casi.jpg', 'Trong thời gian giãn cách xã hội, nhiều ca sĩ vẫn tiếp tục tung ra các chương trình, show trực tuyến riêng thu hút sự chú ý đón xem của khán giả.', 'Hấp dẫn show nhạc online thời giãn cách', '<p>gi&atilde;n c&aacute;ch x&atilde; hội, nhiều ca sĩ vẫn tiếp tục tung ra c&aacute;c chương tr&igrave;nh, show trực tuyến ri&ecirc;ng thu h&uacute;t sự ch&uacute; &yacute; đ&oacute;n xem của kh&aacute;n giả.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://image.thanhnien.vn/2048/uploaded/minhnguyet/2021_07_30/hong-nhung_ykxn.jpg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p>Trong series&nbsp;<em>Quang Vinh Retreat</em>&nbsp;đang được ph&aacute;t tr&ecirc;n YouTube, ca sĩ Quang Vinh mang đến trải nghiệm kh&aacute; th&uacute; vị cho người xem khi show vừa c&oacute; tr&ograve; chuyện về cuộc sống với kh&aacute;ch mời, vừa h&aacute;t lại những ca kh&uacute;c với phong c&aacute;ch acoustic mộc mạc, gợi nhớ về một mảng k&yacute; ức thời thanh xu&acirc;n, như song ca với Jun Phạm b&agrave;i&nbsp;<em>Nếu em đi</em>, với Hiền Thục trong ca kh&uacute;c&nbsp;<em>Đ&ecirc;m lạnh</em>. Mới đ&acirc;y, Quang Vinh h&aacute;t&nbsp;<em>Khi người lớn c&ocirc; đơn</em>&nbsp;với Ali Ho&agrave;ng Dương giữa kh&ocirc;ng gian biển trời xanh m&aacute;t, mang lại cảm x&uacute;c th&uacute; vị cho người xem. &ldquo;T&ocirc;i hy vọng những sản phẩm n&agrave;y gi&uacute;p kh&aacute;n giả c&oacute; được những gi&acirc;y ph&uacute;t thư th&aacute;i trong cao điểm chống dịch&nbsp;<a href=\"https://thanhnien.vn/covid-19/\" rel=\"\">Covid-19</a>&nbsp;hiện nay&rdquo;, Quang Vinh n&oacute;i.</p>\r\n\r\n<p><a href=\"https://thanhnien.vn/van-hoa/nhon-nhip-live-show-ca-nhac-cuoi-nam-1126650.html\" rel=\"\" target=\"_blank\">Ca sĩ Quang H&agrave;</a>&nbsp;cũng vừa thực hiện show nhạc online&nbsp;<em>Tr&aacute;nh&nbsp;<a href=\"https://thanhnien.vn/covid/\" rel=\"\">Covid</a>-19&nbsp;</em>ph&aacute;t trực tiếp tr&ecirc;n fanpage Facebook của m&igrave;nh, đưa người xem trải qua nhiều cung bậc cảm x&uacute;c khi thể hiện loạt hit c&ugrave;ng những bản t&igrave;nh ca quen thuộc. Quang H&agrave; cho biết anh rất nhớ s&acirc;n khấu n&ecirc;n đ&atilde; biến ph&ograve;ng thu tại nh&agrave; th&agrave;nh một s&acirc;n khấu ca nhạc lung linh, đầy đủ &acirc;m thanh, &aacute;nh s&aacute;ng, thiết bị hiện đại. Ngo&agrave;i ra c&oacute; thể kể đến show online H&aacute;t cho những ai ở trong nh&agrave; của Đức Tuấn,&nbsp;<em>Livestream in sweet home&nbsp;</em>của Tuấn Hưng...</p>\r\n\r\n<p>&quot; &gt;</p>\r\n', 'Nguyễn Thanh Bằng', '2021-07-31', 4),
(12, 'ami.png', 'Lần đầu tiên, Billie Eilish - ngôi sao làng nhạc Âu Mỹ gửi lời chào đến Việt Nam, thậm chí còn nói khá trơn tru tiếng Việt khiến các fan bất ngờ.', 'Ngôi sao Billie Eilish lần đầu gửi lời ‘Xin chào Việt Nam’', '<p>Lần đầu ti&ecirc;n, Billie Eilish - ng&ocirc;i sao l&agrave;ng nhạc &Acirc;u Mỹ gửi lời ch&agrave;o đến Việt Nam, thậm ch&iacute; c&ograve;n n&oacute;i kh&aacute; trơn tru tiếng Việt khiến c&aacute;c fan bất ngờ.</p>\r\n\r\n<p>Khuya 30.7, cộng đồng fan nhạc US-UK đ&atilde; c&oacute; một đ&ecirc;m kh&ocirc;ng ngủ để chờ đ&oacute;n album của ng&ocirc;i sao Billie Eilish.</p>\r\n\r\n<p>Ca kh&uacute;c chủ đề c&ugrave;ng t&ecirc;n&nbsp;<em>Happier than ever</em>&nbsp;được rất nhiều fan đ&aacute;nh gi&aacute; l&agrave; &quot;nghe như d&aacute;t v&agrave;ng v&agrave;o tai&quot;, c&ugrave;ng với đ&oacute; l&agrave; sự phủ s&oacute;ng rầm rộ của Billie Eilish tr&ecirc;n tất cả c&aacute;c nền tảng nghe nhạc quốc tế v&agrave; Việt Nam khiến c&aacute;c fan v&ocirc; c&ugrave;ng phấn kh&iacute;ch.</p>\r\n\r\n<p><img alt=\"\" src=\"https://image.thanhnien.vn/2048/uploaded/nguyenvan/2021_08_01/pastedimage01_igxi.png\" style=\"height:639px; width:640px\" /></p>\r\n\r\n<p>Cũng ngay trong tối 30.7, Billie Eilish tiếp tục tung ra MV cho ca kh&uacute;c&nbsp;<em><em>Happier than ever</em></em>&nbsp;v&agrave; được dự đo&aacute;n sẽ nhanh ch&oacute;ng thống lĩnh c&aacute;c&nbsp;<a href=\"https://thethao.thanhnien.vn/\" rel=\"\">bảng xếp hạng</a>&nbsp;to&agrave;n cầu trong thời gian tới.</p>\r\n\r\n<p>Th&uacute; vị v&agrave; bất ngờ hơn, đ&uacute;ng sau 1 ng&agrave;y ph&aacute;t h&agrave;nh album, video Billie Eilish gửi lời ch&agrave;o ri&ecirc;ng đến c&aacute;c fan Việt Nam, được đăng tải ch&iacute;nh thức tr&ecirc;n fanpage Universal Music Vietnam, đ&atilde; khiến cộng đồng nghe nhạc v&agrave; cả fan của nữ ca sĩ &ldquo;vui sướng hơn bao giờ hết&rdquo;.</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-01', 4),
(13, 'vanhoa.jpg', 'Kinhtedothi - Bên nhau vào rạp xem phim Hương vị tình thân, cặp đôi Phương Oanh – Mạnh Trường có màn kết hợp “cười ra nước mắt”.', 'Phương Oanh - Mạnh Trường diễn cảnh vào rạp xem phim “Hương vị tình thân”', '<p>Kinhtedothi - B&ecirc;n nhau v&agrave;o rạp xem phim Hương vị t&igrave;nh th&acirc;n, cặp đ&ocirc;i Phương Oanh &ndash; Mạnh Trường c&oacute; m&agrave;n kết hợp &ldquo;cười ra nước mắt&rdquo;. Shark Long cũng kh&ocirc;ng ngần ngại &ldquo;tố&rdquo; xu hướng bạo lực của Phương Nam v&agrave; b&agrave;y tỏ sự lo lắng cho số phận của m&igrave;nh trong phần 2. Sự tung hứng ăn &yacute; của 2 diễn vi&ecirc;n trước nhiều thử th&aacute;ch l&agrave; điểm nhấn của Cuộc hẹn cuối tuần tập 3 (20 giờ thứ bảy 31/7 - VTV3).</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.kinhtedothi.vn/551/2021/7/30/cuoc%20hen%20cuoi%20tuan%203s.jpg\" style=\"height:426px; width:640px\" /></p>\r\n\r\n<p>Xuất hiện trong số n&agrave;y, cặp đ&ocirc;i nhiều s&oacute;ng gi&oacute; c&ugrave;ng theo d&otilde;i những khoảnh khắc hậu trường đầy cảm x&uacute;c khi tham gia phim&nbsp;<strong><em>Hương vị t&igrave;nh th&acirc;n&nbsp;</em></strong>m&agrave; ch&iacute;nh hai diễn vi&ecirc;n cũng lần đầu nh&igrave;n thấy. BTV Quốc Kh&aacute;nh c&ugrave;ng hai diễn vi&ecirc;n h&oacute;a th&acirc;n v&agrave;o vai tr&ograve; b&igrave;nh luận hậu trường, tạo ra tiếng cười vui vẻ. Chia sẻ sở th&iacute;ch qua ch&ugrave;m c&acirc;u hỏi nhanh, Phương Oanh &ndash; Mạnh Trường rơi v&agrave;o một t&igrave;nh huống &ldquo;kh&oacute; đỡ&rdquo; khi phải t&aacute;i hiện một phi&ecirc;n bản chia tay &ldquo;lầy lội&rdquo; ngay tr&ecirc;n s&acirc;n khấu của chương tr&igrave;nh.</p>\r\n\r\n<p>Sở th&iacute;ch về &acirc;m nhạc của hai kh&aacute;ch mời kh&ocirc;ng chỉ gi&uacute;p kh&aacute;n giả c&oacute; cơ hội thưởng thức tiếng đ&agrave;n piano của Mạnh Trường, m&agrave;n song ca &ldquo;xuất thần&rdquo; của Phương Oanh với ca sĩ L&acirc;m Bảo Ngọc m&agrave; c&ograve;n đưa kh&aacute;n giả đến với mini game s&ocirc;i động về &acirc;m nhạc giữa hai cặp Phương Oanh &ndash; BTV Quốc Kh&aacute;nh với Mạnh Trường &ndash; BTV Huyền Trang.</p>\r\n\r\n<p>Theo d&otilde;i chương tr&igrave;nh, kh&aacute;n giả sẽ c&ugrave;ng điểm lại c&aacute;c vai diễn nổi bật của&nbsp;<strong>Phương Oanh</strong>, chia sẻ về qu&atilde;ng thời gian kh&oacute; khăn nhất khi mới l&agrave;m nghề phải hứng chịu sự ho&agrave;i nghi gắn với danh xưng &ldquo;b&igrave;nh hoa di động&rdquo;, điều t&acirc;m đắc nhất ở vai diễn Phương Nam hay cảnh quay m&agrave; Phương Oanh kh&oacute;c ngay từ khi đọc kịch bản trong Hương vị t&igrave;nh th&acirc;n&hellip; Qua đ&oacute;, gi&uacute;p kh&aacute;n giả hiểu hơn nỗ lực, sự nghi&ecirc;m t&uacute;c, th&aacute;i độ cầu thị, cầu to&agrave;n với nghề của nữ diễn vi&ecirc;n tay ngang n&agrave;y.</p>\r\n\r\n<p>Với danh xưng &ldquo;vựa muối của VTV&rdquo; m&agrave; kh&aacute;n giả ưu &aacute;i đặt cho, BTV Việt Ho&agrave;ng tuần n&agrave;y mang tới g&oacute;c nh&igrave;n vừa d&iacute; dỏm vừa s&acirc;u sắc xoay quanh chủ đề &aacute;p lực của người nổi tiếng. Kh&ocirc;ng chỉ đưa ra c&aacute;c dẫn chứng ngắn gọn v&agrave; sinh động về c&aacute;ch c&aacute;c ng&ocirc;i sao quốc tế đương đầu với &aacute;p lực, Việt Ho&agrave;ng c&ograve;n mạnh dạn đưa ra một bản &ldquo;<em>Hướng dẫn cho người nổi tiếng khỏi tai tiếng</em>&rdquo; được 2 kh&aacute;ch mời t&acirc;m đắc</p>\r\n\r\n<p>L&agrave; một phần kh&ocirc;ng thể thiếu, &acirc;m nhạc của&nbsp;<em>Cuộc hẹn cuối tuần&nbsp;</em>tập 3 c&oacute; sự g&oacute;p mặt của Ngọt Band với ch&ugrave;m ca kh&uacute;c được y&ecirc;u th&iacute;ch<em>.</em>&nbsp;Trước đ&oacute;, xuất hiện c&ugrave;ng b&agrave;i h&aacute;t&nbsp;<em>Con đường hạnh ph&uacute;c&nbsp;</em>c&ugrave;ng bản phối mới ngọt ng&agrave;o, ca sĩ L&acirc;m Bảo Ngọc đ&atilde; l&agrave;m cho diễn vi&ecirc;n Phương Oanh rơi nước mắt nhớ lại tuổi thơ với b&agrave;i h&aacute;t y&ecirc;u th&iacute;ch nhất của m&igrave;nh.</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-01', 4),
(14, 'vanhoa2.jpg', 'Vụ án Trường Lạc giết vua chỉ được ghi trong Đại Việt sử ký toàn thư. Sử thần triều Nguyễn khi biên soạn Khâm định Việt sử thông giám cương', 'Trường Lạc giết vua hay trị bệnh cứu vua?', '<p><a href=\"https://thanhnien.vn/van-hoa/nghi-an-truong-lac-dau-doc-vua-le-thanh-tong-1422705.html\" rel=\"\">Vụ &aacute;n Trường Lạc giết vua</a>&nbsp;chỉ được ghi trong Đại Việt sử k&yacute; to&agrave;n thư. Sử thần triều Nguyễn khi bi&ecirc;n soạn Kh&acirc;m định Việt sử th&ocirc;ng gi&aacute;m cương mục đ&atilde; ho&agrave;n to&agrave;n bỏ qua m&agrave; kh&ocirc;ng b&igrave;nh luận g&igrave;.</p>\r\n\r\n<p><img alt=\"\" src=\"https://image.thanhnien.vn/2048/uploaded/hoangnam/2021_07_31/hinh-chan-trang-cn_rgbm.jpg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p>Hậu duệ của hung thủ xem vụ &aacute;n như chưa hề tồn tại cũng l&agrave; một h&agrave;nh động dễ hiểu. Nhưng đứng dưới g&oacute;c độ sử học đặt c&acirc;u hỏi: Những c&aacute;o buộc đối với qu&yacute; phi Nguyễn Thị Hằng v&agrave; th&aacute;i tử L&ecirc; Tranh đ&uacute;ng đến mức độ n&agrave;o?</p>\r\n\r\n<p>Trong L&ecirc; triều khiếu vịnh thi tập, H&agrave; Nhậm Đại n&oacute;i rằng th&aacute;i tử v&igrave; thấy L&ecirc; Th&aacute;nh T&ocirc;ng y&ecirc;u mến mẹ con Kinh vương n&ecirc;n đ&atilde; tiến h&agrave;nh tr&ugrave; yểm Th&aacute;nh T&ocirc;ng. Khi vừa l&ecirc;n ng&ocirc;i, &ocirc;ng liền giết chết mẹ con Kinh vương. Điều n&agrave;y ho&agrave;n to&agrave;n tr&aacute;i ngược với ghi ch&eacute;p trong ch&iacute;nh văn của Đại Việt sử k&yacute; to&agrave;n thư. Một ng&agrave;y trước khi băng h&agrave;, L&ecirc; Th&aacute;nh T&ocirc;ng đ&atilde; &ldquo;tựa kỷ ngọc, lệnh cho ho&agrave;ng th&aacute;i tử l&ecirc;n nối ng&ocirc;i&rdquo;. Nếu th&aacute;i tử tr&ugrave; yểm phụ ho&agrave;ng của m&igrave;nh th&igrave; liệu nh&agrave; vua c&oacute; tha thứ hay kh&ocirc;ng?</p>\r\n\r\n<p>Cứ cho rằng&nbsp;<a href=\"https://thanhnien.vn/van-hoa/nghi-an-truong-lac-dau-doc-vua-le-thanh-tong-1422705.html\" rel=\"\" target=\"_blank\">L&ecirc; Th&aacute;nh T&ocirc;ng&nbsp;</a>kh&ocirc;ng hề biết đến việc tr&ugrave; yểm đ&oacute; th&igrave; vẫn c&oacute; một c&aacute;o buộc sai sự thật. Đ&oacute; l&agrave; việc giết mẹ con Kinh vương. Đại Việt sử k&yacute; to&agrave;n thư khẳng định, Kinh vương L&ecirc; Kiện vẫn c&ograve;n sống ch&iacute; &iacute;t l&agrave; đến năm 1509 dưới thời L&ecirc; Uy Mục. V&igrave; L&ecirc; Uy Mục hiếu s&aacute;t, nghi ngờ, sai người d&ograve; x&eacute;t 26 vương gia trong đ&oacute; c&oacute; Kinh vương L&ecirc; Kiện n&ecirc;n &ocirc;ng n&agrave;y đ&atilde; trốn đi mất t&iacute;ch. Ch&iacute;nh L&ecirc; Qu&yacute; Đ&ocirc;n khi soạn Đại Việt th&ocirc;ng sử cũng đ&atilde; từng nhận x&eacute;t &ldquo;s&aacute;ch Khiếu vịnh thi tập ch&eacute;p rằng v&igrave; Vương được vua Th&aacute;nh T&ocirc;ng y&ecirc;u qu&yacute;, m&agrave; bị vua Hiến T&ocirc;ng giết, l&agrave; sai&rdquo;.</p>\r\n\r\n<h2>Trường Lạc giết vua hay cứu vua ?</h2>\r\n\r\n<p>Trong s&aacute;ch Chuyện phiếm sử học, Tạ Ch&iacute; Đại Trường đ&atilde; cho rằng qu&yacute; phi Nguyễn Thị Hằng kh&ocirc;ng giết vua. &Ocirc;ng lập luận: &ldquo;Th&aacute;nh T&ocirc;ng kh&ocirc;ng chết v&igrave; vợ bởi nếu thật như vậy th&igrave; tuyệt d&ograve;ng Nguyễn Đức Trung&rdquo;. &Ocirc;ng cho rằng căn bệnh của L&ecirc; Th&aacute;nh T&ocirc;ng mới l&agrave; ch&igrave;a kh&oacute;a giải th&iacute;ch h&agrave;nh động của qu&yacute; phi. Theo &ocirc;ng, vết thương của L&ecirc; Th&aacute;nh T&ocirc;ng kh&ocirc;ng phải l&agrave; bị thương v&igrave; chinh chiến. Bằng cớ l&agrave; sử s&aacute;ch ch&eacute;p L&ecirc; Th&aacute;nh T&ocirc;ng vẫn l&agrave;nh lặn khi từ Chi&ecirc;m Th&agrave;nh trở về v&agrave;o năm 1471. L&ecirc; Th&aacute;nh T&ocirc;ng chết l&agrave; v&igrave; bệnh phong thũng. &Ocirc;ng suy luận: &ldquo;Phong thũng, theo c&aacute;ch hiểu th&ocirc;ng thường, v&agrave; của cả y sinh ng&agrave;y xưa, l&agrave; chỉ hiện trạng bệnh lở l&oacute;i, c&ugrave;i hủi. Vua kh&ocirc;ng bị chiến thương như đ&atilde; n&oacute;i, m&agrave; sử quan lại c&oacute; lời m&agrave;o đầu l&agrave; vua mắc bệnh nặng &ldquo;v&igrave; nhiều phi tần qu&aacute;&rdquo;, vậy th&igrave; Th&aacute;nh T&ocirc;ng đ&atilde; mắc &ldquo;bệnh x&atilde; hội&rdquo;. Vua bị lở l&oacute;i ở chỗ đ&oacute;, hay khắp m&igrave;nh mẩy v&igrave; giang mai ở thời kỳ cuối?&rdquo;. &Ocirc;ng c&ograve;n n&oacute;i rằng thực ra qu&yacute; phi kh&ocirc;ng phải giết vua m&agrave; l&agrave; đang chữa trị cho vua: &ldquo;Y giới T&acirc;y phương trước khi biết đến loại kh&aacute;ng sinh, đ&atilde; chữa bệnh giang mai bằng hợp chất arsenic, y giới Đ&ocirc;ng cũng chữa bằng thạch t&iacute;n (arsenic). Th&aacute;i y viện đời L&ecirc; đ&atilde; d&ugrave;ng vị m&atilde; tiền c&oacute; thạch t&iacute;n chữa cho Th&aacute;nh T&ocirc;ng chăng? V&igrave; thế mới c&oacute; ghi nhận Trường Lạc ho&agrave;ng hậu b&ocirc;i &ldquo;thuốc độc&rdquo; (thạch t&iacute;n) cho vua?&rdquo;.</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-01', 4),
(15, 'xahoi.jpg', 'Tổng cục Hậu cần sáng nay (1/8) tổ chức xuất quân Bệnh viện Dã chiến truyền nhiễm số 5D Bộ Quốc phòng đi làm nhiệm vụ điều trị bệnh nhân Covid-19', '130 sĩ quan Bệnh viện dã chiến Bộ Quốc phòng xuất quân vào miền Nam', '<p>Tổng cục Hậu cần s&aacute;ng nay (1/8) tổ chức xuất qu&acirc;n Bệnh viện D&atilde; chiến truyền nhiễm số 5D Bộ Quốc ph&ograve;ng đi l&agrave;m nhiệm vụ điều trị bệnh nh&acirc;n Covid-19 tại TP.HCM v&agrave; tỉnh B&igrave;nh Dương.</p>\r\n\r\n<p><a href=\"https://vietnamnet.vn/vn/thoi-su/tin-anh/dem-trang-cua-luc-luong-truc-chot-kiem-soat-phong-dich-covid-19-761048.html\" title=\"Đêm trắng của lực lượng trực chốt kiểm soát phòng dịch Covid-19 ở Hà Nội\">Đ&ecirc;m trắng của lực lượng trực chốt kiểm so&aacute;t ph&ograve;ng dịch Covid-19 ở H&agrave; Nội</a></p>\r\n\r\n<p><a href=\"https://vietnamnet.vn/vn/thoi-su/quoc-phong/huy-dong-1-300-xe-van-tai-quan-doi-van-chuyen-vac-xin-covid-19-755911.html\" title=\"Huy động 1.300 xe vận tải quân đội vận chuyển vắc xin Covid-19\">Huy động 1.300 xe vận tải qu&acirc;n đội vận chuyển vắc xin Covid-19</a></p>\r\n\r\n<p><a href=\"https://vietnamnet.vn/vn/thoi-su/an-uong-ngay-gian-cach-761584.html\" title=\"Ăn uống ngày giãn cách\">Ăn uống ng&agrave;y gi&atilde;n c&aacute;ch</a></p>\r\n\r\n<p>Theo quyết định của Bộ trưởng Quốc ph&ograve;ng, Bệnh viện d&atilde; chiến truyền nhiễm số 5D trực thuộc Tổng cục Hậu cần&nbsp;được th&agrave;nh lập l&acirc;m thời c&oacute; quy m&ocirc; 500 giường bệnh, bi&ecirc;n chế 130 c&aacute;n bộ chiến sĩ, do Bệnh viện Qu&acirc;n y&nbsp;105 chủ tr&igrave;, phối hợp với Qu&acirc;n khu 7, Viện Y học Cổ truyền Qu&acirc;n đội v&agrave; một số đơn vị triển khai x&acirc;y dựng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2021/08/01/12/90af8e7d1651e10fb840.jpg\" style=\"height:427px; width:640px\" /></p>\r\n\r\n<p>Bệnh viện đứng ch&acirc;n tại k&yacute; t&uacute;c x&aacute; Đại học Quốc gia TP Hồ Ch&iacute; Minh, TP Dĩ An (tỉnh B&igrave;nh Dương), được tổ chức gồm: Ban gi&aacute;m đốc, 4 cơ quan v&agrave; 10 khoa kh&aacute;m, chữa bệnh, điển h&igrave;nh l&agrave;: Khoa kh&aacute;m bệnh, hồi sức; khoa điều trị bệnh nh&acirc;n c&aacute;c mức độ nhẹ, vừa, nặng; khoa ngoại tổng hợp, chống nhiễm khuẩn, chẩn đo&aacute;n h&igrave;nh ảnh, x&eacute;t nghiệm&hellip;</p>\r\n\r\n<p>Thiếu tướng Nguyễn H&ugrave;ng Thắng, Ph&oacute; Chủ nhiệm, Tham mưu trưởng Tổng cục Hậu cần động vi&ecirc;n v&agrave; giao nhiệm vụ cho đo&agrave;n,&nbsp;đ&acirc;y l&agrave; nhiệm vụ nặng nề, nhiều kh&oacute; khăn đặt ra cho mỗi c&aacute;n bộ, nh&acirc;n vi&ecirc;n bệnh viện.</p>\r\n\r\n<p>Bệnh viện d&atilde; chiến&nbsp;hoạt động xa đơn vị, giữa t&acirc;m dịch, vất vả, nguy hiểm c&ugrave;ng địa b&agrave;n mới lạ, thời tiết kh&iacute; hậu nắng n&oacute;ng, xa hậu phương gia đ&igrave;nh, một số&nbsp;c&aacute;n bộ, chiến sĩ&nbsp;ho&agrave;n cảnh gia đ&igrave;nh c&ograve;n c&oacute; những kh&oacute; khăn nhất định&hellip;</p>\r\n\r\n<p>Thiếu tướng Nguyễn H&ugrave;ng Thắng đ&aacute;nh gi&aacute; cao c&aacute;n bộ, nh&acirc;n vi&ecirc;n, chiến sĩ Bệnh viện d&atilde; chiến đ&atilde; t&iacute;ch cực, chủ động l&agrave;m tốt c&ocirc;ng t&aacute;c chuẩn bị để l&ecirc;n đường theo đ&uacute;ng kế hoạch với &yacute; ch&iacute; quyết t&acirc;m cao, sẵn s&agrave;ng nhận v&agrave; ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ được giao; với với tinh thần tiến c&ocirc;ng, ph&aacute;t huy truyền thống tự lực, tự cường của ng&agrave;nh Hậu cần Qu&acirc;n đội, v&igrave; đồng b&agrave;o, chiến sĩ c&aacute;c tỉnh ph&iacute;a Nam.</p>\r\n\r\n<p>Trước giờ xuất qu&acirc;n, Đại t&aacute;, B&aacute;c sĩ CK2 Nguyễn Văn Chinh, Ph&oacute; gi&aacute;m đốc&nbsp;Bệnh viện Qu&acirc;n y&nbsp;105, Gi&aacute;m đốc Bệnh viện d&atilde; chiến&nbsp;hứa to&agrave;n bệnh viện sẽ đo&agrave;n kết, thống nhất cao, ph&aacute;t huy hết năng lực, tr&iacute; tuệ, tinh thần tr&aacute;ch nhiệm, khắc phục mọi kh&oacute; khăn, ho&agrave;n th&agrave;nh tốt nhiệm vụ được giao.</p>\r\n\r\n<p>Tổng cục Hậu cần đ&atilde; tham mưu c&aacute;c kế hoạch x&acirc;y dựng, triển khai nhiều lực lượng qu&acirc;n y đi l&agrave;m nhiệm vụ; triển khai th&agrave;nh lập 2 bệnh viện d&atilde; chiến truyền nhiễm số 4 v&agrave; số 5D, trong đ&oacute;, Bệnh viện d&atilde; chiến truyền nhiễm số 4 đ&atilde; được triển khai tại&nbsp;TP Nha Trang, tỉnh Kh&aacute;nh H&ograve;a</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-01', 2),
(16, 'xahoi3.jpg', '“Chúng tôi đã liên lạc 17 người đi xe máy đang ở đèo Bình Đê, 2 người đang trên tàu hỏa về quê tự phát sẽ được tiếp nhận đợt cuối”', 'Chủ tịch Quảng Ngãi nói về việc tiếp nhận người dân về quê tự phát đang ở đèo Bình Đê', '<p>&ldquo;Ch&uacute;ng t&ocirc;i đ&atilde; li&ecirc;n lạc 17 người đi xe m&aacute;y đang ở đ&egrave;o B&igrave;nh Đ&ecirc;, 2 người đang tr&ecirc;n t&agrave;u hỏa về qu&ecirc; tự ph&aacute;t sẽ được tiếp nhận đợt cuối&rdquo;, &ocirc;ng&nbsp;Đặng Văn Minh cho biết.</p>\r\n\r\n<p><a href=\"https://vietnamnet.vn/vn/thoi-su/binh-thuan-de-nghi-cung-cap-danh-sach-nguoi-ve-tu-dong-nai-761587.html\" title=\"Bình Thuận đề nghị cung cấp danh sách người về từ Đồng Nai\">B&igrave;nh Thuận đề nghị cung cấp danh s&aacute;ch người về từ Đồng Nai</a></p>\r\n\r\n<p><a href=\"https://vietnamnet.vn/vn/thoi-su/7-500-nguoi-muon-ve-que-cach-ly-tra-phi-cho-quang-ngai-phan-hoi-761553.html\" title=\"7.500 người muốn về quê cách ly trả phí, chờ Quảng Ngãi phản hồi\">7.500 người muốn về qu&ecirc; c&aacute;ch ly trả ph&iacute;, chờ Quảng Ng&atilde;i phản hồi</a></p>\r\n\r\n<p><a href=\"https://vietnamnet.vn/vn/thoi-su/phu-yen-don-toan-bo-cong-dan-o-cac-tinh-phia-nam-ve-que-761578.html\" title=\"Phú Yên đón toàn bộ công dân ở các tỉnh phía Nam về quê\">Ph&uacute; Y&ecirc;n đ&oacute;n to&agrave;n bộ c&ocirc;ng d&acirc;n ở c&aacute;c tỉnh ph&iacute;a Nam về qu&ecirc;</a></p>\r\n\r\n<p>S&aacute;ng 1/8, trao đổi với VietNamNet, Chủ tịch UBND tỉnh Quảng Ng&atilde;i Đặng Văn Minh vẫn khẳng định, chưa c&oacute; kế hoạch tiếp nhận c&ocirc;ng d&acirc;n Quảng Ng&atilde;i từ c&aacute;c tỉnh, TP đang c&oacute; dịch về qu&ecirc;.</p>\r\n\r\n<p>UBND tỉnh&nbsp;vẫn&nbsp;tạm dừng tiếp nhận c&ocirc;ng d&acirc;n về từ c&aacute;c địa phương đang c&oacute; dịch Covid-19 theo bản đồ dịch tễ của Bộ Y tế. Thời gian thực hiện bắt đầu từ 0 giờ ng&agrave;y 1/8.</p>\r\n\r\n<p>&ldquo;Tuy nhi&ecirc;n, hiện nay ch&uacute;ng t&ocirc;i đ&atilde; li&ecirc;n lạc 17 người d&acirc;n đang ở đ&egrave;o B&igrave;nh Đ&ecirc;, 2 người đang tr&ecirc;n t&agrave;u hỏa về qu&ecirc;&nbsp;tự ph&aacute;t sẽ được tiếp nhận đợt cuối&quot;, &ocirc;ng Minh n&oacute;i.</p>\r\n\r\n<p>C&ocirc;ng điện của Thủ tướng Ch&iacute;nh phủ ban h&agrave;nh ng&agrave;y 31/7 đ&atilde; chỉ đạo r&otilde; về việc c&aacute;c tỉnh chủ động hỗ trợ người d&acirc;n tỉnh m&igrave;nh đang ở TP.HCM v&agrave; c&aacute;c tỉnh đang c&oacute; dịch diễn biến phức tạp với số lượng ca mắc lớn.</p>\r\n\r\n<p>Mặt trận tổ quốc (MTTQ) Việt Nam tỉnh Quảng Ng&atilde;i đang vận động một đợt&nbsp;quy&ecirc;n g&oacute;p để hỗ trợ đồng b&agrave;o tỉnh đang ở TP.HCM.</p>\r\n\r\n<p>Ph&oacute; Chủ tịch Ủy ban MTTQ Việt Nam tỉnh Quảng Ng&atilde;i B&ugrave;i Đức Thọ th&ocirc;ng tin, tỉnh Quảng Ng&atilde;i đ&atilde; c&oacute; 20 chuyến xe, mỗi chuyến hơn 20 tấn h&agrave;ng gửi v&agrave;o TP.HCM.&nbsp;Người&nbsp;được hỗ trợ l&agrave; cư d&acirc;n đang sinh sống tại TP.HCM trong đ&oacute; c&oacute; người d&acirc;n tỉnh Quảng Ng&atilde;i...</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2021/08/01/12/19-nguoi-dan-ve-que-tu-phat-cuoi-cung-duoc-quang-ngai-tiep-nhan.jpg\" style=\"height:427px; width:640px\" /></p>\r\n\r\n<p>Cũng trong s&aacute;ng nay, li&ecirc;n lạc với Chủ tịch Hội đồng hương Quảng Ng&atilde;i tại TP.HCM, Trần Đ&igrave;nh Vĩnh cho hay, 2 tỷ đồng của tỉnh Quảng Ng&atilde;i gửi v&agrave;o, ch&uacute;ng t&ocirc;i đang x&eacute;t trong hơn 30 ngh&igrave;n người đ&atilde; đăng k&yacute; để c&oacute; hỗ trợ.</p>\r\n\r\n<p>&ldquo;V&igrave; chỉ x&aacute;c nhận qua điện thoại, đường link tự khai n&ecirc;n cần th&ecirc;m thời gian. Bước đầu ch&uacute;ng t&ocirc;i ưu ti&ecirc;n cho gia đ&igrave;nh ốm yếu kh&ocirc;ng c&oacute; tiền ăn, thất nghiệp d&agrave;i ng&agrave;y...&rdquo;, &ocirc;ng Vĩnh n&oacute;i.</p>\r\n\r\n<p><strong>Thừa Thi&ecirc;n-Huế: X&eacute;t nghiệm Covid-19 tr&ecirc;n diện rộng</strong></p>\r\n\r\n<p>Từ ng&agrave;y 1/8, tỉnh Thừa Thi&ecirc;n-Huế sẽ dừng tiếp nhận c&ocirc;ng d&acirc;n từ c&aacute;c tỉnh, th&agrave;nh phố trực thuộc trung ương đang thực hiện gi&atilde;n c&aacute;ch x&atilde; hội theo Chỉ thị 16 của Thủ tướng.</p>\r\n\r\n<p>Đối với những c&ocirc;ng d&acirc;n đ&atilde; rời khỏi c&aacute;c tỉnh, th&agrave;nh n&oacute;i tr&ecirc;n kể từ ng&agrave;y 31/7 trở về trước, tỉnh Thừa Thi&ecirc;n-Huế sẽ tổ chức tiếp nhận v&agrave; tiến h&agrave;nh c&aacute;c biện ph&aacute;p ph&ograve;ng, chống dịch theo qui định.</p>\r\n\r\n<p>&ldquo;Ban chỉ đạo ph&ograve;ng, chống dịch Covid-19 của tỉnh đề nghị c&ocirc;ng d&acirc;n Thừa Thi&ecirc;n-Huế đang sinh sống, học tập, l&agrave;m việc tại c&aacute;c địa phương đang thực hiện gi&atilde;n c&aacute;ch x&atilde; hội theo Chỉ thị 16 tạm thời kh&ocirc;ng rời khỏi nơi cư tr&uacute; v&agrave; thực hiện tinh thần gi&atilde;n c&aacute;ch &ldquo;ai ở đ&acirc;u ở đấy&rdquo; theo chỉ đạo của Thủ tướng cho đến khi t&igrave;nh h&igrave;nh dịch bệnh được kiểm so&aacute;t&rdquo;, một l&atilde;nh đạo Văn ph&ograve;ng UBND tỉnh cho biết.</p>\r\n\r\n<p>Cũng trong s&aacute;ng nay, Ban chỉ đạo ph&ograve;ng, chống Covid-19 tỉnh ph&aacute;t th&ocirc;ng b&aacute;o &ldquo;thượng khẩn&rdquo; về việc lấy mẫu x&eacute;t nghiệm s&agrave;ng lọc Covid-19 v&ugrave;ng trọng điểm tr&ecirc;n phạm vi to&agrave;n tỉnh.</p>\r\n\r\n<p>Theo đ&oacute;, trước diễn biến phức tạp của dịch Covid-19,&nbsp; Ban Chỉ đạo ph&ograve;ng, chống dịch&nbsp; sẽ lấy mẫu x&eacute;t nghiệm Covid-19 đối với c&aacute;c hộ gia đ&igrave;nh ở gần c&aacute;c chốt kiểm so&aacute;t y tế; Tiểu thương kinh doanh tại c&aacute;c chợ đầu mối, chợ trung t&acirc;m tại địa phương; C&aacute;c hộ gia đ&igrave;nh dọc tuyến QL1A, đường tr&aacute;nh Huế, đường Hồ Ch&iacute; Minh, nh&acirc;n vi&ecirc;n c&acirc;y xăng, nh&acirc;n vi&ecirc;n qu&aacute;n ăn...</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-01', 2);
INSERT INTO `tintuc` (`matin`, `anh`, `motatin`, `tieude`, `noidung`, `tacgia`, `ngayviet`, `maloai`) VALUES
(17, 'yte.webp', 'Người dân Thành phố an tâm \"ai ở đâu ở đấy\". Tuyệt đối không để người dân di chuyển khỏi Thành phố cho tới khi hết thời gian giãn cách, trừ những trường hợp được UBND TP', 'Từ 0h ngày 2/8, người dân tuyệt đối không tự di chuyển khỏi TP.HCM', '<p><strong>Người d&acirc;n Th&agrave;nh phố an t&acirc;m &quot;ai ở đ&acirc;u ở đấy&quot;. Tuyệt đối kh&ocirc;ng để người d&acirc;n di chuyển khỏi Th&agrave;nh phố cho tới khi hết thời gian gi&atilde;n c&aacute;ch, trừ những trường hợp được UBND TP phối hợp với c&aacute;c tỉnh, th&agrave;nh đưa về qu&ecirc; theo nhu cầu.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ng&agrave;y 1/8, Chủ tịch UBND TP.HCM Nguyễn Th&agrave;nh Phong đ&atilde; k&yacute; ban h&agrave;nh c&ocirc;ng văn khẩn về việc tiếp tục &aacute;p dụng biện ph&aacute;p gi&atilde;n c&aacute;ch x&atilde; hội to&agrave;n Th&agrave;nh phố theo tinh thần Chỉ thị số 16 th&ecirc;m 14 ng&agrave;y kể từ 0 giờ ng&agrave;y 2/8/2021.</p>\r\n\r\n<p>Tại c&ocirc;ng văn n&agrave;y, UBND Th&agrave;nh phố y&ecirc;u cầu Thủ trưởng c&aacute;c sở - ban - ng&agrave;nh, Chủ tịch UBND TP Thủ Đức v&agrave; c&aacute;c quận - huyện, c&aacute;c doanh nghiệp tr&ecirc;n địa b&agrave;n Th&agrave;nh phố tiếp tục tập trung thực hiện mạnh mẽ hơn, quyết liệt hơn, triển khai c&oacute; hiệu quả c&aacute;c biện ph&aacute;p ph&ograve;ng, chống dịch theo đ&uacute;ng tinh thần chỉ đạo của UBND TP tại C&ocirc;ng văn số 2468/UBND-VX ng&agrave;y 23/7/2021 v&agrave; c&aacute;c C&ocirc;ng văn kh&aacute;c từ ng&agrave;y 9/7/2021 đến nay</p>\r\n\r\n<p><img alt=\"\" src=\"https://giadinh.mediacdn.vn/thumb_w/640/296230595582509056/2021/8/1/tphcm-gian-cach-theo-chi-thi-16-16277964790781492109069.jpeg\" style=\"height:426px; width:640px\" /></p>\r\n\r\n<p>TP.HCM tiếp tục thực hiện Chỉ thị 16 th&ecirc;m 14 ng&agrave;y.</p>\r\n\r\n<p>Đồng thời, tuy&ecirc;n truyền rộng r&atilde;i, kiểm so&aacute;t nghi&ecirc;m ngặt v&agrave; thực hiện ngay c&aacute;c biện ph&aacute;p hỗ trợ cần thiết về đời sống, y tế để người d&acirc;n Th&agrave;nh phố an t&acirc;m &quot;ai ở đ&acirc;u ở đấy&quot;. Tuyệt đối kh&ocirc;ng để người d&acirc;n di chuyển khỏi Th&agrave;nh phố cho tới khi hết thời gian gi&atilde;n c&aacute;ch, trừ những trường hợp được UBND TP phối hợp với c&aacute;c tỉnh, th&agrave;nh đưa về qu&ecirc; theo nhu cầu.</p>\r\n\r\n<p>TP.HCM tiếp tục thực hiện nghi&ecirc;m c&aacute;c biện ph&aacute;p giảm mật độ giao th&ocirc;ng với c&aacute;c nh&oacute;m đối tượng, thời gian đ&atilde; được quy định cụ thể tại C&ocirc;ng văn số 2490/UBND-VX ng&agrave;y 26/7/2021, C&ocirc;ng văn số 2522/UBND-VX ng&agrave;y 28/7/2021 v&agrave; C&ocirc;ng văn số 2523/UBND-VX ng&agrave;y 28/7/2021 của UBND TP.</p>\r\n\r\n<p>Trong c&ocirc;ng t&aacute;c tổ chức ti&ecirc;m vaccine, UBND Th&agrave;nh phố đề nghị c&aacute;c quận &ndash; huyện, TP Thủ Đức đẩy nhanh tiến độ ti&ecirc;m chủng, an to&agrave;n, hiệu quả, kh&ocirc;ng để vaccine hết hạn; phấn đấu mỗi ng&agrave;y một đội ti&ecirc;m phải ti&ecirc;m &iacute;t nhất 200 người/ng&agrave;y; t&ugrave;y theo điều kiện c&oacute; thể tổ chức ti&ecirc;m sau 18 giờ đối với những điểm ti&ecirc;m c&oacute; khả năng đ&aacute;p ứng để đẩy nhanh tiến độ; tổ chức c&aacute;c đội ti&ecirc;m lưu động để thực hiện ti&ecirc;m chủng tại c&aacute;c khu vực đặc th&ugrave;; đảm bảo c&aacute;c y&ecirc;u cầu ph&ograve;ng, chống dịch theo hướng dẫn của Bộ Y tế.</p>\r\n\r\n<p>Đồng thời tổ chức đảm bảo cung cấp, hỗ trợ kịp thời lương thực, thực phẩm cho tất cả những người lao động ngh&egrave;o, mất thu nhập, kh&ocirc;ng c&ograve;n nguồn dự trữ, nhất l&agrave; người d&acirc;n tại c&aacute;c khu phong toả; kh&ocirc;ng để người d&acirc;n n&agrave;o thiếu ăn, thiếu mặc; tổ chức hỗ trợ y tế cần thiết cho mọi người d&acirc;n, nhất l&agrave; người d&acirc;n trong c&aacute;c khu phong tỏa.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, tăng cường chăm lo động vi&ecirc;n tinh thần, đảm bảo phương tiện ph&ograve;ng hộ, hỗ trợ vật chất cho lực lượng y tế tuyến đầu v&agrave; c&aacute;c lực lượng tham gia ph&ograve;ng, chống dịch COVID-19.</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-01', 3),
(18, 'yte2.jpg', 'Trước tình hình lượng lớn dân di cư từ phía Nam ra Hải Phòng tránh dịch COVID-19, thành phố Hải Phòng yêu cầu đưa toàn bộ người dân đi cách ly tập trung.', 'Hải Phòng xuất hiện tình trạng xe container chở người trốn kiểm soát vào địa bàn', '<p><strong>Trước t&igrave;nh h&igrave;nh lượng lớn d&acirc;n di cư từ ph&iacute;a Nam ra Hải Ph&ograve;ng tr&aacute;nh dịch COVID-19, th&agrave;nh phố Hải Ph&ograve;ng y&ecirc;u cầu đưa to&agrave;n bộ người d&acirc;n đi c&aacute;ch ly tập trung.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tổ phản ứng nhanh ph&ograve;ng chống dịch COVID-19 Hải Ph&ograve;ng vừa cho biết, hiện rất nhiều người từ TP.Hồ Ch&iacute; Minh đang đổ về Hải Ph&ograve;ng, tập trung tại chốt kiểm so&aacute;t li&ecirc;n ng&agrave;nh ph&ograve;ng chống dịch COVID-19 khu vực ch&acirc;n cầu Ngh&igrave;n &ndash; quốc lộ 10.</p>\r\n\r\n<p><img alt=\"\" src=\"https://giadinh.mediacdn.vn/thumb_w/640/296230595582509056/2021/8/1/cnghin-16278036620521501039218.jpg\" style=\"height:479px; width:640px\" /></p>\r\n\r\n<p>Tối 31/7, địa b&agrave;n Hải Ph&ograve;ng xuất hiện t&igrave;nh trạng người trốn trong xe container v&agrave;o địa b&agrave;n</p>\r\n\r\n<p>Để giải ph&oacute;ng lượng người n&agrave;y, thừa lệnh Ban Chỉ đạo ph&ograve;ng chống dịch COVID-19 th&agrave;nh phố, Tổ trưởng Tổ phản ứng nhanh ph&ograve;ng chống dịch COVID-19 của Hải Ph&ograve;ng đ&atilde; giao Bệnh viện Hữu nghị Việt Tiệp khẩn trương lấy mẫu, x&eacute;t nghiệm RT-PCR cho nh&oacute;m đối tượng n&agrave;y.</p>\r\n\r\n<p>Trung t&acirc;m Y tế huyện Vĩnh Bảo tập trung giải quyết ph&acirc;n luồng những trường hợp n&agrave;o đ&atilde; c&oacute; xe trở về đến đ&oacute;, y&ecirc;u cầu c&ocirc;ng an chốt dẫn đường đưa thẳng v&agrave;o khu c&aacute;ch ly tập trung tại kh&aacute;ch sạn sinh vi&ecirc;n Trường Đại học Quản l&yacute; v&agrave; C&ocirc;ng nghệ Hải Ph&ograve;ng.</p>\r\n\r\n<p>Đối với những trường hợp phải giải quyết bằng xe của Trung t&acirc;m Y tế v&agrave; xe của Trung t&acirc;m Cấp cứu 115, giao Trung t&acirc;m Y tế huyện Vĩnh Bảo phối hợp với Trung t&acirc;m Cấp cứu 115 vận chuyển người v&agrave;o khu c&aacute;ch ly tập trung.</p>\r\n\r\n<p>C&ocirc;ng an huyện Vĩnh Bảo phối hợp với c&ocirc;ng an tại chốt đảm bảo an ninh trật tự gi&atilde;n c&aacute;ch x&atilde; hội ở chốt.&nbsp;Trung t&acirc;m Y tế quận L&ecirc; Ch&acirc;n triển khai tiếp nhận to&agrave;n bộ số người từ TP. Hồ Ch&iacute; Minh của Chốt cầu Ngh&igrave;n chuyển v&agrave;o khu c&aacute;ch ly tập trung tại kh&aacute;ch sạn sinh vi&ecirc;n Trường Đại học Quản l&yacute; v&agrave; C&ocirc;ng nghệ Hải Ph&ograve;ng, thực hiện c&aacute;ch ly theo quy định.</p>\r\n\r\n<p>Tổ trưởng Tổ phản ứng nhanh Phạm Thu Xanh lưu &yacute; tất cả c&aacute;c chốt, hiện nay nh&acirc;n d&acirc;n của c&aacute;c tỉnh ph&iacute;a Nam, đặc biệt TP. Hồ Ch&iacute; Minh đang c&oacute; biểu hiện chạy dịch về Hải Ph&ograve;ng cho an to&agrave;n.&nbsp;</p>\r\n\r\n<p>Hải Ph&ograve;ng y&ecirc;u cầu c&aacute;c chốt kiểm so&aacute;t chặt chẽ tất cả những người tr&ecirc;n c&aacute;c xe container</p>\r\n\r\n<p>Cụ thể, trong tối 31/7, địa b&agrave;n Hải Ph&ograve;ng xuất hiện những xe container chở h&agrave;ng chục người trốn ở trong. Để tr&aacute;nh lọt người đi từ v&ugrave;ng dịch v&agrave;o địa b&agrave;n kh&ocirc;ng thực hiện nghi&ecirc;m c&ocirc;ng t&aacute;c ph&ograve;ng dịch, y&ecirc;u cầu c&aacute;c chốt kiểm so&aacute;t chặt chẽ tất cả những người tr&ecirc;n c&aacute;c xe container, xe chở h&agrave;ng, chỉ c&oacute; những người c&oacute; x&eacute;t nghiệm PCR &acirc;m t&iacute;nh của những v&ugrave;ng kh&ocirc;ng bị c&aacute;ch ly theo Chỉ thị 16 mới được v&agrave;o th&agrave;nh phố.</p>\r\n\r\n<p>Tất cả những người kh&aacute;c của c&aacute;c tỉnh đang thực hiện c&aacute;ch ly theo Chỉ thị 16 đều phải đưa đi c&aacute;ch ly tập trung.</p>\r\n\r\n<p>Li&ecirc;n quan đến l&aacute;i xe đường d&agrave;i L.V.S, SN 1971 (địa chỉ tại phường K&ecirc;nh Dương, quận L&ecirc; Ch&acirc;n) dương t&iacute;nh với SARS-CoV-2, t&iacute;nh đến 9h00 ng&agrave;y 1/8/2021, cơ quan chức năng đ&atilde; tiến h&agrave;nh truy vết được 9 trường hợp F1. Theo đ&oacute;, nh&oacute;m F1 n&agrave;y được đưa đi c&aacute;ch ly tập trung v&agrave; lấy mẫu x&eacute;t nghiệm. Kết quả lần 1 cho thấy, tất cả 9F1 đ&atilde; &acirc;m t&iacute;nh với virus SARS-CoV-2.</p>\r\n\r\n<p>Trước đ&oacute;, s&aacute;ng 31/7/2021, ng&agrave;nh y tế Hải Ph&ograve;ng ph&aacute;t hiện 1 bệnh nh&acirc;n l&agrave; l&aacute;i xe container đến Viện Y học biển (quận L&ecirc; Ch&acirc;n) kh&aacute;m v&agrave; l&agrave;m x&eacute;t nghiệm test nhanh COVID-19 c&oacute; kết quả dương t&iacute;nh. L&agrave;m x&eacute;t nghiệm tiếp theo bằng RT-PCR, kết quả khẳng định vẫn dương t&iacute;nh. Theo đ&oacute;, l&aacute;i xe đường d&agrave;i L.V.S được đưa c&aacute;ch ly ngay tại Bệnh viện Hữu nghị Việt Tiệp cơ sở 2.</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-01', 3),
(19, 'giaoduc.jpg', 'GDVN- Xuất sắc đạt điểm 10 trong kỳ thi tốt nghiệp, nữ sinh Trần Nguyễn Huyền Nhi đã truyền lửa đam mê học tập cho các thế hệ', 'Đảng viên trẻ chọn trường sư phạm, mong sớm quay về huyện đảo Cô Tô', '<p>GDVN- Xuất sắc đạt điểm 10 trong kỳ thi tốt nghiệp, nữ sinh Trần Nguyễn Huyền Nhi đ&atilde; truyền lửa đam m&ecirc; học tập cho c&aacute;c thế hệ học sinh huyện đảo tiền ti&ecirc;u C&ocirc; T&ocirc;.</p>\r\n\r\n<p><strong>Lan tỏa tinh thần học tập</strong></p>\r\n\r\n<p>Em Trần Nguyễn Huyền Nhi sinh ra v&agrave; lớn l&ecirc;n tại huyện đảo tiền ti&ecirc;u C&ocirc; T&ocirc;, nơi nằm c&aacute;ch đất liền 60 hải l&yacute; với diện t&iacute;ch nhỏ v&agrave; d&acirc;n số &iacute;t nhất tỉnh Quảng Ninh.</p>\r\n\r\n<p>Tại huyện C&ocirc; T&ocirc;, đến nay c&ograve;n thiếu thốn về điều kiện cơ sở vật chất gi&aacute;o dục n&ecirc;n học sinh tuyến đảo gặp nhiều kh&oacute; khăn, hạn chế trong qu&aacute; tr&igrave;nh học tập.</p>\r\n\r\n<p>Vượt qua những r&agrave;o cản về điều kiện học tập, em Trần Nguyễn Huyền Nhi &ndash; học sinh Trường Trung học phổ th&ocirc;ng C&ocirc; T&ocirc; trong kỳ thi tốt nghiệp trung học phổ th&ocirc;ng 2021 đ&atilde; đạt th&agrave;nh t&iacute;ch xuất sắc với 10 điểm m&ocirc;n Lịch Sử.</p>\r\n\r\n<p>Ngo&agrave;i ra, c&aacute;c m&ocirc;n thi kh&aacute;c của em đều đạt điểm cao như: m&ocirc;n To&aacute;n đạt 8,4 điểm; m&ocirc;n Ngữ văn đạt 8,5 điểm; m&ocirc;n Tiếng Anh đạt 9,4 điểm; m&ocirc;n Địa l&yacute; đạt 9,75 điểm v&agrave; m&ocirc;n Gi&aacute;o dục c&ocirc;ng d&acirc;n đạt 9,0 điểm.</p>\r\n\r\n<p>Với th&agrave;nh t&iacute;ch đ&aacute;ng tự h&agrave;o tr&ecirc;n, Huyền Nhi trở th&agrave;nh tấm gương s&aacute;ng cho c&aacute;c em học sinh kh&oacute;a tiếp theo của Trường Trung học phổ th&ocirc;ng C&ocirc; T&ocirc;.</p>\r\n\r\n<p>Lớn lao hơn, em đ&atilde; mang đến niềm tin, kh&aacute;t vọng v&agrave; lan tỏa sự nỗ lực học tập của bản th&acirc;n tới mọi thế hệ học sinh nơi huyện đảo tiền ti&ecirc;u.</p>\r\n\r\n<p><img alt=\"\" src=\"https://photo-cms-giaoduc.zadn.vn/w700/Uploaded/2021/rgzokt/2021_07_29/01-2793.jpg\" style=\"height:427px; width:640px\" /></p>\r\n\r\n<p>Theo thầy gi&aacute;o Nguyễn Hải Ph&ograve;ng &ndash; Hiệu trưởng Trường Trung học phổ th&ocirc;ng C&ocirc; T&ocirc;, Trần Nguyễn Huyền Nhi l&agrave; một học sinh chăm ngoan, học giỏi đều tất cả c&aacute;c m&ocirc;n học v&agrave; nổi trội nhất l&agrave; c&aacute;c m&ocirc;n học như Ngữ Văn, Lịch Sử, Địa l&yacute;.</p>\r\n\r\n<p>Trong 3 năm học tại trường, em đ&atilde; đạt được nhiều giải thưởng trong c&aacute;c kỳ thi học sinh giỏi cấp tỉnh.</p>\r\n\r\n<p>Từ trước đến nay c&oacute; rất nhiều học sinh của nh&agrave; trường đạt điểm cao tại kỳ thi trung học phổ th&ocirc;ng nhưng em Huyền Nhi l&agrave; học sinh đầu ti&ecirc;n của trường đạt được điểm 10. Đ&acirc;y l&agrave; niềm tự h&agrave;o của thầy v&agrave; tr&ograve; trường Trung học phổ th&ocirc;ng C&ocirc; T&ocirc;.</p>\r\n\r\n<p>&ldquo;Kết quả k&igrave; thi tốt nghiệp năm nay của Trần Nguyễn Huyền Nhi l&agrave; minh chứng cho qu&aacute; tr&igrave;nh r&egrave;n luyện chăm chỉ bền bỉ của Huyền Nhi.</p>\r\n\r\n<p>Đối với một học sinh nơi tuyến đảo vẫn c&ograve;n c&oacute; những kh&oacute; khăn về điều kiện vật chất trong qu&aacute; tr&igrave;nh giảng dạy th&agrave;nh t&iacute;ch Huyền Nhi đạt được rất đ&aacute;ng kh&acirc;m phục v&agrave; tự h&agrave;o&rdquo; thầy gi&aacute;o Nguyễn Hải Ph&ograve;ng x&uacute;c động chia sẻ.</p>\r\n\r\n<p>Với nỗ lực học tập kh&ocirc;ng ngừng, Huyền Nhi c&oacute; một bảng th&agrave;nh t&iacute;ch học tập rất đ&aacute;ng nể với 12 năm liền đều đạt danh hiệu học sinh giỏi.</p>\r\n\r\n<p>Huyền Nhi từng đạt giải Ba to&agrave;n quốc Cuộc thi vận dụng kiến thức li&ecirc;n m&ocirc;n để giải quyết t&igrave;nh huống thực tiễn d&agrave;nh cho học sinh trung học với đề t&agrave;i: &ldquo;Học sinh C&ocirc; T&ocirc; g&oacute;p phần giảm thiểu &ocirc; nhiễm m&ocirc;i trường biển tại huyện đảo C&ocirc; T&ocirc;&rdquo; v&agrave; giải Nh&igrave; m&ocirc;n Lịch sử cấp tỉnh.</p>\r\n\r\n<p>Ng&agrave;y 09/5/2021 nh&acirc;n dịp ch&agrave;o mừng, kỷ niệm 60 năm ng&agrave;y B&aacute;c Hồ ra thăm qu&acirc;n v&agrave; d&acirc;n đảo C&ocirc; T&ocirc;, em Trần Nguyễn Huyền Nhi vinh dự được Chi bộ trường Trung học phổ th&ocirc;ng C&ocirc; T&ocirc; kết nạp v&agrave;o Đảng Cộng sản Việt Nam.</p>\r\n\r\n<p><strong>Theo đuổi ước mơ trở th&agrave;nh một c&ocirc; gi&aacute;o</strong></p>\r\n\r\n<p>Kh&ocirc;ng chỉ l&agrave; tấm gương s&aacute;ng học tập đại diện cho c&aacute;c thế hệ học sinh trường Trung học phổ th&ocirc;ng C&ocirc; T&ocirc;, Huyền Nhi c&ograve;n lan tỏa tinh thần dũng cảm thực hiện ước mơ, theo đuổi đam m&ecirc; của m&igrave;nh.</p>\r\n\r\n<p>Vốn gia đ&igrave;nh c&oacute; truyền thống nghề gi&aacute;o c&oacute; cả bố v&agrave; mẹ đều l&agrave; gi&aacute;o vi&ecirc;n n&ecirc;n ngay từ nhỏ Huyền Nhi đ&atilde; được ảnh hưởng sự nề nếp trong t&aacute;c phong sinh hoạt, học tập, ứng xử.</p>\r\n\r\n<p>Đồng thời, Nhi cũng nu&ocirc;i ước mơ trở th&agrave;nh một c&ocirc; gi&aacute;o, ph&aacute;t huy truyền thống gia đ&igrave;nh nh&agrave; gi&aacute;o như bố mẹ của em.</p>\r\n\r\n<p>Với th&agrave;nh t&iacute;ch cao trong kỳ thi tốt nghiệp, Nhi c&oacute; nhiều lựa chọn v&agrave;o c&aacute;c trường đại học tốp đầu tuyển sinh ban C tuy nhi&ecirc;n em chọn theo học Khoa Ngữ Văn của Trường Đại học sư phạm H&agrave; Nội để tiến gần hơn với ước mơ của m&igrave;nh.</p>\r\n\r\n<p>Chia sẻ niềm vui khi đạt được kết quả thi đ&uacute;ng như nguyện vọng, Huyền Nhi b&agrave;y tỏ: &quot;Em v&ocirc; c&ugrave;ng biết ơn bố mẹ v&agrave; c&aacute;c thầy c&ocirc; đ&atilde; dạy dỗ d&igrave;u dắt em từng bước trưởng th&agrave;nh, lu&ocirc;n ủng hộ động vi&ecirc;n để em c&oacute; được th&agrave;nh quả ng&agrave;y h&ocirc;m nay.</p>\r\n\r\n<p>Ước mơ lớn nhất của em l&agrave; được trở th&agrave;nh một c&ocirc; gi&aacute;o, ph&aacute;t huy truyền thống gia đ&igrave;nh nh&agrave; gi&aacute;o như bố mẹ n&ecirc;n em lựa chọn v&agrave;o Khoa Ngữ văn Trường Đại học sư phạm H&agrave; Nội.</p>\r\n\r\n<p>Em sẽ cố gắng học tập thật tốt v&agrave; t&iacute;ch cực tham gia c&aacute;c hoạt động phong tr&agrave;o ở trường đại học.</p>\r\n\r\n<p>Sau khi học xong em muốn được trở về giảng dạy tại huyện đảo C&ocirc; T&ocirc; th&acirc;n y&ecirc;u, nơi gia đ&igrave;nh em đ&atilde; gắn b&oacute; suốt mấy chục năm qua&rdquo;.</p>\r\n\r\n<p>Theo thầy gi&aacute;o Nguyễn Viết Thiện &ndash; B&iacute; thư Đo&agrave;n trường Trung học phổ th&ocirc;ng C&ocirc; T&ocirc;, Huyền Nhi lựa chọn học ng&agrave;nh sư phạm, một ng&agrave;nh học m&agrave; nhiều bạn trẻ trong thời đại ng&agrave;y nay kh&ocirc;ng mấy mặn m&agrave;.</p>\r\n\r\n<p>Thầy gi&aacute;o Thiện cũng như những thầy, c&ocirc; gi&aacute;o v&agrave; bạn học đ&atilde; đồng h&agrave;nh 3 năm trung học phổ th&ocirc;ng lu&ocirc;n tin tưởng v&agrave;o lựa chọn của Huyền Nhi v&agrave; ch&uacute;c em đạt được ước mơ của m&igrave;nh trở th&agrave;nh một c&ocirc; gi&aacute;o giỏi trong tương lai.</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-01', 5),
(20, 'giaoduc2.jpg', 'GDVN- Cầu tạm bị cuốn vì mưa lũ, bà con nhân dân xã Nậm Tin, Chà Cang nhanh chóng hoàn thiện cây cầu mới để thầy cô', 'Nậm Pồ hoàn thành sớm cây cầu tạm để thầy cô và học sinh đến trường', '<p>GDVN- Cầu tạm bị cuốn v&igrave; mưa lũ, b&agrave; con nh&acirc;n d&acirc;n x&atilde; Nậm Tin, Ch&agrave; Cang nhanh ch&oacute;ng ho&agrave;n thiện c&acirc;y cầu mới để thầy c&ocirc; v&agrave; học sinh đến trường</p>\r\n\r\n<p>Do ảnh hưởng của mưa lớn k&eacute;o d&agrave;i nhiều ng&agrave;y li&ecirc;n tục, cầu tạm tại km 4+300, thuộc tỉnh lộ 145, x&atilde; Ch&agrave; Cang &ndash; trung t&acirc;m&nbsp;<a href=\"https://giaoduc.net.vn/gdvn-post206796.gd\" title=\"2 giáo viên, 34 học sinh ở Nậm Pồ biểu hiện cúm, sốt có bố mẹ về từ Trung Quốc\">huyện Nậm Pồ (Điện Bi&ecirc;n)&nbsp;</a>đ&atilde; bị hư hỏng, cuốn tr&ocirc;i, g&acirc;y kh&oacute; khăn cho việc đi lại của người v&agrave; phương tiện qua khu vực.</p>\r\n\r\n<p>Nậm Tin l&agrave; x&atilde; xa x&ocirc;i hẻo l&aacute;nh của huyện Nậm Pồ (Điện Bi&ecirc;n), từ nhiều năm nay, c&aacute;c thầy c&ocirc; gi&aacute;o, c&aacute;c em học sinh đ&atilde; phải rất vất vả khắc phục kh&oacute; khăn để ho&agrave;n th&agrave;nh gi&aacute;o dục.</p>\r\n\r\n<p>Từ nhiều năm nay, ở Nậm Tin lu&ocirc;n gặp phải t&igrave;nh trạng thiếu điện, nước, ph&ograve;ng học đơn sơ&hellip; Ở Nậm Tin c&oacute; t&igrave;nh y&ecirc;u nghề, mến trẻ, nghị lực phi thường mới gi&uacute;p c&aacute;c c&ocirc; chiến thắng nghịch cảnh để theo đuổi ước mơ &ldquo;trồng người&rdquo;.</p>\r\n\r\n<p>Dẫu biết ở Nậm Tin rất kh&oacute; khăn, nhưng chiều 25/7, nhiều đoạn video được một số thầy c&ocirc; v&agrave; người d&acirc;n đăng tải tr&ecirc;n mạng x&atilde; hội phản &aacute;nh sự sợ h&atilde;i khi chứng kiến cảnh c&aacute;c thầy gi&aacute;o phải vượt qua d&ograve;ng nước lũ bằng một chiếc b&egrave; tre do người d&acirc;n tự chế.</p>\r\n\r\n<p>Ng&agrave;y 26/7, trao đổi với ph&oacute;ng vi&ecirc;n, c&ocirc; gi&aacute;o T&ograve;ng Thị Nọi &ndash; Hiệu trưởng trường mầm non Tậm Tin cho biết ch&iacute;nh quyền, nh&acirc;n d&acirc;n v&agrave; c&aacute;c thầy c&ocirc; gi&aacute;o ở Nậm Tin v&agrave; Ch&agrave; Cang đ&atilde; nhanh ch&oacute;ng l&agrave;m xong c&acirc;y cầu tạm để c&aacute;c thầy c&ocirc; gi&aacute;o v&agrave; học sinh đến trường.</p>\r\n\r\n<p>Khu vực n&agrave;y trước đ&acirc;y c&oacute; cầu nhưng bị hư hỏng từ năm 2018. Do chưa c&oacute; điều kiện sửa chữa n&ecirc;n huyện đ&atilde; l&agrave;m tạm một ngầm tr&agrave;n bằng đất, lắp cống bi để người d&acirc;n v&agrave; c&aacute;c phương tiện qua lại. Tuy nhi&ecirc;n, đợt mưa lớn k&eacute;o d&agrave;i vừa qua đ&atilde; cuốn tr&ocirc;i cầu tạm, khiến người d&acirc;n v&agrave; phương tiện qua đ&acirc;y phải di chuyển bằng b&egrave; tre hết sức nguy hiểm.</p>\r\n\r\n<p><img alt=\"\" src=\"https://photo-cms-giaoduc.zadn.vn/w700/Uploaded/2021/lcgkcwvo/2021_07_26/giaoduc-net-3-354.jpg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p>&Ocirc;ng Phạm Văn Tiếp, Ph&oacute; Ph&ograve;ng Gi&aacute;o dục v&agrave; Đ&agrave;o tạo huyện Nậm Pồ cho biết: &ldquo;Thời điểm xảy ra sự cố chưa v&agrave; năm học n&ecirc;n kh&ocirc;ng ảnh nhiều đến việc đến trường, b&ecirc;n cạnh đ&oacute;, đ&acirc;y cũng kh&ocirc;ng phải l&agrave; tuyến đường ch&iacute;nh để c&aacute;c thầy c&ocirc; gi&aacute;o đến trường. Tuy nhi&ecirc;n, việc khắc phục được c&acirc;y cầu như vậy cũng đỡ phần n&agrave;o cho c&aacute;c thầy c&ocirc; gi&aacute;o v&agrave; c&aacute;c em học sinh trong Nậm Tin.</p>\r\n\r\n<p>Một số h&igrave;nh ảnh đo&agrave;n vi&ecirc;n, c&aacute;c thầy c&ocirc; gi&aacute;o gấp r&uacute;t x&acirc;y dựng cầu tạm do c&aacute;c c&ocirc; gi&aacute;o cung cấp:</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-01', 5),
(21, 'giaoduc3.jpg', 'GDVN- Để thu hút cán bộ giỏi về chuyên môn, về quản lý điều hành, nhà trường cần xây dựng cơ chế hấp dẫn về vị trí việc làm', 'Giáo sư Lê Anh Tuấn hiến kế thu hút người uy tín ngoài trường vào bộ máy quản lý', '<p>GDVN- Để thu h&uacute;t c&aacute;n bộ giỏi về chuy&ecirc;n m&ocirc;n, về quản l&yacute; điều h&agrave;nh, nh&agrave; trường cần x&acirc;y dựng cơ chế hấp dẫn về vị tr&iacute; việc l&agrave;m, m&ocirc;i trường l&agrave;m việc, triển vọng ph&aacute;t triển.</p>\r\n\r\n<p><em>Tiếp tục ghi nhận &yacute; kiến chuy&ecirc;n gia, l&atilde;nh đạo một số cơ sở gi&aacute;o dục về vấn đề c&oacute; n&ecirc;n quy định bắt buộc Chủ tịch Hội đồng trường phải l&agrave; c&aacute;n bộ cơ hữu hay kh&ocirc;ng.</em></p>\r\n\r\n<p><em>H&ocirc;m nay, ph&oacute;ng vi&ecirc;n Tạp ch&iacute; điện tử Gi&aacute;o dục Việt Nam c&oacute; cuộc trao đổi với Gi&aacute;o sư L&ecirc; Anh Tuấn- Chủ tịch Hội đồng trường, Trường Đại học B&aacute;ch khoa H&agrave; Nội để lắng nghe &yacute; kiến của &ocirc;ng về vấn đề n&agrave;y.</em></p>\r\n\r\n<p><strong>C&oacute; &yacute; kiến cho rằng, quy định bắt buộc đối với Chủ tịch Hội đồng trường phải l&agrave; c&aacute;n bộ cơ hữu, đ&acirc;y l&agrave; một hạn chế v&igrave; kh&ocirc;ng thể thu h&uacute;t được người c&oacute; uy t&iacute;n ở ngo&agrave;i v&agrave;o. Thầy đ&aacute;nh gi&aacute; sao về quan điểm n&agrave;y?</strong></p>\r\n\r\n<p><strong>Gi&aacute;o sư L&ecirc; Anh Tuấn:</strong>&nbsp;Đối với nhiều trường đại học lớn tr&ecirc;n thế giới, Chủ tịch Hội đồng trường l&agrave; người ngo&agrave;i trường, thường l&agrave; l&atilde;nh đạo của một tập đo&agrave;n, doanh nghiệp lớn c&oacute; năng lực, kinh nghiệm v&agrave; trải nghiệm thực tiễn quản trị.</p>\r\n\r\n<p>Kinh nghiệm thực tiễn v&agrave; năng lực quản trị doanh nghiệp vượt trội của&nbsp;<a href=\"https://giaoduc.net.vn/gdvn-post219119.gd\" title=\"Có những &quot;ổ khóa&quot; siết chặt tự chủ nằm ngay trong Luật 34/2018/QH14\">Chủ tịch Hội đồng trường</a>&nbsp;v&agrave; c&aacute;c th&agrave;nh vi&ecirc;n Hội đồng trường, kết hợp với kinh nghiệm, năng lực điều h&agrave;nh quản l&yacute; của Ban gi&aacute;m hiệu, sẽ gi&uacute;p cho hoạt động quản trị, điều h&agrave;nh nh&agrave; trường được thực hiện theo đ&uacute;ng chiến lược, tầm nh&igrave;n tr&ecirc;n cơ sở tối ưu mọi nguồn lực.</p>\r\n\r\n<p>Để l&agrave;m được điều n&agrave;y, hệ thống gi&aacute;o dục đại học của họ đ&atilde; ph&aacute;t triển ở mức cao, một người ngo&agrave;i trường c&oacute; thể t&igrave;m hiểu v&agrave; nắm bắt cơ bản cấu tr&uacute;c hệ thống quản trị, điều h&agrave;nh của một trường đại học; ngo&agrave;i ra, với hệ thống quy chế, quy định, hướng dẫn đầy đủ v&agrave; r&otilde; r&agrave;ng, qu&aacute; tr&igrave;nh thực thi &iacute;t khi chịu t&aacute;c động bởi văn h&oacute;a đơn vị, bởi t&iacute;nh c&aacute;ch c&aacute; nh&acirc;n hay xung đột về chức năng, nhiệm vụ.</p>\r\n\r\n<p><img alt=\"\" src=\"https://photo-cms-giaoduc.zadn.vn/w700/Uploaded/2021/bpcgtqvp/2021_07_27/gs-le-anh-tuan-5147.jpg\" style=\"height:426px; width:640px\" /></p>\r\n\r\n<p>Thực tiễn ở Việt Nam c&oacute; nhiều điểm kh&aacute;c biệt, hệ thống gi&aacute;o dục đại học tự chủ mới đang ở giai đoạn đầu, mặt bằng nhận thức về tự chủ v&agrave; vận h&agrave;nh cơ sở gi&aacute;o dục đại học theo hướng tự chủ, tự chịu tr&aacute;ch nhiệm đang ở trong giai đoạn ho&agrave;n thiện, ch&iacute;nh v&igrave; vậy đ&ograve;i hỏi người l&atilde;nh đạo cao nhất cần hiểu biết hệ thống, hiểu biết thực trạng của đơn vị m&igrave;nh th&igrave; mới c&oacute; sự phối hợp tốt với c&aacute;c thực thể l&atilde;nh đạo, điều h&agrave;nh hiện tại của trường.</p>\r\n\r\n<p>Nếu&nbsp;<a href=\"https://giaoduc.net.vn/gdvn-post219686.gd\" title=\"3 đề xuất của Đại biểu Lê Quân về tăng cường tự chủ đại học\">Chủ tịch Hội đồng trường</a>&nbsp;kh&ocirc;ng phải l&agrave; c&aacute;n bộ cơ hữu th&igrave; d&ugrave; chủ trương, chiến lược được x&acirc;y dựng tốt cũng kh&ocirc;ng dễ g&igrave; vận h&agrave;nh được với nguồn lực sẵn c&oacute;, đặc biệt l&agrave; với nguồn lực con người, từ đ&oacute; dẫn tới vai tr&ograve; của Hội đồng trường sẽ kh&ocirc;ng ph&aacute;t huy được như mong đợi.</p>\r\n\r\n<p><strong>Nếu như vậy, thưa thầy, c&oacute; cơ chế n&agrave;o để l&agrave;m mới đội ngũ điều h&agrave;nh, quản l&yacute; bằng c&aacute;c c&aacute;n bộ được thu h&uacute;t từ b&ecirc;n ngo&agrave;i kh&ocirc;ng?</strong></p>\r\n\r\n<p><strong>Gi&aacute;o sư L&ecirc; Anh Tuấn:</strong>&nbsp;Trước ti&ecirc;n, để thu h&uacute;t được c&aacute;n bộ giỏi về chuy&ecirc;n m&ocirc;n, về quản l&yacute; điều h&agrave;nh, nh&agrave; trường cần x&acirc;y dựng cơ chế hấp dẫn về vị tr&iacute; việc l&agrave;m, m&ocirc;i trường l&agrave;m việc v&agrave; triển vọng ph&aacute;t triển.</p>\r\n\r\n<p>Hệ thống t&agrave;i ch&iacute;nh vững mạnh, chiến lược ph&aacute;t triển tham vọng v&agrave; hệ thống quản trị bền vững, ti&ecirc;n tiến cũng l&agrave; những điều kiện quan trọng để thu h&uacute;t người giỏi.</p>\r\n\r\n<p>được tiếp cận c&aacute;c b&aacute;o c&aacute;o n&agrave;y hạn chế. B&aacute;o c&aacute;o thường ni&ecirc;n, trong đ&oacute; bao h&agrave;m cả th&ocirc;ng tin t&agrave;i ch&iacute;nh v&agrave; t&igrave;nh h&igrave;nh thực hiện kế hoạch chiến lược cần được c&ocirc;ng bố như l&agrave; một b&aacute;o c&aacute;o quản trị hằng năm của một cơ sở gi&aacute;o dục đại học tự chủ.</p>\r\n\r\n<p>Theo Luật sửa đổi, bổ sung một số điều của Luật Gi&aacute;o dục đại học 2018 (gọi tắt l&agrave; Luật số 34) quy định: &quot;Hội đồng trường ban h&agrave;nh danh mục vị tr&iacute; việc l&agrave;m, ti&ecirc;u chuẩn v&agrave; điều kiện l&agrave;m việc của từng vị tr&iacute;&quot;.</p>\r\n\r\n<p>Do đ&oacute;, nếu c&aacute;c văn bản ph&aacute;p luật kh&aacute;c ủng hộ th&igrave; Hội đồng trường, tập thể l&atilde;nh đạo trường c&oacute; thể x&acirc;y dựng vị tr&iacute; việc l&agrave;m v&agrave; lựa chọn nh&acirc;n sự cơ hữu hay kh&ocirc;ng cơ hữu ph&ugrave; hợp nhất cho một số vị tr&iacute; quan trọng, đ&aacute;p ứng tốt nhất chiến lược ph&aacute;t triển của cơ sở&nbsp;<a href=\"https://giaoduc.net.vn/gdvn-post219459.gd\" title=\"Trao đổi sôi nổi về Nghị định 60 quy định cơ chế tự chủ tài chính\">gi&aacute;o dục đại học</a>&nbsp;tự chủ.</p>\r\n\r\n<p><a href=\"https://giaoduc.net.vn/gdvn-post219459.gd\" title=\"Trao đổi sôi nổi về Nghị định 60 quy định cơ chế tự chủ tài chính\">Nghị định 60/NĐ-CP</a>&nbsp;ng&agrave;y 21/6/2021 quy định cơ chế tự chủ t&agrave;i ch&iacute;nh của đơn vị sự nghiệp c&ocirc;ng lập, c&oacute; hiệu lực từ ng&agrave;y 15/8/2021, đ&atilde; mở ra cơ hội cho c&aacute;c cơ sở gi&aacute;o dục đại học tự chủ x&acirc;y dựng cơ chế tiền lương theo vị tr&iacute; việc l&agrave;m v&agrave; theo kết quả hoạt động (sau khi Chế độ tiền lương do Ch&iacute;nh phủ quy định theo Nghị quyết số 27-NQ/TW c&oacute; hiệu lực thi h&agrave;nh).</p>\r\n\r\n<p>Đ&acirc;y ch&iacute;nh l&agrave; một trong những cơ sở quan trọng cho việc chủ động lựa chọn nguồn nh&acirc;n sự b&ecirc;n trong (c&aacute;n bộ cơ hữu) hay b&ecirc;n ngo&agrave;i trường m&agrave; kh&ocirc;ng phải lo về sự thiếu hấp dẫn về chế độ tiền lương cho c&aacute;c vị tr&iacute; cốt yếu trong hệ thống điều h&agrave;nh, quản l&yacute;.</p>\r\n\r\n<p>C&oacute; như vậy th&igrave; mới tạo sự cạnh tranh, tạo động lực ph&aacute;t triển; nguồn lực b&ecirc;n trong do đ&oacute; sẽ dần thay đổi để đ&aacute;p ứng ng&agrave;y c&agrave;ng tốt hơn vị tr&iacute; đảm nhiệm hiện tại v&agrave; trang bị năng lực, phẩm chất để hướng đến c&aacute;c vị tr&iacute; mới th&aacute;ch thức hơn.</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-01', 5),
(22, 'giaoduc4.jpg', 'GDVN- Việc tăng số lượng học sinh lớp 10 tại nhiều trường trung học phổ thông nhằm đáp ứng nhu cầu, nguyện vọng của phụ huynh, học sinh.', 'Vụ điểm cao trượt, điểm thấp lại đậu: Quảng Bình tăng học sinh, tăng lớp', '<p>GDVN- Việc tăng số lượng học sinh lớp 10 tại nhiều trường trung học phổ th&ocirc;ng nhằm đ&aacute;p ứng nhu cầu, nguyện vọng của phụ huynh, học sinh.</p>\r\n\r\n<p>Ng&agrave;y 31/7, Văn ph&ograve;ng Ủy ban nh&acirc;n d&acirc;n tỉnh Quảng B&igrave;nh cho hay, Ph&oacute; Chủ tịch Ủy ban nh&acirc;n d&acirc;n tỉnh Hồ An Phong vừa k&yacute; quyết về việc&nbsp;<em>&ldquo;<a href=\"https://giaoduc.net.vn/gdvn-post215438.gd\" title=\"Sau Vũng Tàu, Quảng Bình yêu cầu giáo viên không giao bài tập trong kỳ nghỉ Tết\">điều chỉnh, bổ sung chỉ ti&ecirc;u kế hoạch ph&aacute;t triển lớp, học sinh&nbsp;</a>cho c&aacute;c trường trung học phổ th&ocirc;ng, trung học cơ sở v&agrave; trung học phổ th&ocirc;ng năm học 2021-2022&rdquo;.</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"https://photo-cms-giaoduc.zadn.vn/w700/Uploaded/2021/zgtzgo/2021_06_15/9-185.jpg\" style=\"height:427px; width:640px\" /></em></p>\r\n\r\n<p>Theo đ&oacute;, Ủy ban nh&acirc;n d&acirc;n tỉnh quyết định tăng số học sinh lớp 10 th&ecirc;m 28 em tại Trường trung học phổ th&ocirc;ng Quang Trung;</p>\r\n\r\n<p><a href=\"https://giaoduc.net.vn/gdvn-post199088.gd\" title=\"Giám đốc sở Giáo dục Quảng Bình nói gì về 6.400 thí sinh phải thi lại môn Văn?\">Tăng th&ecirc;m 10 em tại Trường trung học phổ th&ocirc;ng Trần Ph&uacute;</a>; tăng th&ecirc;m 36 em tại Trường trung học phổ th&ocirc;ng Đ&agrave;o Duy Từ;</p>\r\n\r\n<p>Tăng số học sinh lớp 10 th&ecirc;m 10 em tại Trường trung học phổ th&ocirc;ng Trần Hưng Đạo; tăng th&ecirc;m 30 em tại Trường trung học phổ th&ocirc;ng Phan Đ&igrave;nh Ph&ugrave;ng;</p>\r\n\r\n<p>Tại Trường trung học phổ th&ocirc;ng Đồng Hới tăng số học sinh lớp 10 th&ecirc;m 24 em; tại Trường trung học cơ sở v&agrave; trung học phổ th&ocirc;ng H&oacute;a Tiến giảm số lớp 10 l&agrave; 1 lớp với số học sinh lớp 10 giảm 40 em.</p>\r\n\r\n<p>Về chỉ ti&ecirc;u bổ sung, Ủy ban nh&acirc;n d&acirc;n tỉnh Quảng B&igrave;nh quyết định tăng số lớp 10 th&ecirc;m 2 lớp với 90 học sinh tại trường trung học phổ th&ocirc;ng Phan Đ&igrave;nh Ph&ugrave;ng;</p>\r\n\r\n<p><a href=\"https://giaoduc.net.vn/gdvn-post199123.gd\" title=\"Chùm ảnh: 6.400 học sinh ở Quảng Bình “đội nắng” đi thi lại môn Văn\">Tại Trường trung học phổ th&ocirc;ng Đồng Hới, số lớp 10 tăng 1 lớp với 45 học sinh</a>; tại Trường trung học phổ th&ocirc;ng Ng&ocirc; Quyền c&oacute; số lớp 10 tăng 1 lớp với 42 học sinh.</p>\r\n\r\n<p>Ngo&agrave;i ra, c&aacute;c trường trung học phổ th&ocirc;ng, trung học cơ sở v&agrave; trung học phổ th&ocirc;ng kh&aacute;c tr&ecirc;n địa b&agrave;n tỉnh giữ nguy&ecirc;n chỉ ti&ecirc;u kế hoạch ph&aacute;t triển lớp, học sinh năm học 2021-2022 đ&atilde; được giao.</p>\r\n\r\n<p>Tỉnh cũng giao Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo Quảng B&igrave;nh căn cứ c&aacute;c văn bản hướng dẫn của Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo v&agrave; Ủy ban nh&acirc;n d&acirc;n tỉnh đ&atilde; ban h&agrave;nh về c&ocirc;ng t&aacute;c tuyển sinh v&agrave;o lớp 10 trung học phổ th&ocirc;ng năm học 2021-2022 để hướng dẫn c&aacute;c trường được điều chỉnh triển khai, tổ chức tuyển sinh chỉ ti&ecirc;u điều chỉnh, bổ sung theo đ&uacute;ng quy chế tuyển sinh.</p>\r\n\r\n<p>Việc điều chỉnh, bổ sung số lượng học sinh v&agrave; tăng lớp 10 nhằm đ&aacute;p ứng đ&uacute;ng nguyện vọng của phụ huynh v&agrave; học sinh tr&ecirc;n địa b&agrave;n sau k&igrave; thi tuyển sinh v&agrave;o lớp 10 trung học phổ th&ocirc;ng năm học 2021-2022.</p>\r\n\r\n<p>Trước đ&oacute;, như&nbsp;<a href=\"https://giaoduc.net.vn/gdvn-post219431.gd\" title=\"Tuyển sinh lớp 10 ở Quảng Bình: Điểm cao trượt, điểm thấp lại đậu\">Tạp ch&iacute; Điện tử Gi&aacute;o dục Việt Nam đ&atilde; th&ocirc;ng tin</a>, trong k&igrave; thi tuyển sinh lớp 10, c&oacute; h&agrave;ng trăm học sinh học lực giỏi, kh&aacute; v&agrave; c&oacute; kết quả thi tốt, c&oacute; nguyện vọng ch&iacute;nh đ&aacute;ng v&agrave;o học trường trung học phổ th&ocirc;ng nhưng kh&ocirc;ng đủ điều kiện tr&uacute;ng tuyển.</p>\r\n\r\n<p>Trong khi đ&oacute;, một số lượng học sinh c&oacute; kết quả học tập trung b&igrave;nh, thi đạt kết quả thấp tr&uacute;ng tuyển. Sự bất cập n&agrave;y g&acirc;y ảnh hưởng đến chất lượng gi&aacute;o dục trung học phổ th&ocirc;ng, ảnh hưởng đến việc thực hiện chủ trương ph&acirc;n luồng gi&aacute;o dục.</p>\r\n\r\n<p>Do đ&oacute;, l&atilde;nh đạo tỉnh Quảng B&igrave;nh y&ecirc;u cầu c&aacute;c sở ng&agrave;nh r&agrave; so&aacute;t lại c&aacute;c phương &aacute;n để c&oacute; đề xuất cụ thể, kh&ocirc;ng để trường hợp n&agrave;o học sinh sau tốt nghiệp trung học cơ sở phải nghỉ học v&igrave; kh&ocirc;ng c&oacute; trường lớp.</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-01', 5),
(23, 'KINHTE.jpeg', 'Sau 1 tuần thực hiện giãn cách xã hội theo Chỉ thị 17/CT-UBND của UBND TP Hà Nội, nguồn cung thực phẩm tại hệ thống chợ truyền thống,', 'Sau 1 tuần Hà Nội giãn cách xã hội: Hàng hoá dồi dào, không tăng giá đột biến', '<p>Sau 1 tuần thực hiện gi&atilde;n c&aacute;ch x&atilde; hội theo Chỉ thị 17/CT-UBND của UBND TP H&agrave; Nội, nguồn cung thực phẩm tại hệ thống chợ truyền thống, si&ecirc;u thị lu&ocirc;n dồi d&agrave;o kh&ocirc;ng xảy ra t&igrave;nh trạng khan h&agrave;ng, tăng gi&aacute; đột biến. TP H&agrave; Nội đ&atilde; chủ động dự trữ nguồn h&agrave;ng, tạo điều kiện cho việc lưu th&ocirc;ng h&agrave;ng h&oacute;a thuận lợi, đ&aacute;p ứng nhu cầu thiết yếu của người d&acirc;n.</p>\r\n\r\n<p><strong>Thực phẩm, rau xanh kh&ocirc;ng thiếu</strong></p>\r\n\r\n<p>Khảo s&aacute;t thị trường thực phẩm, rau xanh tại hệ thống chợ truyền thống cho thấy, mặc d&ugrave; chợ đầu mối ph&iacute;a Nam tạm dừng hoạt động do c&oacute; ca nhiễm Covid-19 nhưng lượng h&agrave;ng tại c&aacute;c chợ dồi d&agrave;o, kh&ocirc;ng c&oacute; hiện tượng khan hiếm h&agrave;ng h&oacute;a. Tại chợ Khương Thượng (quận Đống Đa) gi&aacute; thịt lợn kh&ocirc;ng tăng, hiện thịt thăn lợn 140.000 đồng/kg; sườn thăn 150.000 đồng/kg, thịt m&ocirc;ng 120.000 đồng/kg. Thịt b&ograve; thăn 280.000 &ndash; 300.000 đồng/kg; b&ograve; dẻ sườn 200.000 &ndash; 220.000 đồng/kg; b&ograve; bắp 300.000 đồng/kg&hellip; C&aacute;c mặt h&agrave;ng thủy, hải sản tại chợ Nam Đồng (Đống Đa) cũng trong t&igrave;nh trạng tương tự, hiện c&aacute; ch&eacute;p gi&aacute; từ 100.000 &ndash; 120.000 đồng/kg, c&aacute; trắm&nbsp; 65.000 &ndash; 70.000 đồng/kg, c&aacute;c loại hải sản đ&ocirc;ng lạnh như mực ống từ 130.000 &ndash; 150.000 đồng/kg; t&ocirc;m loại 10 con/kg gi&aacute; 350.000 đồng; ghẹ xanh từ 3 &ndash; 5 con/kg gi&aacute; 250.000 đồng/kg. Gi&aacute; c&aacute;c loại thịt gia cầm cũng kh&ocirc;ng tăng so với ng&agrave;y đầu ti&ecirc;n thực hiện gi&atilde;n c&aacute;ch x&atilde; hội, hiện g&agrave; ta vẫn đang giữ ở mức 140.000-150.000 đồng/kg, c&aacute;nh v&agrave; đ&ugrave;i g&agrave; c&ocirc;ng nghiệp 80.000-85.000 đồng/kg, lườn 60.000 đồng/kg. Ri&ecirc;ng c&oacute; mặt h&agrave;ng trứng g&agrave; vẫn đang ở mức cao, nếu như trong ng&agrave;y đầu ti&ecirc;n thực hiện gi&atilde;n c&aacute;ch x&atilde; hội, trứng g&agrave; c&ocirc;ng nghiệp 30.000-32.000 đồng/chục, nay đ&atilde; tăng l&ecirc;n 40.000-45.000 đồng/chục, trứng g&agrave; ta 50.000 &ndash; 55.000 đồng/chục</p>\r\n\r\n<p><img alt=\"\" src=\"https://static.tintuc.com.vn/images/ver3/2021/08/01/1627820190141-image-(28)(1).jpeg\" style=\"height:427px; width:640px\" /></p>\r\n\r\n<p>Trong khi c&aacute;c loại thực phẩm kh&ocirc;ng tăng gi&aacute; th&igrave; mặt h&agrave;ng rau củ quả tăng nhẹ từ 5.000 - 10.000 đồng/kg.&nbsp; C&aacute;c tiểu thương kinh doanh rau tại chợ Kim Li&ecirc;n, Khương Thượng (quận Đống Đa), Nghĩa T&acirc;n (quận Cầu Giấy) th&ocirc;ng tin, v&agrave;i h&ocirc;m trước b&iacute; xanh được b&aacute;n với gi&aacute; 10.000 - 15.000 đồng/kg, nhưng hiện đ&atilde; 25.000 đồng/kg; Rau cải từ 10.000 đồng/kg l&ecirc;n 20.000 đồng/kg, rau muống từ 5.000 - 6.000 đồng/b&oacute;, hiện 15.000 - 20.000 đồng/b&oacute;, c&agrave; chua từ 15.000 đồng/kg nay đ&atilde; tăng l&ecirc;n 25.000 đồng/kg, mướp đắng tăng l&ecirc;n 17.000 đồng/kg, mướp hương l&ecirc;n 20.000 đồng/kg, c&agrave; rốt 25.000 đồng/kg; dưa chuột từ 15.000 - 20.000 đồng/kg... D&ugrave; c&aacute;c chợ truyền thống tăng gi&aacute;, nhưng c&aacute;c si&ecirc;u thị vẫn giữ nguy&ecirc;n gi&aacute; b&aacute;n mặt h&agrave;ng rau, củ quả. Tại si&ecirc;u thị Big C&nbsp; Thăng Long, Vinmart Thăng Long (quận Cầu Giấy), Hapro Th&agrave;nh C&ocirc;ng (quận Ba Đ&igrave;nh) gi&aacute; rau củ vẫn giữ ổn định, như b&iacute; xanh 14.300 đồng/kg, cải thảo 14.000 đồng/kg, bắp cải 15.000 đồng/kg; đậu đũa 25.900 đồng/kg&hellip; v&agrave; được b&aacute;n với số lượng kh&ocirc;ng giới hạn.</p>\r\n\r\n<p>L&yacute; giải nguy&ecirc;n nh&acirc;n gi&aacute; rau xanh tăng nhẹ hầu hết c&aacute;c tiểu thương kinh doanh mặt h&agrave;ng n&agrave;y c&oacute; chung phản &aacute;nh, gi&aacute; rau tăng nhẹ l&agrave; do việc vận chuyển gặp nhiều kh&oacute; khăn, chi ph&iacute; tăng cao. Nguyễn Mạnh, tiểu thương kinh doanh thịt lợn tại chợ Nam Đồng cho biết, những ng&agrave;y vừa qua, người ti&ecirc;u d&ugrave;ng đi chợ theo ng&agrave;y chẵn-lẻ n&ecirc;n lượng ti&ecirc;u thụ mạnh bởi đa phần người đi chợ thường mua từ 1-2kg/lần, tuy nhi&ecirc;n gi&aacute; b&aacute;n kh&ocirc;ng tăng do nguồn cung dồi d&agrave;o. Nếu tăng gi&aacute; sức ti&ecirc;u thụ sẽ giảm bởi người d&acirc;n sẽ hạn chế chi ti&ecirc;u trong thời gian gi&atilde;n c&aacute;ch.</p>\r\n\r\n<p>Thực tế cho thấy nhằm hạn chế tối đa việc tiếp x&uacute;c trực tiế nhiều chợ truyền thống đ&atilde; triển khai việc kẻ &ocirc;, căng d&acirc;y chắn&hellip;song vẫn đảm bảo việc mua h&agrave;ng được nhanh ch&oacute;ng, thuận tiện.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-01', 1),
(24, 'kinhte5.jpg', 'Đồng đô la Mỹ sáng nay 2/8 trên thế giới không có biến động nhiều và được dự báo đi ngang trong tuần. Bảng lương phi nông nghiệp có thể ', 'Tỷ giá USD hôm nay 2/8/2021: Đô la Mỹ dự báo ít khả năng tăng cao', '<p>&nbsp;</p>\r\n\r\n<p><strong>Tỷ gi&aacute; USD thị trường thế giới</strong></p>\r\n\r\n<p>S&aacute;ng 2/8, chỉ số Dollar Index (DXY), theo d&otilde;i đồng bạc xanh so với rổ c&aacute;c tiền tệ kh&aacute;c đang ở mức 92,12.</p>\r\n\r\n<p>Gi&aacute; USD so với c&aacute;c ngoại tệ lớn kh&aacute;c s&aacute;ng nay hiện đứng ở mức:</p>\r\n\r\n<p>1 euro đổi 1,1865 USD.</p>\r\n\r\n<p>1 bảng Anh đổi 1,3897 USD.</p>\r\n\r\n<p>1 USD đổi 109,72 y&ecirc;n.</p>\r\n\r\n<p><strong>Chuy&ecirc;n gia nhận định về gi&aacute; USD</strong></p>\r\n\r\n<p>Theo trang DailyFX, đ&ocirc; la Mỹ đ&atilde; bị ảnh hưởng sau quyết định l&atilde;i suất của Cục Dự trữ Li&ecirc;n bang v&agrave;o tuần trước. Chủ tịch Fed, Jerome Powell, đ&atilde; bảo vệ những cam kết &quot;diều h&acirc;u&quot; bằng c&aacute;ch tuy&ecirc;n bố rằng c&aacute;c kỳ vọng v&agrave; chỉ số lạm ph&aacute;t d&agrave;i hạn c&oacute; vẻ ph&ugrave; hợp với mục ti&ecirc;u d&agrave;i hạn của ng&acirc;n h&agrave;ng trung ương l&agrave; 2%. C&aacute;c thị trường đ&atilde; chịu t&aacute;c động bởi tuy&ecirc;n bố n&agrave;y, với rủi ro nghi&ecirc;ng về cổ phiếu v&agrave; c&aacute;c t&agrave;i sản rủi ro kh&aacute;c, g&acirc;y thiệt hại cho đ&ocirc; la Mỹ.</p>\r\n\r\n<p>Đồng bạc xanh th&ocirc;ng qua chỉ số DXY vẫn thấp hơn trong tuần sau khi số liệu PCE của th&aacute;ng 6 kh&ocirc;ng đạt như mong đợi 3,7%, chỉ ở mức 3,5%. Điều n&agrave;y c&oacute; thể sẽ duy tr&igrave; t&igrave;nh h&igrave;nh hiện tại th&ecirc;m một khoảng thời gian cho đến cuộc họp FOMC kế tiếp v&agrave;o th&aacute;ng 9.</p>\r\n\r\n<p>Điều đ&oacute; c&oacute; khả năng khiến c&aacute;c t&iacute;nh to&aacute;n về kỳ vọng ch&iacute;nh s&aacute;ch phần lớn kh&ocirc;ng thay đổi v&agrave; thay v&agrave;o đ&oacute;, c&aacute;c nh&agrave; giao dịch sẽ mong đợi c&aacute;c sự kiện c&oacute; t&aacute;c động cao kh&aacute;c ở xa hơn. V&agrave; b&aacute;o c&aacute;o bảng lương phi n&ocirc;ng nghiệp cho th&aacute;ng 7 sắp c&ocirc;ng bố sẽ đ&oacute;ng vai tr&ograve; l&agrave; b&aacute;o c&aacute;o kinh tế tiềm năng c&oacute; t&aacute;c động.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot; &gt;</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-02', 1),
(25, 'kinhte6.jpg', 'NHNN yêu cầu các tổ chức tín dụng điều chỉnh giảm phí giao dịch trên ATM, POS xử lý qua NAPAS và phí giao dịch chuyển khoản liên ngân', 'NHNN yêu cầu giảm phí giao dịch trên ATM, chuyển khoản liên ngân hàng', '<p>&nbsp;</p>\r\n\r\n<p>Ng&acirc;n h&agrave;ng Nh&agrave; nước Việt Nam vừa c&oacute; c&ocirc;ng văn y&ecirc;u cầu c&aacute;c tổ chức t&iacute;n dụng, chi nh&aacute;nh ng&acirc;n h&agrave;ng nước ngo&agrave;i giảm ph&iacute; giao dịch tr&ecirc;n ATM, POS xử l&yacute; qua C&ocirc;ng ty cổ phần Thanh to&aacute;n quốc gia Việt Nam (NAPAS) v&agrave; ph&iacute; giao dịch chuyển khoản li&ecirc;n ng&acirc;n h&agrave;ng 24/7 từ ng&agrave;y 1/8.</p>\r\n\r\n<p>Cụ thể, c&ocirc;ng văn n&ecirc;u r&otilde; trong bối cảnh dịch COVID-19 c&ograve;n diễn biến phức tạp v&agrave; nhằm triển khai Nghị quyết số 63/NQ-CP của Ch&iacute;nh phủ, Ng&acirc;n h&agrave;ng Nh&agrave; nước chỉ đạo NAPAS tiếp tục triển khai ch&iacute;nh s&aacute;ch giảm ph&iacute; dịch vụ chuyển mạch t&agrave;i ch&iacute;nh v&agrave; b&ugrave; trừ điện tử năm 2021.</p>\r\n\r\n<p><img alt=\"\" src=\"https://static.tintuc.com.vn/images/ver3/2021/08/02/1627865928523-ty-gia-usd-hom-nay-282021-do-la-my-du-bao-it-kha-nang-tang-cao.jpg\" style=\"height:427px; width:640px\" /></p>\r\n\r\n<p>Theo đ&oacute;, NAPAS giảm 50% ph&iacute; dịch vụ chuyển mạch t&agrave;i ch&iacute;nh v&agrave; b&ugrave; trừ điện tử cho c&aacute;c giao dịch tr&ecirc;n ATM, POS so với mức ph&iacute; đang &aacute;p dụng; giảm tối thiểu 75% ph&iacute; dịch vụ chuyển mạch t&agrave;i ch&iacute;nh v&agrave; b&ugrave; trừ điện tử cho c&aacute;c giao dịch chuyển khoản li&ecirc;n ng&acirc;n h&agrave;ng 24/7 so với mức ph&iacute; đang &aacute;p dụng. Thời gian &aacute;p dụng từ ng&agrave;y 1/8 đến ng&agrave;y 31/12.</p>\r\n\r\n<p>Ng&acirc;n h&agrave;ng Nh&agrave; nước y&ecirc;u cầu c&aacute;c tổ chức t&iacute;n dụng, chi nh&aacute;nh ng&acirc;n h&agrave;ng nước ngo&agrave;i thực hiện điều chỉnh giảm ph&iacute; giao dịch tr&ecirc;n ATM, POS xử l&yacute; qua NAPAS v&agrave; ph&iacute; giao dịch chuyển khoản li&ecirc;n ng&acirc;n h&agrave;ng 24/7 cho kh&aacute;ch h&agrave;ng với mức giảm tối thiểu bằng mức giảm ph&iacute; của NAPAS điều chỉnh giảm.</p>\r\n\r\n<p>Ng&acirc;n h&agrave;ng Nh&agrave; nước cũng khuyến kh&iacute;ch c&aacute;c tổ chức t&iacute;n dụng, chi nh&aacute;nh ng&acirc;n h&agrave;ng nước ngo&agrave;i thực hiện giảm ph&iacute; lớn hơn mức giảm của NAPAS, trong đ&oacute; đối với c&aacute;c trường hợp đang &aacute;p dụng ch&iacute;nh s&aacute;ch miễn ph&iacute; đề nghị tiếp tục thực hiện. Thời gian thực hiện tối thiểu từ ng&agrave;y 1/8 đến ng&agrave;y 31/12.</p>\r\n\r\n<p>Ng&acirc;n h&agrave;ng Nh&agrave; nước y&ecirc;u cầu Chủ tịch Hội đồng quản trị, Hội đồng th&agrave;nh vi&ecirc;n, Tổng Gi&aacute;m đốc c&aacute;c tổ chức t&iacute;n dụng, chi nh&aacute;nh ng&acirc;n h&agrave;ng nước ngo&agrave;i chịu tr&aacute;ch nhiệm về việc x&acirc;y dựng, triển khai ch&iacute;nh s&aacute;ch giảm ph&iacute; cho kh&aacute;ch h&agrave;ng tại đơn vị m&igrave;nh bao gồm loại ph&iacute; giảm, mức giảm ph&iacute;, kh&aacute;ch h&agrave;ng giảm ph&iacute;&hellip; đảm bảo c&aacute;c loại ph&iacute;, mức giảm ph&iacute; v&agrave; thời gian &aacute;p dụng đ&aacute;p ứng y&ecirc;u cầu như đ&atilde; n&ecirc;u tr&ecirc;n.</p>\r\n\r\n<p>C&aacute;c tổ chức t&iacute;n dụng, chi nh&aacute;nh ng&acirc;n h&agrave;ng nước ngo&agrave;i c&oacute; b&aacute;o c&aacute;o Thống đốc Ng&acirc;n h&agrave;ng Nh&agrave; nước về c&aacute;c loại ph&iacute; giảm, mức ph&iacute; giảm v&agrave; thời gian &aacute;p dụng giảm ph&iacute; trước ng&agrave;y 15/8 (th&ocirc;ng qua Vụ Thanh to&aacute;n - Ng&acirc;n h&agrave;ng Nh&agrave; nước).</p>\r\n\r\n<p>&quot; &gt;</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-02', 1),
(26, 'kinhte7.jpg', 'Giá cà phê hôm nay 2/8 trong khoảng 35.900 - 36.900 đồng/kg. Kết thúc tuần vừa rồi, tuy giá cà phê 2 sàn thế giới giảm,', 'Giá cà phê hôm nay 2/8: Cà phê tháng 7 tăng mạnh, đâu là yếu tố quyết định giá tháng 8/2021?', '<p>&nbsp;</p>\r\n\r\n<p>Tại huyện Di Linh, L&acirc;m H&agrave;, Bảo Lộc (L&acirc;m Đồng) gi&aacute; c&agrave; ph&ecirc; h&ocirc;m nay được thu mua với mức 35.900 đồng/kg.</p>\r\n\r\n<p>Tại huyện Cư M&#39;gar (Đắk Lắk) gi&aacute; c&agrave; ph&ecirc; h&ocirc;m nay ở mức 36.800 đồng/kg. Tại huyện Ea H&#39;leo (Đắk Lắk), Bu&ocirc;n Hồ (Đắk Lắk) gi&aacute; c&agrave; ph&ecirc; h&ocirc;m nay được thu mua c&ugrave;ng mức 36.700 đồng/kg.</p>\r\n\r\n<p><img alt=\"\" src=\"https://static.tintuc.com.vn/images/ver3/2021/08/02/1627870743769-1627870205511-gia-ca-phe-hom-nay-2.8.jpg\" style=\"height:376px; width:640px\" /></p>\r\n\r\n<p>Tương tự tại tỉnh Đắk N&ocirc;ng, gi&aacute; c&agrave; ph&ecirc; h&ocirc;m nay thu mua ở mức 36.700 đồng/kg tại Gia Nghĩa v&agrave; 36.600 đồng/kg ở Đắk R&#39;lấp.</p>\r\n\r\n<p>Tại tỉnh Gia Lai, gi&aacute; c&agrave; ph&ecirc; h&ocirc;m nay ở mức 36.700 đồng/kg (Chư Pr&ocirc;ng), ở Pleiku v&agrave; La Grai c&ugrave;ng gi&aacute; 36.600 đồng/kg. C&ograve;n gi&aacute; c&agrave; ph&ecirc; h&ocirc;m nay tại tỉnh Kon Tum được thu mua với mức 36.700 đồng/kg. S&aacute;ng nay, gi&aacute; c&agrave; ph&ecirc; tại c&aacute;c v&ugrave;ng trồng trọng điểm đi ngang so với c&ugrave;ng thời điểm s&aacute;ng h&ocirc;m qua.</p>\r\n\r\n<p>Tổng kết tuần trước, gi&aacute; c&agrave; ph&ecirc; giảm trung b&igrave;nh 800 - 1.000 đồng/kg tại c&aacute;c v&ugrave;ng trồng trọng điểm. Dịch Covid-19 b&ugrave;ng ph&aacute;t khiến giao dịch trầm lắng do gi&atilde;n c&aacute;ch x&atilde; hội.</p>\r\n\r\n<p>Kết th&uacute;c phi&ecirc;n giao dịch gần nhất, gi&aacute; c&agrave; ph&ecirc; Robusta tại London giao th&aacute;ng 9/2021 giảm 99 USD/tấn ở mức 1.786 USD/tấn, giao th&aacute;ng 11/2021 giảm 99 USD/tấn ở mức 1.801 USD/tấn. Trong khi đ&oacute; tr&ecirc;n s&agrave;n New York, gi&aacute; c&agrave; ph&ecirc; Arabica giao th&aacute;ng 9/2021 giảm 16,95 cent/lb ở mức 179,55 cent/lb, giao th&aacute;ng 12/2021 giảm 16,85 cent/lb ở mức 182,45 cent/lb. Kết th&uacute;c tuần vừa rồi, tuy gi&aacute; c&agrave; ph&ecirc; 2 s&agrave;n thế giới giảm, nhưng tựu trung cả th&aacute;ng 7 tăng mạnh. Như gi&aacute; Arabica vẫn tăng 12% trong th&aacute;ng 7/2021, v&agrave; tăng 55% so với một năm trước đ&acirc;y. London tăng 5,20% tức tăng 89 đ&ocirc; la/tấn. Nguy&ecirc;n nh&acirc;n do thời tiết bất lợi l&agrave;m giảm sản lượng trong năm 2021, v&agrave; dự kiến sẽ tiếp tục ảnh hưởng tới sản lượng trong năm tới.</p>\r\n\r\n<p>Nhận định về thị trường, chuy&ecirc;n gia Nguyễn Quang B&igrave;nh cho biết, những th&ocirc;ng tin về sương gi&aacute; tại Brazil đang ngu&ocirc;i dần, nhưng d&ograve;ng vốn tr&ecirc;n c&aacute;c s&agrave;n giao dịch t&agrave;i ch&iacute;nh vẫn &#39;&#39;cuồn cuộn&#39;&#39; chảy. &#39;&#39;C&oacute; lẽ n&agrave;o c&aacute;c nh&agrave; đầu tư t&agrave;i ch&iacute;nh thấy m&igrave;nh đ&atilde; thắng đậm tr&ecirc;n nhiều s&agrave;n n&ocirc;ng sản v&agrave; đặc biệt tr&ecirc;n 2 s&agrave;n c&agrave; ph&ecirc;, ng&agrave;y cuối th&aacute;ng họ muốn k&eacute;o về mức thấp để sau n&agrave;y b&agrave;y trận mới? Hay l&agrave; khi đưa gi&aacute; l&ecirc;n v&agrave; l&ecirc;n qu&aacute; n&oacute;ng, họ phải vin v&agrave;o tin sương gi&aacute; c&aacute;c v&ugrave;ng c&agrave; ph&ecirc; Brazil, nay biến chủng Delta l&agrave; c&aacute;i cớ để gi&uacute;p họ chỉnh gi&aacute; xuống để điều h&ograve;a?&#39;&#39;, vị chuy&ecirc;n gia đặt c&acirc;u hỏi tại b&agrave;i viết tr&ecirc;n thesaigontimes.vn.</p>\r\n\r\n<p>Đ&aacute;nh gi&aacute; hai s&agrave;n c&agrave; ph&ecirc; phi&ecirc;n đ&oacute;ng cửa phi&ecirc;n cuối tuần trước xuất hiện hướng đảo ngược mang t&iacute;nh quyết định (key reversal). Nếu n&oacute;i về kỹ thuật, gi&aacute; c&ograve;n chỉnh giảm v&agrave;i ba đợt nữa để t&igrave;m thế ổn định. &Ocirc;ng Nguyễn Quang B&igrave;nh nhận định như vậy c&oacute; thể đo&aacute;n rằng gi&aacute; c&agrave; ph&ecirc; v&agrave;o th&aacute;ng 8/2021 c&oacute; thể trầm lắng v&agrave; &iacute;t s&oacute;ng gi&oacute; hơn&hellip; Cho đến khi c&oacute; một dự b&aacute;o rằng Brazil lại trở r&eacute;t đến mức c&oacute; khả năng xảy ra sương gi&aacute; tr&ecirc;n v&ugrave;ng c&agrave; ph&ecirc; tại đ&oacute;.</p>\r\n\r\n<p>Nhưng yếu tố quyết định gi&aacute; c&agrave; ph&ecirc; đường d&agrave;i l&ecirc;n hay xuống chắc sẽ kh&ocirc;ng v&igrave; tin thời tiết vốn rất bất nhất m&agrave; sẽ dựa v&agrave;o một yếu tố kh&aacute;c. Đ&oacute; ch&iacute;nh l&agrave; lượng tồn kho c&agrave; ph&ecirc; tại c&aacute;c nước ti&ecirc;u thụ.</p>\r\n\r\n<p>Đến hết th&aacute;ng 6/2021, tồn kho c&agrave; ph&ecirc; khả dụng tại ch&acirc;u &Acirc;u đạt 866.538 tấn, giảm so với th&aacute;ng 1/2021 l&agrave; 890.563 tấn. Nếu như lượng c&agrave; ph&ecirc; Arabica kh&aacute; ổn định th&igrave; lượng Robusta giảm nhiều. Tồn kho Robusta ECS tại EU đầu năm l&agrave; 346 ngh&igrave;n tấn th&igrave; đến th&aacute;ng 6/21 chỉ c&ograve;n 315 ngh&igrave;n tấn.</p>\r\n\r\n<p>C&ograve;n tại Nhật Bản, t&iacute;nh đến hết th&aacute;ng 5/2021, tồn kho c&agrave; ph&ecirc; đạt 2,86 triệu bao (bao 60 kg), tăng so với 2,81 triệu bao v&agrave;o th&aacute;ng 4/2021 nhưng giảm 39 ngh&igrave;n bao so với c&ugrave;ng kỳ 2020.</p>\r\n\r\n<p>Trong khi đ&oacute;, Indonesia đ&atilde; ho&agrave;n th&agrave;nh thu hoạch c&agrave; ph&ecirc; năm nay. Ước đạt 11,35 triệu bao trong đ&oacute; 9,70 triệu bao Robusta v&agrave; phần c&ograve;n lại l&agrave; Arabica, tăng sản lượng so với vụ năm ngo&aacute;i.</p>\r\n\r\n<p>&quot; &gt;</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-02', 1);
INSERT INTO `tintuc` (`matin`, `anh`, `motatin`, `tieude`, `noidung`, `tacgia`, `ngayviet`, `maloai`) VALUES
(27, 'xahoi5.jpg', 'Hiện vẫn chưa xác định được nguồn lây nhiễm tại TP Phan Thiết và có nguy cơ lây lan cao.', 'Bình Thuận: Phong tỏa một con hẻm ở Phan Thiết có nhiều ca nghi nhiễm COVID-19', '<p>Ng&agrave;y 2/8, &ocirc;ng Ng&ocirc; Ngọc Dũng, Chủ tịch UBND phường H&agrave;m Tiến, TP Phan Thiết (B&igrave;nh Thuận) đ&atilde; k&yacute; quyết định phong tỏa tạm thời hẻm 69, khu phố 1, phường H&agrave;m Tiến từ 0g ng&agrave;y 2/8 cho đến khi c&oacute; th&ocirc;ng b&aacute;o mới.</p>\r\n\r\n<p>Hẻm n&agrave;y c&oacute; 62 hộ d&acirc;n với 326 người. Trong thời gian &aacute;p dụng biện ph&aacute;p phong tỏa, c&aacute;ch ly y tế, người d&acirc;n trong khu phong tỏa kh&ocirc;ng được đi ra khỏi khu vực, trừ nh&acirc;n vi&ecirc;n y tế, trường hợp thực hiện c&ocirc;ng vụ, c&aacute;c trường hợp khẩn cấp như cấp cứu, kh&aacute;m chữa bệnh v&agrave; c&aacute;c trường hợp đặc biệt kh&aacute;c.</p>\r\n\r\n<p><img alt=\"\" src=\"https://static.tintuc.com.vn/images/ver3/2021/08/02/1627893489372-1627892656173-69-mqas.jpg\" style=\"height:379px; width:640px\" /></p>\r\n\r\n<p>Theo UBND TP Phan Thiết, khu vực n&agrave;y nghi c&oacute; ổ dịch với nhiều ca nghi nhiễm n&ecirc;n quyết định phong tỏa để ng&agrave;nh y tế khẩn trương điều tra dịch tễ, truy vết, x&aacute;c định nguồn l&acirc;y.</p>\r\n\r\n<p>T&iacute;nh đến thời điểm n&agrave;y, TP Phan Thiết đ&atilde; c&oacute; 12 ca mắc COVID-19 v&agrave; 13 ca nghi nhiễm mới.</p>\r\n\r\n<p>T&igrave;nh h&igrave;nh dịch bệnh tr&ecirc;n địa b&agrave;n th&agrave;nh phố đang diễn biến hết sức phức tạp, từ 0g ng&agrave;y 2/8 to&agrave;n TP Phan Thiết đ&atilde; thực hiện gi&atilde;n c&aacute;ch x&atilde; hội theo Chỉ thị 16.</p>\r\n\r\n<p>T&iacute;nh đến trưa 2/8, to&agrave;n tỉnh B&igrave;nh Thuận đ&atilde; ghi nhận 601 ca mắc COVID-19. Cụ thể: La Gi 503, T&aacute;nh Linh 27, Tuy Phong 13, Đức Linh 9, Phan Thiết 12, Bắc B&igrave;nh 5, H&agrave;m Thuận Bắc 6, H&agrave;m Thuận Nam 9 v&agrave; H&agrave;m T&acirc;n 17 ca.</p>\r\n\r\n<p>Trong đ&oacute; c&oacute; 566 ca đang được điều trị tại cơ sở y tế, 35 ca đ&atilde; điều trị khỏi v&agrave; xuất viện. Hiện tỉnh B&igrave;nh Thuận đang thực hiện gi&atilde;n c&aacute;ch theo Chỉ thị 15 tr&ecirc;n địa b&agrave;n 8 huyện; thực hiện Chỉ thị 16 tr&ecirc;n địa b&agrave;n thị x&atilde; La Gi v&agrave; th&agrave;nh phố Phan Thiết.</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-02', 2),
(28, 'xahoi6.jpg', 'Sau khi hỗ trợ thuê xe cho gia đình anh Xò về quê an toàn, anh Trần Hoàng Vương, thành viên Câu lạc bộ xe bán tải Đà Nẵng đã tiếp tục hỗ trợ gia đình 20 triệu', 'Gia đình bé 10 ngày tuổi về quê tránh dịch được tặng 20 triệu mua xe máy', '<p>Sau khi hỗ trợ thu&ecirc; xe cho gia đ&igrave;nh anh X&ograve; về qu&ecirc; an to&agrave;n, anh Trần Ho&agrave;ng Vương, th&agrave;nh vi&ecirc;n C&acirc;u lạc bộ xe b&aacute;n tải Đ&agrave; Nẵng đ&atilde; tiếp tục&nbsp;hỗ trợ&nbsp;gia đ&igrave;nh 20 triệu mua xe m&aacute;y.</p>\r\n\r\n<p>Vợ chồng anh Xồng B&aacute; X&ograve; qu&ecirc; ở x&atilde; miền n&uacute;i Tam Hợp (huyện Tương Dương, tỉnh Nghệ An) v&agrave;o B&igrave;nh Dương l&agrave;m c&ocirc;ng nh&acirc;n.</p>\r\n\r\n<p>Dịch bệnh khiến vợ chồng anh thất nghiệp. Kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o kh&aacute;c, hai vợ chồng đ&agrave;nh chạy xe m&aacute;y chở theo con trai mới ch&agrave;o đời 10 ng&agrave;y vượt hơn 1.000km về qu&ecirc;.</p>\r\n\r\n<p>Khuya 30/7, anh chở vợ con tr&ecirc;n xe của anh X&ograve; đ&atilde; cũ về đến Đ&agrave; Nẵng.</p>\r\n\r\n<p>&quot;Tại&nbsp;khu vực trạm trung chuyển ở hầm Hải V&acirc;n đ&oacute;n, t&ocirc;i gặp được vợ chồng anh X&ograve; v&agrave; thấy ch&aacute;u nhỏ rất tội, v&igrave; mới sinh được 10 ng&agrave;y đ&atilde; di chuyển tr&ecirc;n qu&atilde;ng đường d&agrave;i...</p>\r\n\r\n<p>Khi thấy xe của anh X&ograve; đ&atilde; cũ, m&aacute;y hỏng, x&iacute;ch đứt, đầu t&ocirc;i nghĩ v&agrave; muốn hỗ trợ cho gia đ&igrave;nh anh X&ograve; một phương tiện di chuyển tốt hơn&rdquo;, anh Trần Ho&agrave;ng Vương n&oacute;i.</p>\r\n\r\n<p>Anh Vương chia sẻ, trước ho&agrave;n cảnh kh&oacute; khăn như vậy, ch&uacute;ng t&ocirc;i nghĩ gi&uacute;p được g&igrave; sẽ cố gắng. Sau khi nh&oacute;m hỗ trợ gia đ&igrave;nh phương tiện về Nghệ An an to&agrave;n, chiều 1/8 t&ocirc;i đ&atilde; chuyển 20 triệu đến gia đ&igrave;nh anh X&ograve; để mua xe m&aacute;y.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2021/08/02/08/gia-dinh-be-10-ngay-tuoi-ve-que-tranh-dich-duoc-tang-20-trieu-mua-xe-may-1.jpg\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<p>Anh Vương chia sẻ,&nbsp;những ng&agrave;y qua c&oacute; rất nhiều gia đ&igrave;nh chở theo con nhỏ vượt h&agrave;ng ngh&igrave;n km trở về qu&ecirc;. Anh mong được g&oacute;p sức c&ugrave;ng&nbsp;bạn b&egrave; để c&oacute; thể gi&uacute;p phần nhỏ cho một số gia đ&igrave;nh gặp kh&oacute; khăn trong l&uacute;c n&agrave;y được b&igrave;nh an.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-02', 2),
(29, 'xahoi7.jpg', 'Nhiều người ra khỏi thủ đô sáng 2/8 đã bị ngành chức năng tại các điểm chốt yêu cầu quay lại. Những người khác muốn về quê đón con?', 'Hà Nội siết chặt cửa ngõ sau công điện của Thủ tướng, có được về quê đón con?', '<p>Nhiều người ra khỏi thủ đ&ocirc; s&aacute;ng&nbsp;2/8 đ&atilde; bị ng&agrave;nh chức năng tại c&aacute;c điểm chốt y&ecirc;u cầu quay lại. Những người kh&aacute;c muốn về qu&ecirc; đ&oacute;n con? Vậy trường hợp n&agrave;o người d&acirc;n được ra v&agrave;o H&agrave; Nội v&agrave; cần c&aacute;c giấy tờ g&igrave;?&nbsp;</p>\r\n\r\n<p><a href=\"https://vietnamnet.vn/vn/thoi-su/an-toan-giao-thong/nhieu-tai-xe-quay-dau-tai-cua-ngo-sau-cong-dien-siet-chat-dan-roi-thu-do-761979.html\" title=\"Nhiều tài xế quay đầu tại cửa ngõ sau công điện siết chặt dân rời Thủ đô\">Nhiều t&agrave;i xế quay đầu tại cửa ng&otilde; sau c&ocirc;ng điện siết chặt d&acirc;n rời Thủ đ&ocirc;</a></p>\r\n\r\n<p><a href=\"https://vietnamnet.vn/vn/thoi-su/ha-noi-yeu-cau-tuyet-doi-khong-de-nguoi-dan-ra-khoi-tp-trong-thoi-gian-gian-cach-761807.html\" title=\"Hà Nội yêu cầu tuyệt đối không để người dân ra khỏi TP trong thời gian giãn cách\">H&agrave; Nội y&ecirc;u cầu tuyệt đối kh&ocirc;ng để người d&acirc;n ra khỏi TP trong thời gian gi&atilde;n c&aacute;ch</a></p>\r\n\r\n<p><a href=\"https://vietnamnet.vn/vn/thoi-su/nguoi-nha-gui-thuc-pham-tu-que-toi-co-duoc-di-nhan-761297.html\" title=\"Người nhà gửi thực phẩm từ quê ra, tôi có được đi nhận?\">Người nh&agrave; gửi thực phẩm từ qu&ecirc; ra, t&ocirc;i c&oacute; được đi nhận?</a></p>\r\n\r\n<p><em>Bạn Hung Pham&nbsp;hỏi:&nbsp;T&ocirc;i ở H&agrave; Nội c&oacute; con về H&agrave; Nam chơi với &ocirc;ng b&agrave; trước khi H&agrave; Nội &aacute;p dụng Chỉ thị 17. Nay t&ocirc;i muốn về H&agrave; Nam đ&oacute;n ch&aacute;u về H&agrave; Nội để ch&aacute;u đi học ch&iacute;nh thức chương tr&igrave;nh lớp 5 ( học online). Xin hỏi qu&yacute; b&aacute;o t&ocirc;i phải cần những thủ tục n&agrave;o để c&oacute; thể về H&agrave; Nam đ&oacute;n ch&aacute;u về H&agrave; Nội.</em></p>\r\n\r\n<p><em>Bạn Ban Pham hỏi: Em ở huyện Mỹ Đức, H&agrave; Nội trong thời gian thực hiện gi&atilde;n c&aacute;ch x&atilde; hội em lại ở trọ tại Long Bi&ecirc;n, H&agrave; Nội. V&igrave; em c&oacute; con nhỏ 3 tuổi v&agrave; 1 tuổi em muốn cho c&aacute;c ch&aacute;u về qu&ecirc;, vậy em c&oacute; thể về kh&ocirc;ng?&nbsp;</em></p>\r\n\r\n<p>Về việc v&agrave;o th&agrave;nh phố H&agrave; Nội,&nbsp;<em>bạn Đặng Đức hỏi: Hiện tại t&ocirc;i đang ở TP Vinh (Nghệ An), c&oacute; đăng k&yacute; ti&ecirc;m vắc xin tại Tổng c&ocirc;ng ty c&oacute; trụ sở tại TP H&agrave; Nội. Hiện tại, H&agrave; Nội đang c&oacute; Chỉ thị 16, người ngoại tỉnh kh&ocirc;ng được ph&eacute;p di chuyển v&agrave;o th&agrave;nh phố.&nbsp;Vậy với l&yacute; do tr&ecirc;n t&ocirc;i c&oacute; được v&agrave;o th&agrave;nh phố kh&ocirc;ng v&agrave; phải mang theo những giấy tờ g&igrave;?</em></p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i đ&atilde; li&ecirc;n hệ với một số ng&agrave;nh chức năng li&ecirc;n quan th&igrave; nhận được c&acirc;u hỏi chung&nbsp;về&nbsp;việc ra v&agrave;o H&agrave; Nội như sau:</p>\r\n\r\n<p>Thực hiện C&ocirc;ng điện của Thủ tướng Ch&iacute;nh phủ, ng&agrave;y 1/8, Chủ tịch UBND TP H&agrave; Nội đ&atilde; c&oacute; C&ocirc;ng điện hỏa tốc số 17 về việc&nbsp;thực hiện c&aacute;c biện ph&aacute;p cấp b&aacute;ch ph&ograve;ng chống dịch Covid-19.</p>\r\n\r\n<p>Theo đ&oacute;, y&ecirc;u cầu tuyệt đối kh&ocirc;ng để người d&acirc;n ra khỏi th&agrave;nh phố, trừ những người được ch&iacute;nh quyền cho ph&eacute;p. Đồng thời nhấn mạnh việc người d&acirc;n ở tại nh&agrave;, chỉ ra ngo&agrave;i trong trường hợp thật sự cần thiết như: mua lương thực, thực phẩm, thuốc men, cấp cứu, kh&aacute;m chữa bệnh, ti&ecirc;m chủng v&agrave; c&aacute;c trường hợp khẩn cấp kh&aacute;c;&nbsp;đi c&ocirc;ng t&aacute;c c&ocirc;ng vụ, l&agrave;m việc tại cơ quan, c&ocirc;ng sở, l&agrave;m việc tại nh&agrave; m&aacute;y, cơ sở sản xuất, cơ sở kinh doanh dịch vụ, h&agrave;ng h&oacute;a thiết yếu được ph&eacute;p hoạt động.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2021/07/29/19/chi-tiet-nhung-giay-to-can-co-khi-di-chuyen-ra-vao-ha-noi.jpg\" style=\"height:822px; width:640px\" /></p>\r\n\r\n<p>Đối với trường hợp ra khỏi th&agrave;nh phố,&nbsp;c&aacute;c phương tiện được cấp m&atilde; QR luồng xanh được di chuyển m&agrave; kh&ocirc;ng cần kiểm tra. Trường hợp kh&aacute;c, t&agrave;i xế phải dừng lại khai b&aacute;o y tế,&nbsp;xuất tr&igrave;nh giấy tờ t&ugrave;y th&acirc;n, giấy x&eacute;t nghiệm Covid-19 bằng phương ph&aacute;p PCR trong v&ograve;ng 72 tiếng v&agrave; giấy đi đường theo mẫu m&agrave; th&agrave;nh phố ban h&agrave;nh.</p>\r\n\r\n<p>Về thắc mắc &quot;về qu&ecirc; đ&oacute;n con v&agrave; đưa con về qu&ecirc;&quot;, người d&acirc;n kh&ocirc;ng đủ điều kiện hợp l&yacute; theo quy định để c&oacute; thể qua c&aacute;c chốt, n&ecirc;n&nbsp;tạm thời ở y&ecirc;n tại nh&agrave;, c&ugrave;ng hợp t&aacute;c với ng&agrave;nh chức năng để đảm bảo việc ph&ograve;ng chống dịch Covid-19 đạt hiệu quả cao nhất.</p>\r\n\r\n<p>Đối với trường hợp bạn Đặng Đức hỏi v&agrave;o th&agrave;nh phố H&agrave; Nội để ti&ecirc;m vắc xin, đ&oacute; l&agrave; một trong những l&yacute; do được ph&eacute;p theo quy định. Khi v&agrave;o th&agrave;nh phố bạn cần c&oacute; đủ c&aacute;c giấy tờ như&nbsp;tr&ecirc;n v&agrave; giấy x&aacute;c nhận đi ti&ecirc;m vắc xin. Tuy nhi&ecirc;n, hiện nay việc ti&ecirc;m vắc xin đang triển khai ở nhiều tỉnh, th&agrave;nh tr&ecirc;n cả nước, với qu&atilde;ng đường đi 300km, bạn c&oacute; thể c&acirc;n nhắc di chuyển để ph&ograve;ng chống dịch được tốt nhất.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Nguyễn Như Quyền', '2021-08-02', 2),
(30, 'yte5.jpg', 'Chiều 1/8, GS.TS Trần Văn Thuấn - Thứ trưởng Bộ Y tế đánh giá nguy cơ dịch của Tây Ninh là rất cao, nếu địa phương không khẩn trương thì hậu quả sẽ rất khó lường.', 'Nguy cơ bùng phát dịch COVID-19 ở Tây Ninh là rất lớn', '<p><strong>Chiều 1/8, GS.TS Trần Văn Thuấn - Thứ trưởng Bộ Y tế đ&aacute;nh gi&aacute; nguy cơ dịch của T&acirc;y Ninh l&agrave; rất cao, nếu địa phương kh&ocirc;ng khẩn trương th&igrave; hậu quả sẽ rất kh&oacute; lường.</strong></p>\r\n\r\n<h2><strong><em>Qu&aacute; tải về điều trị tại T&acirc;y Ninh</em></strong></h2>\r\n\r\n<h2>T&iacute;nh từ đầu đợt dịch đến ng&agrave;y 1/8, tr&ecirc;n địa b&agrave;n tỉnh T&acirc;y Ninh ghi nhận 1.962&nbsp; trường hợp nhiễm SARS-COV-2.</h2>\r\n\r\n<h2>Hiện nay tổng số giường điều trị của tỉnh T&acirc;y Ninh hiện c&oacute; l&agrave; 4.760 giường v&agrave; c&oacute; thể mở rộng quy m&ocirc; theo t&igrave;nh h&igrave;nh thực tế tại địa phương.</h2>\r\n\r\n<h2>T&acirc;y Ninh y&ecirc;u cầu mỗi huyện c&oacute; &iacute;t nhất 100 giường c&aacute;ch ly trường hợp test nhanh dương t&iacute;nh hoặc x&eacute;t nghiệm RT-PCR s&agrave;ng lọc dương t&iacute;nh v&agrave; 200 giường điều trị người nhiễm COVID-19 kh&ocirc;ng triệu chứng, kh&ocirc;ng c&oacute; bệnh l&yacute; nền.</h2>\r\n\r\n<p><img alt=\"\" src=\"https://moh.gov.vn/documents/20182/988701/02.8.2021.+TT+Thuan+tai+Tay+ninh+1.jpg/5b41679d-a193-43a1-8d3b-635a5e6f8884?t=1627868485028\" style=\"height:480px; width:640px\" /></p>\r\n\r\n<h2>GS.TS Trần Văn Thuấn, Thứ trưởng Bộ Y tế đề nghị hệ thống ch&iacute;nh trị của T&acirc;y Ninh phải v&agrave;o cuộc chống dịch mạnh mẽ, quyết liệt hơn</h2>\r\n\r\n<h2>Tỉnh n&agrave;y đang &aacute;p dụng m&ocirc; h&igrave;nh th&aacute;p điều trị 5 tầng. Trong đ&oacute;:</h2>\r\n\r\n<h2>Tầng 1: Tiếp nhận những trường hợp test nhanh dương t&iacute;nh hoặc x&eacute;t nghiệm RT-PCR s&agrave;ng lọc dương t&iacute;nh. Số giường hiện c&oacute;: 2.700 giường.</h2>\r\n\r\n<h2>Tầng 2: Tiếp nhận những trường hợp F0 mức độ nhẹ, c&oacute; 1.350 giường.</h2>\r\n\r\n<h2>Tầng 3: Tiếp nhận những trường hợp F0 triệu chứng mức độ trung b&igrave;nh, c&oacute; bệnh l&yacute; nền, trẻ em &lt;5 tuổi, người &gt;70 tuổi. C&ocirc;ng suất dự kiến: 370 giường theo quy chuẩn BYT/500 giường kế hoạch.</h2>\r\n\r\n<h2>Tầng 4: Tiếp nhận những trường hợp F0 triệu chứng mức độ nặng, c&oacute; bệnh l&yacute; đi k&egrave;m, cần can thiệp điều trị chuy&ecirc;n khoa. C&oacute; 210 giường (hiện đang hoạt động 110 giường; đang n&acirc;ng cấp 45 giường, x&acirc;y dựng mới 50 giường).</h2>\r\n\r\n<h2>Tầng 5: Triệu chứng mức độ nguy kịch.</h2>\r\n\r\n<h2>Ngo&agrave;i ra, tỉnh đ&atilde; chỉ đạo Sở X&acirc;y dựng, Sở Y tế v&agrave; c&aacute;c đơn vị li&ecirc;n quan sẵn s&agrave;ng c&aacute;c phương &aacute;n x&acirc;y dựng bệnh viện d&atilde; chiến quy m&ocirc; 1.000 giường, mở rộng tiếp nhận, điều trị bệnh nh&acirc;n COVID-19.</h2>\r\n\r\n<h2>Phương &aacute;n th&agrave;nh lập bệnh viện d&atilde; chiến tại c&aacute;c khu c&ocirc;ng nghiệp, khu chế xuất trong c&aacute;c t&igrave;nh huống khẩn cấp, để kịp thời ứng ph&oacute; khi dịch bệnh b&ugrave;ng ph&aacute;t lan rộng.</h2>\r\n\r\n<h2>Tỉnh T&acirc;y Ninh đ&atilde; triển khai 5 hệ thống x&eacute;t nghiệm SARS&ndash;CoV&ndash;2 (2 cơ sở c&ocirc;ng lập v&agrave; 3 cơ sở tư nh&acirc;n), c&ocirc;ng suất: mẫu đơn l&agrave; 1.478 v&agrave; mẫu gộp l&agrave; 7.390 mẫu.</h2>\r\n\r\n<h2>Hiện nay số ca nhiễm tr&ecirc;n địa b&agrave;n T&acirc;y Ninh cao (hiện đang điều trị 1.556 ca) , việc qu&aacute; tải tại c&aacute;c cơ sở điều trị v&agrave; khả năng tiếp nhận c&aacute;c trường hợp bệnh nặng rất hạn chế, cần sự chi viện, hỗ trợ chuy&ecirc;n m&ocirc;n từ Bộ Y tế</h2>\r\n\r\n<h2>&quot;Ch&uacute;ng t&ocirc;i li&ecirc;n tục ghi nhận c&aacute;c ca dương t&iacute;nh trong cộng đồng với số lượng 100-150 ca mỗi ng&agrave;y, nhiều trường hợp kh&ocirc;ng x&aacute;c định được c&aacute;c yếu tố dịch tễ, chuỗi l&acirc;y nhiễm, kh&oacute; khăn trong c&ocirc;ng t&aacute;c thống k&ecirc; dịch tễ, định hướng x&eacute;t nghiệm&quot;, BS Nguyễn Văn Cường, Phụ tr&aacute;ch Sở Y tế T&acirc;y Ninh n&oacute;i.</h2>\r\n\r\n<h2><strong><em>Phải mạnh mẽ, thực chất hơn</em></strong></h2>\r\n\r\n<h2>Ph&aacute;t biểu tại cuộc họp, GS.TS Trần Văn Thuấn - Thứ trưởng Bộ Y tế, Tổ trưởng tổ c&ocirc;ng t&aacute;c đặc biệt của Bộ Y tế hỗ trợ c&aacute;c tỉnh miền Đồng Nam Bộ chống dịch COVID-19 - đ&aacute;nh gi&aacute; nguy cơ dịch của T&acirc;y Ninh l&agrave; rất cao, nếu địa phương kh&ocirc;ng khẩn trương th&igrave; hậu quả sẽ rất kh&oacute; lường.</h2>\r\n\r\n<h2>&quot;Đề nghị cả hệ thống ch&iacute;nh trị của tỉnh phải v&agrave;o cuộc mạnh mẽ, thực chất, hiệu quả hơn của Tỉnh cần c&oacute; kịch bản ứng ph&oacute; với kịch bản c&oacute; 10.000 bệnh nh&acirc;n. Đồng thời đẩy nhanh tốc độc truy vết, x&eacute;t nghiệm&quot; - GS Thuấn n&oacute;i.</h2>\r\n', 'Nguyễn Thanh Bằng', '2021-08-02', 3),
(31, 'yte6.jpg', 'Ngày 02/8/2021 Bộ Y tế vừa yêu cầu không phun khử khuẩn ngoài trời, vào người do kém hiệu quả và ảnh hưởng xấu đến sức khỏe.', 'Bộ Y tế yêu cầu không phun hóa chất khử khuẩn ngoài trời và vào người', '<p><strong>Ng&agrave;y 02/8/2021 Bộ Y tế vừa y&ecirc;u cầu kh&ocirc;ng phun khử khuẩn ngo&agrave;i trời, v&agrave;o người do k&eacute;m hiệu quả v&agrave; ảnh hưởng xấu đến sức khỏe.</strong></p>\r\n\r\n<h2>Bộ Y tế vừa có văn bản gửi các địa phương về việc vệ sinh khử khuẩn phòng, chống dịch COVID-19.</h2>\r\n\r\n<h2>Theo đó, trước hình dịch COVID-19 đang diễn biến phức tạp, một số địa phương áp dụng biện pháp phun hóa chất diệt khuẩn tại các nơi công cộng để phòng chống dịch. Tại một số địa điểm, cơ quan, tổ chức lắp đặt buồng khử khuẩn để phun hóa chất diệt khuẩn vào người đứng trong buồng. Một số đơn vị sử dụng máy phun hóa chất diệt khuẩn phun vào người cách ly, nhập cảnh&hellip;</h2>\r\n\r\n<p><img alt=\"\" src=\"https://moh.gov.vn/documents/174521/987201/2.8+Phun+kh%E1%BB%AD+khu%E1%BA%A9n.jpg/eca211ac-86a1-422d-8b85-0ba94eafb135?t=1627889239914\" style=\"height:362px; width:645px\" /></p>\r\n\r\n<h2>Theo Tổ chức Y tế thế giới (WHO), những nơi như đường phố, vỉa hè không phải là nơi chứa virus SARS-CoV-2. Việc phun hóa chất diệt khuẩn ở ngoài trời không được WHO, Trung tâm Phòng ngừa và Kiểm soát bệnh tật Hoa kỳ (CDC Hoa kỳ) khuyến cáo do kém hiệu quả, có thể ảnh hưởng xấu đến sức khỏe người phun và người xung quanh khu vực phun.</h2>\r\n\r\n<h2>Việc sử dụng số lượng hóa chất diệt khuẩn để phun diệt khuẩn ngoài trời còn có thể gây hại đến môi trường và làm lãng phí nguồn hóa chất dự trữ cho công tác phòng, chống dịch.</h2>\r\n\r\n<h2>Mặt khác, WHO và CDC Hoa kỳ cũng khuyến cáo không phun hóa chất diệt khuẩn vào người trong bắt cứ tình huống nào do phương pháp này không làm giảm nguy cơ lây nhiễm mà có thể gây hại cho sức khỏe con người.</h2>\r\n\r\n<h2>Vì thế, để đảm bảo công tác vệ sinh khử khuẩn phòng, chống dịch COVID-19 hiệu quả, an toàn và tiết kiệm, Bộ Y tế đề nghị UBND các tỉnh thành chỉ đạo không thực hiện việc phun hóa chất, chế phẩm diệt khuẩn để diệt virus SARS-CoV-2 tại những khu vực ngoài trời. Đồng thời, không áp dụng biện pháp phun hóa chất, chế phẩm diệt khuẩn vào người trong bất cứ tình huống nào, gồm cả biện pháp dùng máy phun hóa chất trực tiếp vào người hoặc sử dụng buồng khử khuẩn phun hóa chất.</h2>\r\n\r\n<h2>Theo đó, việc phun khử khuẩn chỉ áp dụng trong phạm vi hẹp khi xử lý môi trường khu vực có bệnh nhân COVID-19 tại cộng đồng theo hướng dẫn tại công văn số 1560/BYT-MT ngày 25/3/2020.</h2>\r\n\r\n<h2>Bộ Y tế cũng lưu ý khi sử dụng các hóa chất, chế phẩm diệt khuẩn dùng trong gia dụng và y tế đã được Bộ Y tế (Cục Quản lý môi trường y tế) cấp số đăng ký lưu hành còn hiệu lực, phải sử dụng theo đúng liều lượng và phương pháp sử dụng ghi trên nhãn sản phẩm. Danh sách chế phẩm được cấp số đăng ký lưu hành được công khai trên trang thông tin điện tử http://vihema.gov.vn./.</h2>\r\n\r\n<p>&nbsp;</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-02', 3),
(32, 'yte7.jpg', 'Theo Bộ trưởng Bộ Y tế Nguyễn Thanh Long, có thể các bệnh viện, cơ sở y tế tư nhân cũng đang khó khăn, tuy nhiên sự chung tay, góp sức từ', 'Bộ trưởng Nguyễn Thanh Long kêu gọi hệ thống y tế tư nhân  ', '<p>&nbsp;</p>\r\n\r\n<p><strong>Theo Bộ trưởng Bộ Y tế Nguyễn Thanh Long, c&oacute; thể c&aacute;c bệnh viện, cơ sở y tế tư nh&acirc;n cũng đang kh&oacute; khăn, tuy nhi&ecirc;n sự chung tay, g&oacute;p sức từ ch&iacute;nh c&aacute;c đơn vị n&agrave;y sẽ gi&uacute;p nhiều người bệnh COVID-19 được điều trị hồi sức.</strong></p>\r\n\r\n<h2><strong><em>Nhiều đơn vị y tế tư nh&acirc;n chủ động tham gia chống dịch COVID-19</em></strong></h2>\r\n\r\n<h2>Sau khi đi kiểm tra việc thiết lập 4 trung t&acirc;m hồi sức t&iacute;ch cực của Bộ Y tế v&agrave; thăm, động vi&ecirc;n c&aacute;c y b&aacute;c sĩ tại Bệnh viện Hồi sức COVID-19 TP Hồ Ch&iacute; Minh do Bệnh viện Chợ Rẫy đang đảm nhiệm, tối 1/8, GS.TS Nguyễn Thanh Long - Bộ trưởng Bộ Y tế đ&atilde; gặp gỡ, trao đổi với l&atilde;nh đạo c&aacute;c bệnh viện, cơ sở y tế tư nh&acirc;n tr&ecirc;n địa b&agrave;n TP Hồ Ch&iacute; Minh.</h2>\r\n\r\n<h2>C&ugrave;ng dự gặp gỡ, trao đổi c&oacute; l&atilde;nh đạo UBND TP Hồ Ch&iacute; Minh, Sở Y tế v&agrave; một số sở, ban, ng&agrave;nh li&ecirc;n quan.</h2>\r\n\r\n<h2>Ph&aacute;t biểu tại cuộc gặp, Bộ trưởng Nguyễn Thanh Long nhấn mạnh, đ&acirc;y l&agrave; thời điểm rất kh&oacute; khăn, đặc biệt đối với người d&acirc;n của Th&agrave;nh phố trong cuộc chiến với dịch bệnh.</h2>\r\n\r\n<p><img alt=\"\" src=\"https://moh.gov.vn/documents/20182/988701/02.8.2021.+BT+tai+TPHCM+0.jpg/87576d8a-4b34-4823-89fe-6d4edf0f355a?t=1627867791965\" style=\"height:427px; width:640px\" /></p>\r\n\r\n<h2>Thời gian qua, Bộ Y tế đ&atilde; huy động c&aacute;c bệnh viện c&ocirc;ng lập tr&ecirc;n địa b&agrave;n tham gia ph&ograve;ng, chống dịch COVID-19. Để tăng cường nguồn lực cho c&ocirc;ng t&aacute;c ph&ograve;ng, chống dịch, Bộ Y tế đ&atilde; c&oacute; văn bản gửi Chủ tịch UBND c&aacute;c tỉnh, th&agrave;nh phố đề nghị huy động sự tham gia của c&aacute;c cơ sở kh&aacute;m chữa bệnh tư nh&acirc;n.</h2>\r\n\r\n<h2>Sự chung tay, g&oacute;p sức của y tế tư nh&acirc;n sẽ gi&uacute;p cho nhiều người bệnh COVID-19 được điều trị hồi sức.</h2>\r\n\r\n<h2>Bộ trưởng Nguyễn Thanh Long chia sẻ: &quot;Ch&uacute;ng t&ocirc;i hiểu rằng trong cuộc chiến ph&ograve;ng, chống dịch n&agrave;y, ai cũng c&oacute; tr&aacute;ch nhiệm, nhưng đối với ng&agrave;nh y tế th&igrave; tr&aacute;ch nhiệm lớn lao hơn. Người thầy thuốc c&oacute; tr&aacute;ch nhiệm v&agrave; bổn phận chăm s&oacute;c sức khoẻ nh&acirc;n d&acirc;n. V&igrave; vậy t&ocirc;i rất thiết tha đề nghị c&aacute;c vị l&atilde;nh đạo c&aacute;c bệnh viện, cơ sở y tế tư nh&acirc;n tiếp tục chung tay, chung sức, chung l&ograve;ng g&aacute;nh v&aacute;c c&ugrave;ng Th&agrave;nh phố trong c&ocirc;ng t&aacute;c ph&ograve;ng, chống dịch, đặc biệt trong điều trị, chăm s&oacute;c sức khoẻ người mắc COVID-19 để Th&agrave;nh phố vượt qua giai đoạn rất kh&oacute; khăn n&agrave;y&quot;.</h2>\r\n\r\n<p>&nbsp;</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-02', 3),
(33, '1626936637197-13-1626923051-thum-1.jpg', 'Theo đề án của Bộ VH-TT-DL, Liên hoan phim Việt Nam sẽ trở thành thương hiệu quốc gia, có thể sẽ thu hút như Liên hoan phim Cannes', 'Phát triển Liên hoan phim Việt Nam thành một Cannes mới?', '<p>Theo đề &aacute;n của Bộ VH-TT-DL, Li&ecirc;n hoan phim Việt Nam&nbsp;sẽ trở th&agrave;nh thương hiệu quốc gia, c&oacute; thể sẽ thu h&uacute;t như Li&ecirc;n hoan phim Cannes nổi tiếng ở Ph&aacute;p.</p>\r\n\r\n<h2>&ldquo;Người người biết đến&rdquo; như Cannes</h2>\r\n\r\n<p>Đề &aacute;n X&acirc;y dựng v&agrave; quảng b&aacute; thương hiệu quốc gia Li&ecirc;n hoan phim (LHP) Việt Nam&nbsp;bắt đầu bước v&agrave;o giai đoạn triển khai. Đề &aacute;n c&oacute; 3 mục ti&ecirc;u chung. Thứ nhất, LHP Việt Nam&nbsp;trở th&agrave;nh thương hiệu quốc gia trong lĩnh vực&nbsp;<a href=\"https://thanhnien.vn/van-hoa/dien-anh/\" rel=\"\">điện ảnh</a>, khẳng định vị thế, uy t&iacute;n ở trong nước v&agrave; quốc tế. Thứ hai, g&oacute;p phần quảng b&aacute; nghệ thuật điện ảnh, h&igrave;nh ảnh quốc gia, sức hấp dẫn văn h&oacute;a, cảnh đẹp thi&ecirc;n nhi&ecirc;n, con&nbsp;<a href=\"https://thanhnien.vn/the-gioi/nguoi-viet-nam-chau/\" rel=\"\">người Việt Nam</a>&nbsp;th&acirc;n thiện, hiếu kh&aacute;ch. Thứ ba, g&oacute;p phần x&acirc;y dựng v&agrave; ph&aacute;t triển c&aacute;c<a href=\"https://thanhnien.vn/van-hoa/lien-hoan-phim-cannes-lan-thu-74-chinh-thuc-cong-bo-phim-tranh-canh-co-vang-1393450.html\" rel=\"\" target=\"_blank\">&nbsp;ng&agrave;nh c&ocirc;ng nghiệp văn h&oacute;a Việt Nam</a>&nbsp;đến năm 2020, tầm nh&igrave;n đến năm 2030 theo Quyết định 1755 của&nbsp;<a href=\"https://thanhnien.vn/thu-tuong-chinh-phu/\" rel=\"\">Thủ tướng Ch&iacute;nh phủ</a>.</p>\r\n\r\n<p>Để thực hiện điều đ&oacute;, đề &aacute;n cũng đề ra việc&nbsp;<a href=\"https://thanhnien.vn/kinh-doanh/chinh-sach-phat-trien/\" rel=\"\">ph&aacute;t triển nguồn nh&acirc;n lực</a>, đ&agrave;o tạo v&agrave; n&acirc;ng cao năng lực chuy&ecirc;n m&ocirc;n cho c&aacute;n bộ l&agrave;m c&ocirc;ng t&aacute;c tổ chức, truyền th&ocirc;ng quảng b&aacute; sự kiện LHP Việt Nam. Trong đ&oacute; sẽ c&oacute; bộ phận chuy&ecirc;n tr&aacute;ch tổ chức, gi&aacute;m đốc cũng như đại sứ của li&ecirc;n hoan.</p>\r\n\r\n<p>Về việc n&acirc;ng cao chất lượng phim dự li&ecirc;n hoan, đề &aacute;n n&ecirc;u 4 điểm. Thứ nhất, n&acirc;ng cao hiệu quả của c&ocirc;ng t&aacute;c đặt h&agrave;ng sản xuất phim c&oacute; sử dụng ng&acirc;n s&aacute;ch nh&agrave; nước nhằm tăng cường chất lượng đầu v&agrave;o li&ecirc;n hoan. Thứ hai, x&acirc;y dựng kế hoạch lựa chọn đề t&agrave;i, kịch bản đặt h&agrave;ng sản xuất phim sử dụng ng&acirc;n s&aacute;ch nh&agrave; nước, đặt mục ti&ecirc;u tham dự LHP Việt Nam&nbsp;đồng thời c&ugrave;ng c&aacute;c nhiệm vụ kh&aacute;c. Thứ ba, c&oacute; kế hoạch đầu tư một phần ng&acirc;n s&aacute;ch nh&agrave; nước cho tư nh&acirc;n sản xuất phim nhằm n&acirc;ng cao chất lượng, hướng tới mục đ&iacute;ch gửi phim tham dự LHP Việt Nam. Cuối c&ugrave;ng, n&acirc;ng cao chất lượng tuyển chọn phim tham dự LHP Việt Nam.</p>\r\n\r\n<p>Về m&ocirc; h&igrave;nh c&aacute;c LHP, giải thưởng đ&atilde; từng th&agrave;nh c&ocirc;ng tr&ecirc;n&nbsp;<a href=\"https://thanhnien.vn/the-gioi/\" rel=\"\">thế giới</a>, đề &aacute;n chia l&agrave;m 3 loại ti&ecirc;u biểu. LHP t&agrave;i liệu quốc tế Florence (&Yacute;) thi&ecirc;n về chức năng giao lưu văn h&oacute;a, trao đổi ng&agrave;nh điện ảnh, kh&ocirc;ng ưu ti&ecirc;n đầu tư ph&aacute;t triển thị trường. M&ocirc; h&igrave;nh LHP Cannes (Ph&aacute;p) đề cao t&igrave;m t&ograve;i, s&aacute;ng tạo nghệ thuật, đồng thời t&iacute;ch cực x&uacute;c tiến giao dịch thương mại. M&ocirc; h&igrave;nh Oscar (Mỹ) thi&ecirc;n về thương mại, doanh thu ph&ograve;ng v&eacute;, truyền th&ocirc;ng v&agrave; t&iacute;nh thương mại được ưu ti&ecirc;n, khai th&aacute;c triệt để.</p>\r\n\r\n<p>&Ocirc;ng Vi Kiến Th&agrave;nh,&nbsp;<a href=\"https://thanhnien.vn/van-hoa/lien-hoan-phim-cannes-khoi-dong-cong-bo-cac-hang-muc-chinh-vao-thang-6-1386763.html\" rel=\"\" target=\"_blank\">Cục trưởng Cục Điện ảnh</a>, cho rằng ch&uacute;ng ta sẽ đi theo m&ocirc; h&igrave;nh Cannes. &ldquo;Của m&igrave;nh th&igrave; đương nhi&ecirc;n phải hướng tới t&ocirc;n vinh gi&aacute; trị c&aacute;c t&aacute;c phẩm điện ảnh. M&igrave;nh phải theo m&ocirc; h&igrave;nh Ph&aacute;p chứ theo c&aacute;ch t&ocirc;n vinh&nbsp;<a href=\"https://thanhnien.vn/giai-tri/\" rel=\"\">giải tr&iacute;</a>&nbsp;sao được&rdquo;, &ocirc;ng Th&agrave;nh cho biết.</p>\r\n\r\n<h2>Mục ti&ecirc;u sai từ đầu ?</h2>\r\n\r\n<p>Chuy&ecirc;n gia marketing Phạm Vũ T&ugrave;ng, người nhiều năm gắn b&oacute; với truyền th&ocirc;ng&nbsp;<a href=\"https://thanhnien.vn/van-hoa/\" rel=\"\">văn h&oacute;a</a>, cho rằng đề &aacute;n n&agrave;y bị&hellip; ngược. Theo đ&oacute;, phần đề &aacute;n li&ecirc;n quan đến c&aacute;c việc truyền th&ocirc;ng như mang quảng b&aacute; phim ở c&aacute;c hội chợ du lịch, lập ban truyền th&ocirc;ng&hellip; lại nhiều hơn phần việc cần l&agrave;m cho gi&aacute; trị li&ecirc;n hoan. &ldquo;LHP th&igrave; quan trọng nhất l&agrave; phim. Phim kh&ocirc;ng hay th&igrave; kh&ocirc;ng thu h&uacute;t v&agrave; truyền th&ocirc;ng cũng chẳng l&agrave;m g&igrave; được&rdquo;, &ocirc;ng T&ugrave;ng n&oacute;i. &Ocirc;ng cũng cho rằng việc x&acirc;y dựng gi&aacute; trị LHP chủ yếu tr&ecirc;n gi&aacute; trị phim nh&agrave; nước hiện tại kh&ocirc;ng c&ograve;n ph&ugrave; hợp khi lượng phim l&egrave;o t&egrave;o v&agrave; cũng kh&ocirc;ng thu h&uacute;t kh&aacute;n giả.</p>\r\n\r\n<p><a href=\"https://thanhnien.vn/van-hoa/lien-hoan-phim-cannes-2020-thay-doi-cach-thuc-to-chuc-1222317.html\" rel=\"\" target=\"_blank\">Đạo diễn Nguyễn Ho&agrave;ng Điệp</a>&nbsp;kh&aacute; ngạc nhi&ecirc;n về việc chọn LHP Việt Nam&nbsp;để ph&aacute;t triển th&agrave;nh thương hiệu quốc gia, trong khi c&oacute; LHP quốc tế H&agrave; Nội đang l&agrave;m tốt. &ldquo;C&ograve;n c&aacute;i cốt l&otilde;i của đề &aacute;n đ&atilde; sai từ đầu khi n&oacute;i l&agrave; LHP quốc tế kh&ocirc;ng ph&ugrave; hợp m&agrave; l&agrave; LHP quốc gia mới ph&ugrave; hợp để th&agrave;nh thương hiệu quốc gia. Điện ảnh kh&ocirc;ng cần sợ yếu tố quốc tế. Đọc đề &aacute;n thấy dường như c&oacute; &aacute;p lực phải chứng minh LHP quốc gia tốt hơn LHP quốc tế H&agrave; Nội&rdquo;, đạo diễn Ho&agrave;ng Điệp nhận định.</p>\r\n\r\n<p>Th&ecirc;m v&agrave;o đ&oacute;, nữ đạo diễn cho rằng LHP phải l&agrave; một nơi ph&aacute;t hiện v&agrave; ph&aacute;t triển điện ảnh. Điện ảnh cũng kh&ocirc;ng phải c&acirc;u chuyện của du lịch. &ldquo;Việc coi điện ảnh như c&ocirc;ng cụ để quảng b&aacute;&nbsp;<a href=\"https://thanhnien.vn/du-lich/\" rel=\"\">du lịch</a>, điện ảnh hay nhận tiền từ c&aacute;c đơn vị du lịch l&agrave; ho&agrave;n to&agrave;n sai tr&aacute;ch nhiệm của LHP&rdquo;, b&agrave; cho biết v&agrave; lưu &yacute; về việc: &ldquo;Nếu lựa chọn sai th&igrave; sẽ đầu tư sai, kế hoạch h&agrave;nh động cũng sai&rdquo;.</p>\r\n\r\n<p>Đạo diễn Phan Đăng Di cho rằng muốn th&uacute;c đẩy điện ảnh c&oacute; sức cạnh tranh v&agrave; khả năng hội nhập, th&agrave;nh nh&acirc;n tố mạnh trong x&acirc;y dựng c&ocirc;ng nghiệp văn h&oacute;a th&igrave; trước hết kh&ocirc;ng thể lơ mơ về c&aacute;c ti&ecirc;u chuẩn chung để được c&ocirc;ng nhận. Theo &ocirc;ng: &ldquo;Cơ bản ch&uacute;ng ta đang &aacute;p dụng hệ gi&aacute; trị một m&igrave;nh một chợ, rất xa c&aacute;c ti&ecirc;u chuẩn chung của quốc tế kh&ocirc;ng chỉ trong kiểm duyệt, hợp t&aacute;c l&agrave;m phim với nước ngo&agrave;i, th&uacute;c đẩy t&iacute;nh cạnh tranh của điện ảnh quốc gia, đối xử với t&agrave;i năng v&agrave; cả trong hội nhập quốc tế&rdquo;.</p>\r\n\r\n<p>&Ocirc;ng Di cũng cho rằng việc chọn LHP Việt Nam&nbsp;để x&acirc;y dựng th&agrave;nh thương hiệu quốc gia thay v&igrave; một LHP quốc tế cũng cho thấy tư duy kiểu &ldquo;kh&ocirc;ng giống ai&rdquo;. &ldquo;C&aacute;ch g&igrave; khiến một LHP quốc gia vốn chỉ để tuy&ecirc;n dương phim của một nền điện ảnh nhỏ như Việt Nam&nbsp;lại c&oacute; thể trở th&agrave;nh một thương hiệu để quốc tế biết đến? Đ&oacute; phải l&agrave; một LHP quốc tế do một quốc gia tổ chức, được thử th&aacute;ch qua thời gian, l&agrave; điểm đến được tin tưởng lựa chọn của giới điện ảnh to&agrave;n cầu th&igrave; mới trở th&agrave;nh một thương hiệu quốc gia được&rdquo;, &ocirc;ng Di n&oacute;i.</p>\r\n\r\n<p>&ldquo;Việc cải thiện cho được những vấn đề nội tại đang rất trầm kha của ng&agrave;nh mới l&agrave; việc cần l&agrave;m ngay, thay v&igrave; nghĩ ra những đề &aacute;n đao to b&uacute;a lớn nhưng thiếu thực tiễn v&agrave; kh&ocirc;ng c&oacute; cơ sở khoa học vững như đề &aacute;n n&agrave;y&rdquo;, đạo diễn Phan Đăng Di b&agrave;y tỏ. Một trong những vấn đề đ&oacute;, theo &ocirc;ng, l&agrave; việc đầu tư b&agrave;i bản cho những người l&agrave;m phim trẻ. Nhiều người trong số họ đ&atilde; dự c&aacute;c LHP lớn tr&ecirc;n thế giới một c&aacute;ch ch&iacute;nh thức m&agrave; kh&ocirc;ng hề c&oacute; hỗ trợ của nh&agrave; nước.</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-02', 4),
(34, 'vanhoa.jpg', 'Trên trang mạng cá nhân, nữ diễn viên Phương Oanh vừa chia sẻ lý do không tham gia giải thưởng VTV Awards.', 'Phương Oanh lên tiếng về lý do yêu cầu rút tên, không tranh giải VTV Awards', '<p><a href=\"https://thanhnien.vn/giai-tri/phuong-oanh-vang-mat-trong-de-cu-dien-vien-nu-an-tuong-1422384.html\" rel=\"\" target=\"_blank\">Phương Oanh</a>&nbsp;viết: &ldquo;Oanh xin ch&iacute;nh thức th&ocirc;ng b&aacute;o như sau: Oanh đ&atilde; y&ecirc;u cầu r&uacute;t t&ecirc;n kh&ocirc;ng tham gia tranh giải VTV Awards năm nay ngay từ khi giải chuẩn bị khởi động. V&agrave; đến giờ vẫn sẽ giữ vững quyết định n&agrave;y&rdquo;.</p>\r\n\r\n<p>Nữ diễn vi&ecirc;n b&agrave;y tỏ: &ldquo;Mong ban tổ chức v&agrave; kh&aacute;n giả t&ocirc;n trọng quyết định của Oanh. C&ograve;n l&yacute; do th&igrave; như Oanh đ&atilde; từng chia sẻ với fans (người h&acirc;m mộ - ph&oacute;ng vi&ecirc;n) của m&igrave;nh trước đ&oacute; rồi. Oanh chỉ muốn an b&igrave;nh nhất c&oacute; thể để tập trung trọn vẹn với vai diễn của Oanh, với những kh&aacute;n giả tin tưởng, y&ecirc;u thương Oanh ch&acirc;n th&agrave;nh. Ngắn gọn vậy th&ocirc;i ạ. Xin cảm ơn! Cảm ơn tất cả&rdquo;.</p>\r\n\r\n<p>Khi<a href=\"https://thanhnien.vn/van-hoa/vtv-awards-nsnd-thu-ha-hong-diem-manh-truong-hong-dang-den-vau-duoc-diem-ten-1422175.html\" rel=\"\" target=\"_blank\">&nbsp;danh s&aacute;ch</a>&nbsp;đề cử hạng mục Diễn vi&ecirc;n nữ ấn tượng của giải thưởng VTV Awards 2021 được c&ocirc;ng bố, nhiều kh&aacute;n giả ngạc nhi&ecirc;n khi kh&ocirc;ng c&oacute; t&ecirc;n Phương Oanh, người đang đảm nhiệm vai ch&iacute;nh trong bộ phim&nbsp;<em><a href=\"https://thanhnien.vn/van-hoa/manh-truong-noi-ve-cai-ket-cua-cap-doi-nam-long-trong-phim-huong-vi-tinh-than-1423401.html\" rel=\"\" target=\"_blank\">Hương vị t&igrave;nh th&acirc;n</a></em>.</p>\r\n\r\n<p>Danh s&aacute;ch n&agrave;y gồm c&oacute;:&nbsp;<a href=\"https://thanhnien.vn/van-hoa/nsnd-thu-ha-con-trai-toi-giat-minh-khi-thay-bach-cuc-1359655.html\" rel=\"\" target=\"_blank\">NSND Thu H&agrave;</a>, Hồng Diễm, Lương Thu Trang (phim&nbsp;<em>Hướng dương ngược nắng</em>), Cao Nguyệt Hằng (<em>H&atilde;y n&oacute;i lời y&ecirc;u</em>), Nguyễn Ngọc Th&uacute;y Diễm (<em>C&aacute;t đỏ</em>), Nguyễn Thanh Hương (<em>M&ugrave;a hoa t&igrave;m lại)</em>, Nguyễn Ngọc Lan, NSND Kim Xu&acirc;n (Tr&oacute;i buộc y&ecirc;u thương), Nguyễn Thu Quỳnh (<em>Hương vị t&igrave;nh th&acirc;n</em>), NSƯT Hạnh Th&uacute;y (<em>Thương con c&aacute; r&ocirc; đồng</em>).</p>\r\n\r\n<p>C&ograve;n nhớ, mặc d&ugrave; bộ phim<a href=\"https://thanhnien.vn/van-hoa/nsnd-thu-ha-con-trai-toi-giat-minh-khi-thay-bach-cuc-1359655.html\" rel=\"\" target=\"_blank\">&nbsp;<em>Quỳnh b&uacute;p b&ecirc;</em>&nbsp;</a>rất h&uacute;t kh&aacute;n giả&nbsp;<a href=\"https://thanhnien.vn/van-hoa/truyen-hinh/\" rel=\"\">xem truyền h&igrave;nh</a>, nhưng Phương Oanh - người thủ vai ch&iacute;nh của bộ phim từng &ldquo;trượt&rdquo; top 5 đề cử hạng mục Diễn vi&ecirc;n nữ ấn tượng của VTV Awards 2019. Danh s&aacute;ch top 5 gồm: Bảo Thanh, Thanh Hương,&nbsp;<a href=\"https://thanhnien.vn/van-hoa/thu-quynh-toi-khong-suy-nghi-gi-viec-minh-chi-dong-vai-thu-1384956.html\" rel=\"\" target=\"_blank\">Thu Quỳnh</a>, Bảo H&acirc;n v&agrave; Ninh Dương Lan Ngọc. Cuối c&ugrave;ng, người gi&agrave;nh giải thưởng l&agrave; nữ diễn vi&ecirc;n Bảo Thanh với vai Thư trong phim&nbsp;<em>Về nh&agrave; đi con</em>.</p>\r\n\r\n<p>Cho đến giải thưởng&nbsp;<a href=\"https://thanhnien.vn/van-hoa/khoi-dong-giai-thuong-vtv-awards-1394044.html\" rel=\"\" target=\"_blank\">VTV Awards 2020</a>, top 5 đề cử giải thưởng Diễn vi&ecirc;n nữ ấn tượng mới c&oacute; t&ecirc;n Phương Oanh (c&ugrave;ng với Hồng Diễm, L&atilde; Thanh Huyền, Diễm My v&agrave;&nbsp; Quỳnh Cool).</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-02', 4),
(35, '226077841_412217970162680_1486580440016970706_n_sxlb.jpg', 'rong 16 học sinh Việt Nam tham dự kỳ thi Toán học trẻ quốc tế (IIMC) 2021, ', '16 học sinh Việt Nam giành giải Toán học trẻ quốc tế', '<p>Trong 16 học sinh Việt Nam tham dự kỳ thi To&aacute;n học trẻ quốc tế (IIMC) 2021, c&oacute; 14 em gi&agrave;nh huy chương, 2 em gi&agrave;nh giải khuyến kh&iacute;ch.</p>\r\n\r\n<p>Theo kết quả cuộc thi được Ban tổ chức c&ocirc;ng bố ng&agrave;y 2/8, ở khối lớp 6, Nguyễn Anh H&ugrave;ng gi&agrave;nh huy chương v&agrave;ng. Đ&acirc;y cũng l&agrave; giải v&agrave;ng duy nhất của đo&agrave;n Việt Nam. Năm huy chương bạc thuộc về c&aacute;c em L&ecirc; T&ugrave;ng L&acirc;m, Nguyễn Nhật Minh, Nguyễn Ngọc Huy Anh, H&agrave; Mạnh H&ugrave;ng, Đ&agrave;o Viết Tuấn Kiệt. Hai huy chương đồng thuộc về Nguyễn Mạnh L&acirc;m, Nguyễn Huy Ho&agrave;ng Long.</p>\r\n\r\n<p>Với khối 8, bốn em gi&agrave;nh huy chương bạc l&agrave; Nguyễn Nam Sơn, Nguyễn Kh&aacute;nh Dương, Nguyễn Cảnh Dương, Trần Ngọc Nhật Minh. Hai em giải đồng l&agrave;&nbsp;<a href=\"https://vnexpress.net/nu-sinh-gianh-huy-chuong-vang-khoa-hoc-quoc-te-4020601.html\" rel=\"dofollow\">Nguyễn Bảo Ng&acirc;n Giang</a>, Vũ Nguyễn Nam Anh. C&ograve;n lại Đ&agrave;m Ch&iacute; Ki&ecirc;n, Trần Quốc Việt gi&agrave;nh giải khuyến kh&iacute;ch.</p>\r\n\r\n<p>X&eacute;t phần thi đồng đội, học sinh mỗi khối được chia về 6 bảng. C&aacute;c em gi&agrave;nh hai giải v&ocirc; địch, một giải nh&igrave; v&agrave; một giải ba.</p>\r\n\r\n<p>16 học sinh n&agrave;y đến từ c&aacute;c trường THCS H&agrave; Nội - Amsterdam, Cầu Giấy, Newton, Ng&ocirc;i sao H&agrave; Nội, Nguyễn Trường Tộ của H&agrave; Nội.</p>\r\n\r\n<p>Kỳ thi To&aacute;n học trẻ quốc tế 2021 (International Mathematics Competition - IMC) được tổ chức thường ni&ecirc;n cho học sinh THCS to&agrave;n thế giới. T&ecirc;n cuộc thi sẽ được gh&eacute;p từ chữ c&aacute;i đầu của quốc gia tổ chức với cụm IMC. Năm nay, kỳ thi do Indonesia đăng cai tổ chức (IIMC), diễn ra từ 29/7 đến 1/8 theo h&igrave;nh thức trực tuyến. Gần 600 học sinh đến từ 31 quốc gia v&agrave; v&ugrave;ng l&atilde;nh thổ tranh t&agrave;i ở hai cấp độ lớp 6 (Keystage II) v&agrave; lớp 8 (Keystage III).</p>\r\n\r\n<p>Việt Nam bắt đầu tham dự cuộc thi n&agrave;y từ năm 2011. Năm 2015, Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo c&ocirc;ng nhận kỳ thi n&agrave;y v&agrave; cho ph&eacute;p trường THPT chuy&ecirc;n H&agrave; Nội - Amsterdam lập đội tuyển đại diện cho Việt Nam tham dự.</p>\r\n\r\n<p>Năm 2021, được Ban tổ chức IMC quốc tế ủy quyền, Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo H&agrave; Nội c&ugrave;ng Quỹ Ph&aacute;t triển Gi&aacute;o dục IEG tổ chức tuyển chọn đội tuyển từ c&aacute;c trường THCS ở H&agrave; Nội.</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-02', 5),
(36, '1627205514215-1627203778607-ha-noi-16272002779271159585693.jpeg', 'Đại học Bách khoa Hà Nội quyết định dừng tổ chức kỳ thi đánh giá', 'Đại học Bách khoa Hà Nội hủy kỳ thi đánh giá tư duy năm 2021', '<p>Đại học B&aacute;ch khoa H&agrave; Nội quyết định dừng tổ chức kỳ thi đ&aacute;nh gi&aacute; tư duy năm nay do ảnh hưởng của Covid-19.</p>\r\n\r\n<p>Theo kế hoạch, trường B&aacute;ch khoa H&agrave; Nội x&eacute;t tuyển bằng kết quả kỳ thi đ&aacute;nh gi&aacute; tư duy, chiếm 30-40% trong tổng hơn 7.400 chỉ ti&ecirc;u. Sau khi dừng tổ chức thi ri&ecirc;ng, to&agrave;n bộ chỉ ti&ecirc;u n&agrave;y sẽ d&agrave;nh cho phương thức x&eacute;t tuyển bằng kết quả thi tốt nghiệp THPT. Danh s&aacute;ch tổ hợp, chỉ ti&ecirc;u, phương &aacute;n đối với th&iacute; sinh diện đặc c&aacute;ch tốt nghiệp sẽ được nh&agrave; trường c&ocirc;ng bố sớm.</p>\r\n\r\n<p>Ngo&agrave;i ra, trường B&aacute;ch khoa H&agrave; Nội cũng sẽ trả lại lệ ph&iacute; dự kỳ thi đ&aacute;nh gi&aacute; tư duy cho học sinh đ&atilde; đăng k&yacute; tham gia. C&aacute;ch thức ho&agrave;n trả sẽ được c&ocirc;ng bố v&agrave; thực hiện trong thời gian sớm nhất. Trường sẽ tiếp tục chuẩn bị v&agrave; tổ chức kỳ thi đ&aacute;nh gi&aacute; tư duy năm sau.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-vnexpress.vnecdn.net/2021/08/02/thi-tn-1627889578-9201-1627889806.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=y7FI7-53hlp0n-7DwFmb7w\" style=\"height:453px; width:680px\" /></p>\r\n\r\n<p>Kỳ thi đ&aacute;nh gi&aacute; tư duy được Đại học B&aacute;ch khoa H&agrave; Nội tổ chức lần đầu năm 2020 v&agrave; trở th&agrave;nh một trong c&aacute;c phương thức tuyển sinh của trường b&ecirc;n cạnh x&eacute;t điểm thi tốt nghiệp THPT, x&eacute;t hồ sơ năng lực hay x&eacute;t tuyển thẳng.</p>\r\n\r\n<p>Năm nay, trường dự định tổ chức thi đ&aacute;nh gi&aacute; năng lực sau kỳ thi tốt nghiệp THPT, với 3 cụm thi tại Đại học B&aacute;ch khoa H&agrave; Nội, Đại học Vinh (Nghệ An) v&agrave; Đại học H&agrave;ng Hải (Hải Ph&ograve;ng).</p>\r\n\r\n<p>Để chuẩn bị tổ chức kỳ thi đ&aacute;nh gi&aacute; tư duy, nh&agrave; trường đ&atilde; huy động gần 600 c&aacute;n bộ, giảng vi&ecirc;n l&agrave;m c&ocirc;ng t&aacute;c thi, tổ chức ti&ecirc;m vaccine ph&ograve;ng Covid-19 cho người l&agrave;m c&ocirc;ng t&aacute;c thi, chuẩn bị c&aacute;c phương &aacute;n, biện ph&aacute;p đảm bảo an to&agrave;n. Trường cũng đ&atilde; c&ocirc;ng bố đề cương &ocirc;n tập để học sinh tham khảo bởi đề thi đ&aacute;nh gi&aacute; tư duy c&oacute; cấu tr&uacute;c v&agrave; c&aacute;ch ra đề rất kh&aacute;c kỳ thi tốt nghiệp THPT.</p>\r\n\r\n<p>Trước th&ocirc;ng b&aacute;o hủy tổ chức kỳ thi lần n&agrave;y, trường đ&atilde; một lần ho&atilde;n kỳ thi từ th&aacute;ng 7 sang th&aacute;ng 8 do Covid-19.</p>\r\n\r\n<p>Từ ng&agrave;y 27/4 đến nay, cả nước ghi nhận hơn 153.600 ca Covid-19. 19 tỉnh, th&agrave;nh ph&iacute;a Nam v&agrave; H&agrave; Nội phải gi&atilde;n c&aacute;ch x&atilde; hội theo Chỉ thị 16.</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-02', 5),
(37, 'giaoduc5.jpg', 'Từ ngày 1/8, viên chức giáo dục đào tạo và viên chức giáo dục nghề nghiệp ở trường công lập được', 'Hai nhóm giáo viên được hưởng phụ cấp thâm niên', '<p>Từ ng&agrave;y 1/8, vi&ecirc;n chức gi&aacute;o dục đ&agrave;o tạo v&agrave; vi&ecirc;n chức gi&aacute;o dục nghề nghiệp ở trường c&ocirc;ng lập được hưởng phụ cấp th&acirc;m ni&ecirc;n.</p>\r\n\r\n<p>Theo Nghị định 77/2021 được Ch&iacute;nh phủ ban h&agrave;nh ng&agrave;y 1/8, nh&oacute;m gi&aacute;o vi&ecirc;n v&agrave; thời gian được hưởng phụ cấp th&acirc;m ni&ecirc;n được bổ sung v&agrave; quy định chi tiết hơn.</p>\r\n\r\n<p>Những người được hưởng trợ cấp gồm: Vi&ecirc;n chức ng&agrave;nh gi&aacute;o dục đ&agrave;o tạo (mang m&atilde; số c&oacute; c&aacute;c k&yacute; tự đầu V.07) v&agrave; ng&agrave;nh gi&aacute;o dục nghề nghiệp (V.09). Những nh&agrave; gi&aacute;o n&agrave;y phải đang c&ocirc;ng t&aacute;c tại trường c&ocirc;ng lập, được Nh&agrave; nước cấp kinh ph&iacute; hoạt động.</p>\r\n\r\n<p>Ngo&agrave;i nh&oacute;m tr&ecirc;n, gi&aacute;o vi&ecirc;n đang giảng dạy, hướng dẫn thực h&agrave;nh, th&iacute; nghiệm tại t&agrave;u huấn luyện, xưởng trường, trạm, trại, trung t&acirc;m thực h&agrave;nh v&agrave; c&aacute;c ph&ograve;ng th&iacute; nghiệm cũng được hưởng phụ cấp th&acirc;m ni&ecirc;n.</p>\r\n\r\n<p>Những người c&ograve;n lại, kể cả giữ m&atilde; số mang k&yacute; tự V.07 v&agrave; V.09 m&agrave; kh&ocirc;ng đảm nhận c&ocirc;ng việc, vai tr&ograve; được quy định, sẽ kh&ocirc;ng được hưởng phụ cấp.</p>\r\n\r\n<p>Trước đ&oacute;, Nghị định 54/2011 chỉ n&ecirc;u người được hưởng th&acirc;m ni&ecirc;n thuộc nh&oacute;m đ&atilde; được chuyển, xếp lương theo Nghị định 204/2004. Quy định mới n&agrave;y gi&uacute;p x&aacute;c định r&otilde; v&agrave; chi tiết nh&oacute;m gi&aacute;o vi&ecirc;n đủ điều kiện nhận phụ cấp.</p>\r\n\r\n<p><img alt=\"\" src=\"https://i1-vnexpress.vnecdn.net/2021/08/02/ve-tranh-5-1585117177-2642-1627871276.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qk4VSspKOsqU_tspZidjlA\" style=\"height:453px; width:680px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Nguyễn Thanh Bằng', '2021-08-02', 5);

